INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11001, 1, 1, 11001, 1, "ＴＰ上昇＋5", "加速の玉飾り", "不思議な効果を持つイヤリング。\n装備者に活力加速を与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11002, 1, 1, 11002, 1, "ダメージを受けるたびＴＰ100回復（最大１回）", "水蓮の盾", "不思議な効果を持つ円盾。受けた\n攻撃をエネルギーに変換できる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11003, 1, 1, 11003, 1, "バトル開始時ＴＰ100回復", "青輝の環", "不思議な効果を持つ腕輪。戦いの\n始まりとともに、装備者にエネル\nギーを与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11004, 1, 1, 11004, 1, "ＴＰ消費軽減＋2", "冷玉の杖", "不思議な効果を持つ杖。装備者に\n力を温存させる効果をもたらす。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11005, 1, 1, 11005, 1, "重複可能の行動速度＋10％", "風渡りの靴", "不思議な効果を持つ靴。装備した\n者は迅速に動くことができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12001, 2, 1, 12001, 1, "状態異常命中＋20", "紫晶ビザリオ", "不思議な効果を持つ首飾り。\n敵に異常を与えやすくなる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12002, 2, 1, 12002, 1, "敵全体に呪い付与（毎秒【弱体】数×300ダメージ）", "魔杖メジナエ", "不思議な効果を持つ杖。所持する\nだけで敵を呪うことができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12003, 2, 1, 12003, 1, "敵全体に毒付与（毎秒【弱体】数×300ダメージ）", "暗剣ポワゾン", "不思議な効果を持つ短剣。持つだ\nけで敵を毒にすることができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12004, 2, 1, 12004, 1, "敵の物理/魔法攻撃力－10％", "破守帽ディスペル", "不思議な効果を持つ帽子。装備す\nると敵の攻撃を弱体させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12005, 2, 1, 12005, 1, "敵の物理/魔法防御力－10％", "崩刀ヤマツキ", "不思議な効果を持つ刀。\n敵の守りを断ち崩す力がある。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13001, 3, 1, 13001, 1, "敵の物理/魔法被クリダメ＋10％", "キラースピア", "不思議な効果を持つ槍。鋭い一撃\nで敵に深い傷を負わせる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13002, 3, 1, 13002, 1, "物理/魔法クリダメ＋10％", "ハナミノカタナ", "不思議な効果を持つ刀。致命の攻\n撃を与えたときの威力を高める。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13003, 3, 1, 13003, 1, "通常攻撃強化＋30", "クリーヴフィスト", "不思議な効果を持つ籠手。強化\nされた一撃を放つことができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13004, 3, 1, 13004, 1, "物理/魔法クリティカル＋10％", "クリティカルアーク", "不思議な効果を持つ⼸。装備する\nと致命の一射を与えやすくなる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13005, 3, 1, 13005, 1, "ＨＰ吸収＋10", "アブゾーブハチェット", "不思議な効果を持つ斧。攻撃と共\nに敵の体力を吸い取る力を宿す。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14001, 4, 1, 14001, 1, "バフ強化＋30", "強化のジェム", "不思議な効果を持つ耳飾り。装備\n者の強化効果を高める。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14002, 4, 1, 14002, 1, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", "抗鉄のプレート", "不思議な効果を持つ鎧。同朋の\n強靭を、自身の攻撃に変換する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14003, 4, 1, 14003, 1, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", "抗魔のドレス", "不思議な効果を持つドレス。装備\n者の抗魔の才を攻撃⼒に換える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14004, 4, 1, 14004, 1, "物理/魔法攻撃力＋20％", "蛇紋のツルギ", "不思議な効果を持つ大剣。装備者\nの攻撃力を強化する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14005, 4, 1, 14005, 1, "物理/魔法防御力＋10％", "防守のメイル", "不思議な効果を持つ鎧。装備者の\n守りの力を高めることができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15001, 5, 1, 15001, 1, "回復強化＋10", "回生杖", "不思議な効果を持つ杖。\n癒しの⼒を高めてくれる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15002, 5, 1, 15002, 1, "ＨＰ＋10％", "豊命輪", "不思議な効果を持つ指輪。\n装備者の生命力を強化する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15003, 5, 1, 15003, 1, "15秒ごとに物理無効バリア展開（【守備】数×500無効）", "鉄防甲", "不思議な効果を持つ鎧。\n装備者を物理攻撃から守る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15004, 5, 1, 15004, 1, "15秒ごとに魔法無効バリア展開（【守備】数×500無効）", "魔封衣", "不思議な効果を持つローブ。\n装備者を魔法攻撃から守る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15005, 5, 1, 15005, 1, "継続ＨＰ回復（毎秒【守備】数×500回復）", "再生ノ腕輪", "不思議な効果を持つ腕輪。\n装備者に再生の力を与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21001, 1, 2, 21001, 2, "ＴＰ上昇＋10", "流烈の耳飾り", "不思議な効果を持った耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21002, 1, 2, 21002, 2, "ダメージを受けるたびＴＰ100回復（最大２回）", "星涙の輝盾", "不思議な効果を持った円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21003, 1, 2, 21003, 2, "バトル開始時ＴＰ200回復", "蒼珠の腕輪", "不思議な効果を持った腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21004, 1, 2, 21004, 2, "ＴＰ消費軽減＋4", "星明りの儀杖", "不思議な効果を持った杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21005, 1, 2, 21005, 2, "重複可能の行動速度＋20％", "疾風の翼靴", "不思議な効果を持った、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22001, 2, 2, 22001, 2, "状態異常命中＋40", "闇至宝アノマリーラ", "不思議な効果を持った、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22002, 2, 2, 22002, 2, "敵全体に呪い付与（毎秒【弱体】数×600ダメージ）", "邪眼杖ドゥーム", "不思議な効果を持った、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22003, 2, 2, 22003, 2, "敵全体に毒付与（毎秒【弱体】数×600ダメージ）", "影刃剣プラール", "不思議な効果を持った、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22004, 2, 2, 22004, 2, "敵の物理/魔法攻撃力－20％", "薔薇飾帽イレース", "不思議な効果を持った、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22005, 2, 2, 22005, 2, "敵の物理/魔法防御力－20％", "斬赤刃ディバイド", "不思議な効果を持った、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23001, 3, 2, 23001, 2, "敵の物理/魔法被クリダメ＋20％", "フェイタリティ", "不思議な効果を持った、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23002, 3, 2, 23002, 2, "物理/魔法クリダメ＋20％", "クレナイノタチ", "不思議な効果を持った、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23003, 3, 2, 23003, 2, "通常攻撃強化＋60", "ラプチャーガントレット", "不思議な効果を持った籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23004, 3, 2, 23004, 2, "物理/魔法クリティカル＋20％", "スカイストライク", "不思議な効果を持った、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23005, 3, 2, 23005, 2, "ＨＰ吸収＋20", "インヘイルアックス", "不思議な効果を持った斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24001, 4, 2, 24001, 2, "バフ強化＋60", "進化のイヤリング", "不思議な効果を持った、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24002, 4, 2, 24002, 2, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", "紅反鋼のアーマー", "不思議な効果を持った、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24003, 4, 2, 24003, 2, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", "聖減術のテラドレス", "不思議な効果を持った、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24004, 4, 2, 24004, 2, "物理/魔法攻撃力＋40％", "炎王のグレートソード", "不思議な効果を持った、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24005, 4, 2, 24005, 2, "物理/魔法防御力＋20％", "桜堅牢のコート", "不思議な効果を持った鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25001, 5, 2, 25001, 2, "回復強化＋20", "創世緑杖", "不思議な効果を持った杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25002, 5, 2, 25002, 2, "ＨＰ＋20％", "空命護珠", "不思議な効果を持った、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25003, 5, 2, 25003, 2, "15秒ごとに物理無効バリア展開（【守備】数×1000無効）", "鋼装堅鎧", "不思議な効果を持った、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25004, 5, 2, 25004, 2, "15秒ごとに魔法無効バリア展開（【守備】数×1000無効）", "魔護霊装", "不思議な効果を持った、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25005, 5, 2, 25005, 2, "継続ＨＰ回復（毎秒【守備】数×1000回復）", "緑護ノ盾輪", "不思議な効果を持った、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31001, 1, 3, 31001, 3, "ＴＰ上昇＋15", "瞬閃蝶の月飾り", "不思議な効果を持っている月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31002, 1, 3, 31002, 3, "ダメージを受けるたびＴＰ100回復（最大３回）", "海宝華の煌盾", "不思議な効果を持つ宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31003, 1, 3, 31003, 3, "バトル開始時ＴＰ300回復", "碧貴石の聖輪", "不思議な効果を持つ聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31004, 1, 3, 31004, 3, "ＴＰ消費軽減＋6", "永天鏡の王笏", "不思議な効果を持つ、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31005, 1, 3, 31005, 3, "重複可能の行動速度＋30％", "熾天使の翔靴", "不思議な効果を持つ、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32001, 2, 3, 32001, 3, "状態異常命中＋60", "幻獣飾テンタシオン", "不思議な効果を持つ、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32002, 2, 3, 32002, 3, "敵全体に呪い付与（毎秒【弱体】数×900ダメージ）", "深淵蝕杖マリディクト", "不思議な効果を持つ、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32003, 2, 3, 32003, 3, "敵全体に毒付与（毎秒【弱体】数×900ダメージ）", "滅刻凶刃ヴェノマス", "不思議な効果を持つ、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32004, 2, 3, 32004, 3, "敵の物理/魔法攻撃力－30％", "燦然御纏アノラ・ベイラ", "不思議な効果を持つ、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32005, 2, 3, 32005, 3, "敵の物理/魔法防御力－30％", "断壁鎖刃フラグメント", "不思議な効果を持つ、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33001, 3, 3, 33001, 3, "敵の物理/魔法被クリダメ＋30％", "リーサルハルバード", "不思議な効果を持つ、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33002, 3, 3, 33002, 3, "物理/魔法クリダメ＋30％", "オオトリノカタナ", "不思議な効果を持つ、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33003, 3, 3, 33003, 3, "通常攻撃強化＋90", "アースデストラクション", "不思議な効果を持つ、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33004, 3, 3, 33004, 3, "物理/魔法クリティカル＋30％", "ジャッジメントアロー", "不思議な効果を持つ、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33005, 3, 3, 33005, 3, "ＨＰ吸収＋30", "ザ・ハーヴェスト", "不思議な効果を持つ、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34001, 4, 3, 34001, 3, "バフ強化＋90", "極醒のアミュレット", "不思議な効果を持つ、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34002, 4, 3, 34002, 3, "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", "断刃拒絶のアルギュロス", "不思議な効果を持つ、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34003, 4, 3, 34003, 3, "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", "緋華栄華のグラナート", "不思議な効果を持つ、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34004, 4, 3, 34004, 3, "物理/魔法攻撃力＋60％", "覇光帝龍のグローリア", "不思議な効果を持つ、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34005, 4, 3, 34005, 3, "物理/魔法防御力＋30％", "華舞玲瓏のハルナデシコ", "不思議な効果を持つ、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35001, 5, 3, 35001, 3, "回復強化＋30", "妖精霊杖・癒光", "不思議な効果を持つ、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35002, 5, 3, 35002, 3, "ＨＰ＋30％", "泉湧命環・豊穣", "不思議な効果を持つ、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35003, 5, 3, 35003, 3, "15秒ごとに物理無効バリア展開（【守備】数×1500無効）", "白光輝鎧・断壁", "不思議な効果を持つ、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35004, 5, 3, 35004, 3, "15秒ごとに魔法無効バリア展開（【守備】数×1500無効）", "幽玄煌装・絶魔", "不思議な効果を持つ、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。");
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35005, 5, 3, 35005, 3, "継続ＨＰ回復（毎秒【守備】数×1500回復）", "輪廻ノ翡翠珠輪", "不思議な効果を持つ、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。");
