INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11001, "加速の玉飾り", "ＴＰ上昇＋5", "不思議な効果を持つイヤリング。\n装備者に活力加速を与える。", 11001, 1, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11002, "水蓮の盾", "ダメージを受けるたびＴＰ100回復（最大１回）", "不思議な効果を持つ円盾。受けた\n攻撃をエネルギーに変換できる。", 11002, 1, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11003, "青輝の環", "バトル開始時ＴＰ100回復", "不思議な効果を持つ腕輪。戦いの\n始まりとともに、装備者にエネル\nギーを与える。", 11003, 1, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11004, "冷玉の杖", "ＴＰ消費軽減＋2", "不思議な効果を持つ杖。装備者に\n力を温存させる効果をもたらす。", 11004, 1, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (11005, "風渡りの靴", "重複可能の行動速度＋10％", "不思議な効果を持つ靴。装備した\n者は迅速に動くことができる。", 11005, 1, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12001, "紫晶ビザリオ", "状態異常命中＋20", "不思議な効果を持つ首飾り。\n敵に異常を与えやすくなる。", 12001, 2, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12002, "魔杖メジナエ", "敵全体に呪い付与（毎秒【弱体】数×300ダメージ）", "不思議な効果を持つ杖。所持する\nだけで敵を呪うことができる。", 12002, 2, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12003, "暗剣ポワゾン", "敵全体に毒付与（毎秒【弱体】数×300ダメージ）", "不思議な効果を持つ短剣。持つだ\nけで敵を毒にすることができる。", 12003, 2, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12004, "破守帽ディスペル", "敵の物理/魔法攻撃力－10％", "不思議な効果を持つ帽子。装備す\nると敵の攻撃を弱体させる。", 12004, 2, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (12005, "崩刀ヤマツキ", "敵の物理/魔法防御力－10％", "不思議な効果を持つ刀。\n敵の守りを断ち崩す力がある。", 12005, 2, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13001, "キラースピア", "敵の物理/魔法被クリダメ＋10％", "不思議な効果を持つ槍。鋭い一撃\nで敵に深い傷を負わせる。", 13001, 3, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13002, "ハナミノカタナ", "物理/魔法クリダメ＋10％", "不思議な効果を持つ刀。致命の攻\n撃を与えたときの威力を高める。", 13002, 3, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13003, "クリーヴフィスト", "通常攻撃強化＋30", "不思議な効果を持つ籠手。強化\nされた一撃を放つことができる。", 13003, 3, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13004, "クリティカルアーク", "物理/魔法クリティカル＋10％", "不思議な効果を持つ⼸。装備する\nと致命の一射を与えやすくなる。", 13004, 3, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (13005, "アブゾーブハチェット", "ＨＰ吸収＋10", "不思議な効果を持つ斧。攻撃と共\nに敵の体力を吸い取る力を宿す。", 13005, 3, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14001, "強化のジェム", "バフ強化＋30", "不思議な効果を持つ耳飾り。装備\n者の強化効果を高める。", 14001, 4, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14002, "抗鉄のプレート", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×5）", "不思議な効果を持つ鎧。同朋の\n強靭を、自身の攻撃に変換する。", 14002, 4, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14003, "抗魔のドレス", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×5）", "不思議な効果を持つドレス。装備\n者の抗魔の才を攻撃⼒に換える。", 14003, 4, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14004, "蛇紋のツルギ", "物理/魔法攻撃力＋20％", "不思議な効果を持つ大剣。装備者\nの攻撃力を強化する。", 14004, 4, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (14005, "防守のメイル", "物理/魔法防御力＋10％", "不思議な効果を持つ鎧。装備者の\n守りの力を高めることができる。", 14005, 4, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15001, "回生杖", "回復強化＋10", "不思議な効果を持つ杖。\n癒しの⼒を高めてくれる。", 15001, 5, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15002, "豊命輪", "ＨＰ＋10％", "不思議な効果を持つ指輪。\n装備者の生命力を強化する。", 15002, 5, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15003, "鉄防甲", "15秒ごとに物理無効バリア展開（【守備】数×500無効）", "不思議な効果を持つ鎧。\n装備者を物理攻撃から守る。", 15003, 5, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15004, "魔封衣", "15秒ごとに魔法無効バリア展開（【守備】数×500無効）", "不思議な効果を持つローブ。\n装備者を魔法攻撃から守る。", 15004, 5, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (15005, "再生ノ腕輪", "継続ＨＰ回復（毎秒【守備】数×500回復）", "不思議な効果を持つ腕輪。\n装備者に再生の力を与える。", 15005, 5, 1, 1);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21001, "流烈の耳飾り", "ＴＰ上昇＋10", "不思議な効果を持った耳飾りの形\nをした遺物。流水の激しさのよう\nに、活力加速を増幅させる。", 21001, 1, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21002, "星涙の輝盾", "ダメージを受けるたびＴＰ100回復（最大２回）", "不思議な効果を持った円盾の形を\nした遺物。装備者に星の癒しを与\nえ攻撃をエネルギーに変換する。", 21002, 1, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21003, "蒼珠の腕輪", "バトル開始時ＴＰ200回復", "不思議な効果を持った腕輪の遺物\nあしらわれた蒼珠が、敵を感知し\n力を授ける。", 21003, 1, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21004, "星明りの儀杖", "ＴＰ消費軽減＋4", "不思議な効果を持った杖の遺物。\n封じられた星々の神秘が、力の消\n耗を肩代わりしてくれる。", 21004, 1, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (21005, "疾風の翼靴", "重複可能の行動速度＋20％", "不思議な効果を持った、翼の生え\nた靴形の遺物。装備した者に疾風\nの力を施し、行動を速める。", 21005, 1, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22001, "闇至宝アノマリーラ", "状態異常命中＋40", "不思議な効果を持った、⾸飾りの\n遺物。宝⽯の輝きで敵を虜にし、\n怪異をもたらすという。", 22001, 2, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22002, "邪眼杖ドゥーム", "敵全体に呪い付与（毎秒【弱体】数×600ダメージ）", "不思議な効果を持った、杖の形を\nした遺物。杖先のコアから流れる\n邪気が敵に呪いを付与する。", 22002, 2, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22003, "影刃剣プラール", "敵全体に毒付与（毎秒【弱体】数×600ダメージ）", "不思議な効果を持った、短剣の形\nをした遺物。刃に仕込まれた毒が\n気化し、接敵と共に毒を与える。", 22003, 2, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22004, "薔薇飾帽イレース", "敵の物理/魔法攻撃力－20％", "不思議な効果を持った、帽子の形\nをした遺物。絡みつく茨の様に、\n敵の攻撃力を阻害する。", 22004, 2, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (22005, "斬赤刃ディバイド", "敵の物理/魔法防御力－20％", "不思議な効果を持った、⼑の遺物\n血で⾚く染まった⼑⾝で、敵の装\n甲をたやすく断ち割る。", 22005, 2, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23001, "フェイタリティ", "敵の物理/魔法被クリダメ＋20％", "不思議な効果を持った、槍の遺物\n特殊な形の穂先が、敵により深い\n傷を刻み込む。", 23001, 3, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23002, "クレナイノタチ", "物理/魔法クリダメ＋20％", "不思議な効果を持った、刀の形を\nした遺物。秋の紅を映す美しき刃\nは致命の攻撃をさらに強化する。", 23002, 3, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23003, "ラプチャーガントレット", "通常攻撃強化＋60", "不思議な効果を持った籠⼿の遺\n物。装備者は研ぎ澄まされた強\n力な一撃を放つことができる。", 23003, 3, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23004, "スカイストライク", "物理/魔法クリティカル＋20％", "不思議な効果を持った、⼸の遺物\n装備者は烈⾵の⼒を授かり、獲物\nの急所を貫きやすくなる。", 23004, 3, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (23005, "インヘイルアックス", "ＨＰ吸収＋20", "不思議な効果を持った斧の遺物。\n繰り出される⼀撃は、掠めるだけ\nで獲物の魂を吸い取る。", 23005, 3, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24001, "進化のイヤリング", "バフ強化＋60", "不思議な効果を持った、耳飾りの\n形をした遺物。魔石に込められた\n魔力が強化の力を増幅させる。", 24001, 4, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24002, "紅反鋼のアーマー", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×10）", "不思議な効果を持った、鎧の遺物\n紅き鋼の加護は、守備力を攻撃力\nへと変換する力を持つ。", 24002, 4, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24003, "聖減術のテラドレス", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×10）", "不思議な効果を持った、ドレスの\n遺物。精霊の加護が、防魔結界か\nら力を集めてくれる。", 24003, 4, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24004, "炎王のグレートソード", "物理/魔法攻撃力＋40％", "不思議な効果を持った、大剣の形\nをした遺物。湧き出る炎の力が、\n攻撃の能力を大きく高める。", 24004, 4, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (24005, "桜堅牢のコート", "物理/魔法防御力＋20％", "不思議な効果を持った鎧の遺物。\n千年咲く桜の如く、盤石な堅牢さ\nを装備者に与える。", 24005, 4, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25001, "創世緑杖", "回復強化＋20", "不思議な効果を持った杖の遺物。\n装備者に⼤樹の恵みを施し、治癒\n術の才覚を授けてくれる。", 25001, 5, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25002, "空命護珠", "ＨＰ＋20％", "不思議な効果を持った、指輪の形\nをした遺物。装備者を天空の護り\nで包み込み生命力を増幅させる。", 25002, 5, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25003, "鋼装堅鎧", "15秒ごとに物理無効バリア展開（【守備】数×1000無効）", "不思議な効果を持った、鎧の形を\nした遺物。高度な技術で精錬され\nた鋼は、物理攻撃から身を護る。", 25003, 5, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25004, "魔護霊装", "15秒ごとに魔法無効バリア展開（【守備】数×1000無効）", "不思議な効果を持った、ローブの\n遺物。神樹の蔦が織り込まれ、装\n備者を魔法から護る。", 25004, 5, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (25005, "緑護ノ盾輪", "継続ＨＰ回復（毎秒【守備】数×1000回復）", "不思議な効果を持った、腕輪の遺\n物。溢れ出す⾃然の⼒は装備者に\n生命の息吹をもたらす。", 25005, 5, 2, 2);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31001, "瞬閃蝶の月飾り", "ＴＰ上昇＋15", "不思議な効果を持っている月を象\nる耳飾り。内部から発する魔力は\n装備者に蝶のような瞬麗さを授け\n活力加速を大きく上昇させる。", 31001, 1, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31002, "海宝華の煌盾", "ダメージを受けるたびＴＰ100回復（最大３回）", "不思議な効果を持つ宝石を飾った\n円盾。海のような青き魔力は装備\n者に庇護を与え、迫り来る脅威す\nらエネルギーへと昇華させる。", 31002, 1, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31003, "碧貴石の聖輪", "バトル開始時ＴＰ300回復", "不思議な効果を持つ聖なる腕輪。\n失われた技術で装飾された宝石が\n装備者に加護を齎し、危機が迫る\nと膨大な力を分け与える。", 31003, 1, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31004, "永天鏡の王笏", "ＴＰ消費軽減＋6", "不思議な効果を持つ、かつて天界\nに伝えられし杖。天にかざすと鏡\n⽟に篭められた祈りが溢れ出して\n尽きることなき力を授ける。", 31004, 1, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (31005, "熾天使の翔靴", "重複可能の行動速度＋30％", "不思議な効果を持つ、天使の加護\nを宿した戦靴。装備するだけで神\n性を授かり、天使のような自在さ\nで空を翔ることができる。", 31005, 1, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32001, "幻獣飾テンタシオン", "状態異常命中＋60", "不思議な効果を持つ、幻獣を象る\n首飾り。この宝石の神秘に魅せら\nれた者は、一切の異変や脅威に抗\nうことができなくなってしまう。", 32001, 2, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32002, "深淵蝕杖マリディクト", "敵全体に呪い付与（毎秒【弱体】数×900ダメージ）", "不思議な効果を持つ、月を蝕む杖\n存在するだけで、絶えず呪力の奔\n流を溢れさせ、近づく全ての敵を\n呪いで浸食していく。", 32002, 2, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32003, "滅刻凶刃ヴェノマス", "敵全体に毒付与（毎秒【弱体】数×900ダメージ）", "不思議な効果を持つ、黒刃の刺剣\n刃に埋め込まれた宝玉が、絶え間\nなく邪毒を撒き散らし、破滅へと\n刻々と時を数える。", 32003, 2, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32004, "燦然御纏アノラ・ベイラ", "敵の物理/魔法攻撃力－30％", "不思議な効果を持つ、宝飾をあし\nらったベール。蔽布の下から滲み\n出るオーラが、相手の敵意を解き\nほぐし、無力化させてしまう。", 32004, 2, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (32005, "断壁鎖刃フラグメント", "敵の物理/魔法防御力－30％", "不思議な効果を持つ、⼑⾝に鎖を\n這わせた妖⼑。主の意思に逆らい\n暴れる鎖が怨敵の守りを打ち崩し\n紫黒の凶刃が悉くを断ち斬る。", 32005, 2, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33001, "リーサルハルバード", "敵の物理/魔法被クリダメ＋30％", "不思議な効果を持つ、地神の力を\n宿した烈槍。大地の力を糧に放つ\n無比の一閃は、地に立つすべての\n敵に甚大なダメージを与える。", 33001, 3, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33002, "オオトリノカタナ", "物理/魔法クリダメ＋30％", "不思議な効果を持つ、伝説に語ら\nれる極みの一振り。鳳凰の力を宿\nした真紅の刃は、焔の如き赫く激\n烈な一撃を与える。", 33002, 3, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33003, "アースデストラクション", "通常攻撃強化＋90", "不思議な効果を持つ、地核のエネ\nルギーを封じた籠手。振るう一撃\nは、世界を破壊するかの如く、通\n常攻撃を大きく強化する。", 33003, 3, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33004, "ジャッジメントアロー", "物理/魔法クリティカル＋30％", "不思議な効果を持つ、咎⼈を裁き\nし聖⼸。この弓に選ばれし装備者\nは神⾵の加護を得て、対象に慈悲\nを与えず一瞬で急所を射貫く。", 33004, 3, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (33005, "ザ・ハーヴェスト", "ＨＰ吸収＋30", "不思議な効果を持つ、金色の⼤戦\n斧。刈り取った獲物の魂を糧に、\nその絢爛なる刃は燦燦と輝きを増\nしてゆく。", 33005, 3, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34001, "極醒のアミュレット", "バフ強化＋90", "不思議な効果を持つ、炎天の魔力\nを封じた耳飾り。潜在能力を究極\nまで呼び覚まし、強化の力を大幅\nに上昇させる。", 34001, 4, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34002, "断刃拒絶のアルギュロス", "物理/魔法攻撃力アップ\n（先頭キャラの物理防御力×15）", "不思議な効果を持つ、⽩⾦の鎧。\n⽩⻯の鱗を繋ぎ合わせた外装が盟\n友の力を集結させる効果を持ち強\n力無比な一撃を可能にする。", 34002, 4, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34003, "緋華栄華のグラナート", "物理/魔法攻撃力アップ\n（先頭キャラの魔法防御力×15）", "不思議な効果を持つ、栄華の象徴\nとして仕立てられたドレス。深紅\nに咲き乱れる花の加護は、装備者\nの絶魔の⼒を攻撃⼒へ変換する。", 34003, 4, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34004, "覇光帝龍のグローリア", "物理/魔法攻撃力＋60％", "不思議な効果を持つ、光⿓の⼒を\n授かりし⼤剣。その⼀振りはかつ\nての栄光の羽ばたきを想起させ、\n極限の覇気を世界に放つ。", 34004, 4, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (34005, "華舞玲瓏のハルナデシコ", "物理/魔法防御力＋30％", "不思議な効果を持つ、東の国に伝\nわりし聖鎧。玉の様に透き通る輝\nきを放つ鎧は、装備者に華の加護\nを授け、守護の力を高める。", 34005, 4, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35001, "妖精霊杖・癒光", "回復強化＋30", "不思議な効果を持つ、妖精の輝き\nを封じた杖。宝⽟から無限の⽣命\n⼒が溢れ出し、増幅された快癒の\n⼒であらゆる苦しみを取り除く。", 35001, 5, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35002, "泉湧命環・豊穣", "ＨＰ＋30％", "不思議な効果を持つ、翠緑に彩ら\nれた指輪。装備者に、かつて豊穣\nに溢れた楽園の光景を想起させ、\n際限なき⽣命⼒を与える。", 35002, 5, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35003, "白光輝鎧・断壁", "15秒ごとに物理無効バリア展開（【守備】数×1500無効）", "不思議な効果を持つ、白金の輝き\nを放つ鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n物理攻撃を断つ障壁を展開する。", 35003, 5, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35004, "幽玄煌装・絶魔", "15秒ごとに魔法無効バリア展開（【守備】数×1500無効）", "不思議な効果を持つ、幽庵の煌き\nを宿す鎧。顕現する翅は、意思を\n持つかのよう、装備者に呼応して\n魔法攻撃を絶つ障壁を展開する。", 35004, 5, 3, 3);
INSERT INTO `v1_36eee0c2ffea64515ec733d205454f9a19253fad7dcd44a1c07b5c76eb481f87` VALUES (35005, "輪廻ノ翡翠珠輪", "継続ＨＰ回復（毎秒【守備】数×1500回復）", "不思議な効果を持つ、翠宝玉をあ\nしらった腕輪。常に滾々とマナが\n湧き出し、生命力を湛え続ける。", 35005, 5, 3, 3);
