INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204502, "スパイククラブ", 204502, 0, 0, 270, 80, 1, 2.0, 0, 0, 0, "【物理】近距離攻撃\n巨大なハサミと硬い殻が自慢の\nカニの魔物。砂に潜んで気長に獲物\nを待つ姿はどこか愛らしい。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204503, "スパイククラブ", 204502, 0, 0, 270, 20, 1, 2.0, 0, 0, 0, "【物理】近距離攻撃\n巨大なハサミと硬い殻が自慢の\nカニの魔物。砂に潜んで気長に獲物\nを待つ姿はどこか愛らしい。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204600, "マジックレイ", 204600, 0, 0, 270, 1080, 2, 3.5, 0, 0, 0, "【魔法】範囲攻撃\n頭部の石から発する超音波で\n巧みに狩りをするエイの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204700, "リーフボア", 204700, 0, 0, 270, 200, 1, 4.0, 0, 0, 0, "【物理】近接攻撃\n固い葉っぱを鎧のように纏い、\n痛烈な体当たりをする大イノシシ。\nだが所詮葉っぱ。魔法は防げない。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204701, "サンドボア", 204701, 0, 0, 270, 200, 1, 4.0, 0, 0, 0, "【物理】近接攻撃\n砂を固めて鎧のように纏うのは、\n捨て身を恐れない一族の戦士の証。\nだが所詮は砂。魔法は防げない。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204702, "ワイルドボア", 204702, 0, 0, 270, 170, 1, 4.0, 0, 0, 0, "【物理】近接攻撃\n熱地に生きる、原始の大イノシシ。\n数多のマグマ岩を突き続けた先に、\n鎧をも砕く牙の一撃を体得した。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204703, "リーフボア", 204703, 0, 0, 270, 200, 1, 4.0, 0, 0, 0, "【物理】近接攻撃\n固い葉っぱを鎧のように纏い、\n痛烈な体当たりをする大イノシシ。\nだが所詮葉っぱ。魔法は防げない。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204800, "ルインドール", 204800, 0, 0, 270, 150, 1, 1.8, 0, 0, 0, "【物理】近接攻撃\nイクト・ガラディオンに付き従う古代の自動人形。\n意思を持たず、自身が瓦解するまで戦い続ける。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204801, "古代小兵", 204801, 0, 0, 270, 600, 2, 1.8, 0, 0, 0, "【物理】近接攻撃\nイクト・ガラディオンに付き従う古代の自動人形。\n意思を持たず、自身が瓦解するまで戦い続ける。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204802, "古代小兵", 204801, 0, 0, 270, 350, 2, 1.8, 0, 0, 0, "【物理】近接攻撃\nイクト・ガラディオンに付き従う古代の自動人形。\n意思を持たず、自身が瓦解するまで戦い続ける。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204803, "古代小兵", 204801, 0, 0, 270, 100, 2, 1.8, 0, 0, 0, "【物理】近接攻撃\nイクト・ガラディオンに付き従う古代の自動人形。\n意思を持たず、自身が瓦解するまで戦い続ける。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (204900, "ゴブリンリトル", 204900, 0, 0, 270, 100, 1, 0.5, 0, 0, 0, "【物理】近接攻撃\n幼くも、勇猛な見習いゴブリン。\n非力を補う動きで、前線を駆ける。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205000, "ポイズントード", 205000, 0, 0, 270, 500, 1, 2.5, 0, 0, 0, "【物理】近距離攻撃\n擬態した宝石に釣られた者を\n捕食する、毒舌ガマの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205001, "ポイズントード", 205000, 0, 0, 270, 500, 1, 2.5, 0, 0, 0, "【物理】近距離攻撃\n擬態した宝石に釣られた者を\n捕食する、毒舌ガマの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205100, "ツチノコブラ", 205100, 0, 13, 270, 550, 1, 2.5, 0, 0, 0, "【物理】中距離攻撃\n物陰から飛びかかり魔力を吸って\n腹を満たすという魔物ヘビの変種。\n魔力が高いほど狙われやすい。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205200, "タツノコナイト", 205200, 0, 0, 270, 650, 1, 2.0, 0, 0, 0, "【物理】中距離攻撃\n海で溺れた騎士の槍を奪い、\n縄張りを警備するタツノコの戦士。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205201, "タツノコナイト", 205200, 0, 0, 270, 270, 1, 2.0, 0, 0, 0, "【物理】中距離攻撃\n海で溺れた騎士の槍を奪い、\n縄張りを警備するタツノコの戦士。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205300, "アイスチック", 205300, 0, 0, 270, 550, 2, 3.0, 0, 0, 0, "【魔法】中距離攻撃\n氷を纏う尾羽根を飛ばして\n攻撃するトリの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205301, "アイスチック", 205301, 0, 0, 1000, 450, 2, 3.0, 0, 0, 0, "【魔法】中距離攻撃\n氷を纏う尾羽根を飛ばして\n攻撃するトリの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205400, "アイスフィッシュ", 205400, 0, 12, 270, 550, 1, 2.6, 0, 0, 0, "【物理】近接攻撃\n氷河を削って巣を作り、近づく者は\n無差別に襲うサカナの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205401, "マグマフィッシュ", 205401, 0, 12, 270, 550, 1, 2.4, 0, 0, 0, "【物理】近接攻撃\n岩石をも噛み砕く強靭なアゴで\n辺りを喰い荒らすサカナの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205402, "アイスフィッシュ", 205402, 0, 12, 1000, 300, 1, 2.6, 0, 0, 0, "【物理】近接攻撃\n氷河を削って巣を作り、近づく者は\n無差別に襲うサカナの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205500, "ランドスネーク", 205500, 0, 14, 270, 550, 1, 3.0, 0, 0, 0, "【物理】中距離攻撃\n穴を掘って獲物をじっと待ち、\n毒牙で確実に仕留めるヘビの魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205600, "クレイジードギー", 205600, 0, 0, 270, 100, 1, 2.5, 0, 0, 0, "【物理】近接攻撃\n主人と共に生み出された、凶暴な改造犬。\n大きな頭を支えようと、必死な顔が殊更、怖い。");
