INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (205900, "イカリイカ", 205900, 0, 0, 270, 200, 0, 2.5, 0, 0, 0, "【物理】中距離攻撃\nイカリ頭のイカ魔物。金属質の錨は\n高硬度のかわりに重く、バランスを\n崩しよくひっくり返る。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206000, "トドドン", 206000, 0, 1, 270, 100, 1, 4.0, 0, 0, 0, "【物理】近接攻撃\n身体も態度も巨漢な北海のドン。\n纏う厚い脂肪が敵からの物理攻撃\nを押止める。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206100, "ワイズワンド", 206100, 0, 7, 270, 1080, 2, 4.5, 0, 0, 0, "【魔法】遠距離攻撃\n浴びれば凍える冷気の魔法で、\n戦意を奪うオウムガイ型の魔物。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206200, "ギャングオッター", 206200, 0, 1, 270, 650, 1, 3.5, 0, 0, 0, "【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206201, "ノーティビーバー", 206201, 0, 1, 270, 650, 1, 0.5, 0, 0, 0, "【物理】中距離攻撃\n片っ端から木を削り倒すビーバー。\n削った木片は家作りだけでなく、\n家を守るための飛び道具となる。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206300, "ルナポッド", 206300, 0, 0, 270, 1110, 0, 10.0, 0, 0, 0, "【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206301, "ルナポッド", 206300, 0, 0, 270, 1800, 0, 10.0, 0, 0, 0, "【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206400, "リトルアイ", 206400, 0, 0, 270, 100, 1, 2.5, 0, 0, 0, "【物理】近接攻撃\n一ツ目族の王に仕える、小さな召使い。\n王様がくれた金ピカ装備が、大のお気に入り。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206500, "コマンド・ウキキー", 206500, 0, 0, 300, 100, 1, 2.5, 0, 0, 0, "【物理】近接攻撃\nウキキーがより成長した姿。幼少より大岩を投げ\n続けたその太腕は、絶大な打撃を繰り出す。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206700, "ヒヨリのシャドウ", 206700, 0, 1, 270, 200, 1, 2.295, 0, 0, 0, "ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206701, "レイ（ニューイヤー）のシャドウ", 206701, 0, 4, 270, 400, 1, 1.965, 0, 0, 0, "レイ（ニューイヤー）の姿をした特殊な魔物\n\n・敵全体に物理中ダメージを与え、\n　さらにスタンさせる。\n・【挑発】敵全体を挑発する。\n　挑発後のモーション中に攻撃を受けた場合、\n　範囲内の敵すべてに物理中ダメージを与え、\n　さらに物理防御力を小ダウンさせる。\n・自分の周囲の味方すべてに\n　魔法無効バリアを展開し、さらに\n　自身の魔法防御力を中アップさせる。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (206702, "ユイ（ニューイヤー）のシャドウ", 206702, 0, 7, 270, 600, 2, 2.27, 0, 0, 0, "ユイ（ニューイヤー）の姿をした特殊な魔物\n\n・味方全体に物理/魔法無効バリアと\n　継続ＨＰ回復状態を付与するが、\n　自身の魔法攻撃力を大ダウンさせる。\n・前から２番目の敵１キャラの\n　物理攻撃力と魔法攻撃力を大ダウンさせる。\n・魔法攻撃を行う全ての味方キャラの\n　魔法攻撃力を大アップさせる。");
