INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (214400, "スリーピィオウル", 214400, 0, 0, 270, 645, 2, 3.5, 0, 0, 0, "【魔法】中距離攻撃\n寝不足で気怠げなフクロウの魔物。\n敵を睨み術をかけ、動きを奪う。");
