INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (253300, "イカリイカ", 253300, 0, 0, 270, 405, 2, 2.5, 0, 0, 0, "【魔法】【妨害】\nイカリ頭のイカ魔物。金属質の錨は高硬度の\nかわりに重く、バランスを崩しよくひっくり返る。\n\n・近接攻撃、行動速度ダウン\n　（ダメージ無効化時も付与）\n・与えるダメージによるＴＰ回復量80％ダウン");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (253400, "ツキツキ", 253400, 0, 0, 270, 400, 1, 0.9, 0, 0, 0, "【物理】【妨害】\n魔力の流れに敏感なキツツキ。\n鋭い嘴の攻撃で気力を減衰させる。\n\n・魔攻高いキャラ攻撃、ＴＰダウン\n・与えるダメージによるＴＰ回復量80％ダウン");
