INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (255100, "アイスチック", 255100, 0, 0, 270, 575, 2, 3.0, 0, 0, 0, "【魔法】【中距離】\n氷を纏う尾羽根を飛ばして攻撃するトリの魔物。\n\n・前から２番目攻撃\n・与えるダメージによるＴＰ回復量80％ダウン");
