INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (303600, "バーンサウルス", 303600, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, "【物理】強靭な脚と鋭利なツメで\n獲物を一瞬で狩る、獰猛な恐竜。\n滅びた大地でマグマを喰らい、\n生存の道を見つけた孤高の存在。\n\n・敵全体に物理中ダメージを与え、\n　自分の行動速度を上げる。\n\n・前方3キャラに物理中ダメージ\n　を与え、行動速度を下げる。");
