INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (307404, "ペテルギウス", 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, "【ＭＯＤＥ１】レムとラムのシャドウを召喚。\n大罪司教の狂気に呑まれた双子の影は、\n魔女教の手となり、仲間の前に立ち塞がる。\n\n・目の前の敵１キャラに物理ダメージを与える。");
