INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312305, "グレートトゥンヌス", 312305, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, "【ＭＯＤＥ２】海を駆ける怪魚はさらに力を増す。\nひと泳ぎで自身を鼓舞するかのように大渦をおこ\nし大海を分かつ巨大な砲弾となって敵に激突する。\n\n・ユニオンバーストは、前から３キャラに\n　物理特大ダメージを与え\n　さらにノックバックさせる。\n・自身の物理攻撃力を大アップ、行動速度を\n　中アップ、ＴＰを小アップさせる。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312306, "グレートトゥンヌス", 312302, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, "【ＭＯＤＥ３】幾度もその黒い巨体に銛を受けよ\nうとも、伝説の巨魚の猛進は止められない。\n永きにわたる海の漢たちとの勝負の決着の時。\n\n・ユニオンバーストは、敵全体にキャラの最大\n　ＨＰに応じた物理割合ダメージを与える。\n　さらにノックバックさせる。\n・自身の物理攻撃力を大アップ、行動速度を\n　中アップ、ＴＰを大アップさせる。\n・残り時間が35秒を切ると、さらに攻撃が激し\n　く凶暴になる。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312400, "アイスグリフォン", 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, "【物理】氷翼を広げ、冒険者を威嚇\nする大鷲。縄張りに踏み込んだ者\nを、容赦なく氷漬けにし子どもたち\nの贄とする。\n\n・ユニオンバーストは、前方小範囲\n　に物理大ダメージを与え、さらに\n　大きく吹き飛ばし、氷結状態に\n　する。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312401, "アイスグリフォン", 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, "【物理】氷翼を広げ、冒険者を威嚇\nする大鷲。縄張りに踏み込んだ者\nを、容赦なく氷漬けにし子どもたち\nの贄とする。\n\n・ユニオンバーストは、前方小範囲\n　に物理大ダメージを与え、さらに\n　大きく吹き飛ばし、氷結状態に\n　する。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312500, "マダムエレクトラ", 312500, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, "【魔法】塔の魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・敵全体に魔法大ダメージを与え、自分の魔法\n　攻撃力と、行動速度を小アップさせる。\n　物理攻撃をする敵にはさらに追加ダメージ。\n・前方範囲内の敵すべてに魔法中ダメージを与え\n　物理防御力と魔法防御力を中ダウンさせる。\n・物理攻撃力が最も高い敵１キャラに魔法大\n　ダメージを与え、長時間麻痺状態にする。\n　自分のＨＰが85％を切ると、魔法攻撃力が\n　最も高い敵にもこの攻撃を行う。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312501, "マダムエレクトラ", 312501, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, "【魔法】魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・マダムエレクトラから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、自身の魔法攻撃力と魔法\n　防御力をバトル終了まで小アップさせ、最も近\n　い敵１キャラに魔法特大ダメージを与える。");
INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (312502, "マダムエレクトラ", 312502, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, "【魔法】魔力を蓄え雷撃として放電する怪鳥。\n雷雲と共に顕現し、天災の如き雷で敵を葬り去る。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を中アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％カッ\n　ト効果を付与する。\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\n　させ、ＴＰ上昇を小ダウンさせる。\n・マダムエレクトラから受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、バトル終了まで自身の魔\n　法攻撃力を小アップ、魔法防御力を中アップさ\n　せる。その後、最も近い敵１キャラに魔法特大\n　ダメージを与え、麻痺状態を付与する。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・最も近い敵１キャラに魔法特大ダメージを与え、\n　麻痺状態を付与する。このスキルの状態異常は\n　ダメージを無効化しても付与される。");
