INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (316100, "レインスピリット", 316100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, "【魔法】塔の魔力が集まり形を成した冷酷な水霊。\n吹き荒れる暴雨は、無慈悲に冒険者を飲み込む。\n\n・ユニオンバーストは、敵全体にキャラの\n　最大ＨＰに応じた魔法割合ダメージを\n　与えて吹き飛ばす。\n　さらに、前方範囲内の敵すべてにキャラの\n　最大ＨＰに応じた追加の魔法割合ダメージを\n　与える。\n・前方範囲内の敵すべてに魔法中ダメージを与え、\n　小さく吹き飛ばす。さらに最も近い敵１キャラ\n　に追加の魔法中ダメージを与える。\n・一番遠くの敵１キャラに魔法中ダメージを与え、\n　さらに小さく引き寄せる。\n・レインスピリット自身のＨＰが40%を切ると、\n　自分の魔法防御力を特大アップさせ、\n　さらに攻撃が激しく狂暴になる。");
