INSERT INTO `v1_38826d69bff108a9515e6456db1a103ea6eb0a3859ea30295e67e6c744a6be8c` VALUES (321900, "バジリスク・アロス", 321900, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, "【魔法】巨大に変異したバジリスクの亜種。異相\nの巨体を羽ばたかせ、獲物を絶望に突き落とす。\n\n・バジリスク・アロス、スノウゴート、アイス\n　チック、アイスフィッシュ、アイスモールから\n　受けるすべてのダメージで発生する、パーティ\n　キャラのＴＰ回復量を中ダウンさせる。\n・最も近い敵１キャラに魔法中ダメージを与える。");
