INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52001, "500マイルを獲得する。", 51001, 1, 0, 1, 500, "ペコリーヌの\n満腹ツインおにぎり", 1, "ペコリーヌの満腹ツインおにぎり", 2, 10, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 2, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 0, 1);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52002, "500マイルを獲得する。", 51002, 1, 0, 1, 500, "コッコロの\nゆるりと休憩サラダ", 2, "コッコロのゆるりと休憩サラダ", 30001, 10, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 2, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 0, 3);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52003, "平凡な未鑑定トレジャーを1個獲得する。", 51003, 1, 0, 1, 500, "キャルの\n絶品五つ星バーガー", 1, "キャルの絶品五つ星バーガー", 20004, 10, "このターンに振るダイスの目が必ず「5」になる。", 1, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52004, "800マイルを獲得する。", 51004, 3, 0, 1, 500, "リリのコツコツ\nまい進かしわ餅", 3, "リリのコツコツまい進かしわ餅", 20001, 10, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 4, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52005, "次にダイスを振った際、1マス多く進める。", 51005, 0, 0, 3, 500, "クリアの彩り華やか\nバラエティ弁当", 3, "クリアの彩り華やかバラエティ弁当", 1, 11, "ランダムなイベントを発生させる。", 1, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 0, 4);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52006, "800マイルを獲得する。", 51006, 1, 0, 1, 500, "プレシアの食いしん坊\nぶーぶー肉まん", 1, "プレシアの食いしん坊ぶーぶー肉まん", 20002, 10, "このターンに振るダイスの目が「4・5・6」のみになる。", 4, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52007, "福引券を1枚獲得する。", 51007, 2, 0, 1, 500, "カヤとイノリの\n食欲倍増ケバブサンド", 1, "カヤとイノリの食欲倍増ケバブサンド", 1, 10, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 3, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 0, 7);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52008, "500マイルを獲得する。", 51008, 0, 1, 2, 500, "タマキの金運招く\nレインボーたい焼き", 2, "タマキの金運招くレインボーたい焼き", 200, 10, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 2, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 0, 5);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52009, "平凡な未鑑定トレジャーを1個獲得する。", 51009, 2, 0, 1, 500, "クローチェの\nフルパワーポテトサラダ", 2, "クローチェのフルパワーポテトサラダ", 1, 10, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 1, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 1, 7);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52010, "500マイルを獲得する。", 51010, 0, 2, 2, 500, "アヤネとクルミの\n青空ピクニックセット", 3, "アヤネとクルミの青空ピクニックセット", 1, 10, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 2, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 0, 6);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52011, "福引券を1枚獲得する。", 51011, 2, 0, 1, 500, "リマの不思議な\nスイートアップル", 3, "リマの不思議なスイートアップル", 1, 10, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 3, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 0, 7);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52012, "平凡な未鑑定トレジャーを1個獲得する。", 51012, 1, 0, 1, 500, "ニノンのオリジナル\n一二三寿司", 2, "ニノンのオリジナル一二三寿司", 30002, 10, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 0, 3);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52013, "800マイルを獲得する。", 51013, 0, 0, 3, 500, "クレジッタの開運\nお菓子盛り合わせ", 3, "クレジッタの開運お菓子盛り合わせ", 2, 10, "RANK2のマイルショップを開く。", 4, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 0, 8);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52014, "次にダイスを振った際、1マス多く進める。", 51014, 2, 0, 1, 500, "リノの\n「時は金時」かき氷", 2, "リノの「時は金時」かき氷", 1, 11, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52015, "次にダイスを振った際、1マス多く進める。", 51015, 1, 0, 1, 500, "シズルの愛情\n盛り盛りハンバーグ", 1, "シズルの愛情盛り盛りハンバーグ", 2, 11, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 0, 1);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52016, "なし", 51016, 1, 0, 1, 500, "アオイの疾走\nサンドイッチ", 3, "アオイの疾走サンドイッチ", 20005, 0, "このターンに振るダイスの目が必ず「6」になる。", 0, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52017, "なし", 51017, 2, 0, 1, 500, "クウカの緊縛\n触手活け造り", 3, "クウカの緊縛触手活け造り", 1, 10, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 5, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 1, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52018, "1000マイルを獲得する。", 51018, 1, 0, 1, 500, "ランファの世話焼き三昧肉じゃが", 1, "ランファの世話焼き三昧肉じゃが", 20006, 10, "このターンに振るダイスの目が必ず「3」になる。", 6, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52019, "なし", 0, 1, 0, 1, 500, "ワカナの節約腹八分目カレー", 1, "ワカナの節約腹八分目カレー", 20007, 0, "このターンに振るダイスの目が必ず「8」になる。", 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52020, "次にダイスを振った際、1マス多く進める。", 51014, 2, 0, 2, 500, "リノの\n「時は金時」かき氷", 2, "リノの「時は金時」かき氷", 1, 11, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52021, "	平凡な未鑑定トレジャーを2個獲得する。", 51021, 3, 0, 2, 500, "カスミの真実見抜く\nボンボン・ショコラ", 3, "カスミの真実見抜くボンボン・ショコラ", 2, 10, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 7, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52022, "なし", 51022, 3, 0, 1, 500, "アキノのゲヘニアン\nチキンプレート", 1, "アキノのゲヘニアンチキンプレート", 20008, 0, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 0, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52023, "平凡な未鑑定トレジャーを1個獲得する。", 51023, 2, 0, 1, 500, "ミミのコロコロ\nお手玉おにぎり", 1, "ミミのコロコロお手玉おにぎり", 1, 10, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 1, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 1, 7);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52024, "次にダイスを振った際、3マス多く進める。", 51024, 0, 0, 3, 500, "アユミの隠密\nガトーインビジブル", 3, "アユミの隠密ガトーインビジブル", 2, 11, "RANK2のマイルショップを開く。", 3, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 0, 8);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52025, "なし", 51025, 1, 0, 2, 500, "アネモネの\n花爛漫ステーキサラダ", 3, "アネモネの花爛漫ステーキサラダ", 3, 0, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52026, "福引券を2枚獲得する。", 51026, 0, 0, 3, 500, "アンナの\n煉獄蕃茄煮混沌卵", 1, "アンナの煉獄蕃茄煮混沌卵", 1, 10, "ランダムなイベントを発生させる。", 8, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 0, 4);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52027, "なし", 51027, 2, 0, 1, 500, "ユイの\n栄養満点まごころ弁当", 3, "ユイの栄養満点まごころ弁当", 20004, 0, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52028, "なし", 51028, 1, 0, 1, 500, "リンの\nナッツ入りぐうたらあんパン", 3, "リンのナッツ入りぐうたらあんパン", 30003, 10, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 9, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52029, "なし", 51029, 1, 0, 1, 500, "シェフィの\n氷晶煌めく手作りアイス", 2, "シェフィの氷晶煌めく手作りアイス", 30004, 0, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, 3);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52030, "なし", 51030, 3, 0, 3, 500, "ミソラの\n気まぐれ味噌ラーメン★", 1, "ミソラの気まぐれ味噌ラーメン★", 1, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 0, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, 12);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52031, "なし", 51031, 1, 0, 1, 500, "イロハの\n最強クワガタゼリー（人間用）", 3, "イロハの最強クワガタゼリー（人間用）", 20009, 0, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 0, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, 2);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52032, "次にダイスを振った際、3マス多く進める。", 51032, 2, 0, 2, 500, "グレイスの\n目ん玉飛び出るカップケーキ", 3, "グレイスの目ん玉飛び出るカップケーキ", 4, 11, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 3, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52033, "なし", 51033, 0, 0, 3, 500, "ソノの今日は特別ご褒美オムライス", 1, "ソノの今日は特別ご褒美オムライス", 3, 0, "RANK3のマイルショップを開く。", 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 0, 8);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52034, "平凡な未鑑定トレジャーを1個獲得する。", 51034, 2, 0, 2, 500, "ジータの\nグリルド・グラッジチャンク", 1, "ジータのグリルド・グラッジチャンク", 1, 10, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 0, 9);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52035, "貴重な未鑑定トレジャーを1個獲得する。", 51035, 0, 0, 3, 500, "ルイズマリーの\n成功しすぎてるフライ", 1, "ルイズマリーの成功しすぎてるフライ", 1, 10, "ランダムなイベントを発生させる。", 10, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 0, 4);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52036, "なし", 51036, 3, 0, 1, 500, "ホウオウの\n食べる権利をあげます！饅頭", 3, "ホウオウの食べる権利をあげます！饅頭", 1, 0, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 0, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", 1, 7);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52037, "平凡な未鑑定トレジャーを3個獲得する。", 51037, 0, 1, 2, 500, "魔法少女\nナナカちゃんウエハース", 3, "魔法少女ナナカちゃんウエハース", 1, 10, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 11, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", 0, 6);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52038, "福引券を1枚獲得する。", 51038, 1, 0, 1, 500, "アリサがお届け♪\nリノセウスの包み焼き", 1, "アリサがお届け♪リノセウスの包み焼き", 2, 10, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 3, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", 0, 1);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52039, "なし", 51039, 2, 0, 3, 500, "イリヤの†ブラッディ†\nトマトジュース", 2, "イリヤの†ブラッディ†トマトジュース", 2, 0, "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", 0, "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", 0, 12);
INSERT INTO `v1_3940940d85a9da9a0a1053115e42ebad2ef422eded9102e36cf397599aa5deda` VALUES (52040, "次にダイスを振った際、2マス多く進める。", 51040, 3, 0, 2, 500, "マツリの活力源\n～騎士団遠征プレート～", 1, "マツリの活力源～騎士団遠征プレート～", 2, 11, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", 0, 9);
