INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10.85, 24, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 100, 1, 100, 7);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (11.15, 24, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 101, 1, 101, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (6.4, 24, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 102, 7, 102, 5);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (0.0, 24, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 103, 3, 103, 3);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (2.43, 24, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 104, 2, 104, 8);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (9.08, 24, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 105, 4, 105, 3);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (0.0, 24, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 106, 6, 106, 6);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10.6, 24, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 107, 3, 107, 8);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (7.54, 24, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 108, 6, 108, 4);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (11.5, 24, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 109, 2, 109, 7);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (8.83, 24, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 110, 2, 110, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (6.68, 24, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 111, 1, 111, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (8.8, 24, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 112, 6, 112, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (8.9, 24, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 113, 2, 113, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (3.7, 24, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 0.0, "エリコ", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129901, 114, 2, 114, 5);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (7.6, 24, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 200, 1, 200, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (2.25, 24, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 201, 2, 201, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (3.28, 24, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 202, 2, 202, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (2.98, 24, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 203, 4, 203, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (3.0, 24, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 204, 4, 204, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10.3, 24, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 205, 1, 205, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (2.93, 24, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 206, 6, 206, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (9.3, 24, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 207, 3, 207, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (6.9, 24, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 208, 4, 208, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (0.0, 24, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 209, 2, 209, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (5.95, 24, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 210, 1, 210, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (6.73, 24, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 211, 2, 211, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (0.0, 24, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 212, 2, 212, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (6.65, 24, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 213, 1, 213, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (3.05, 24, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 0.0, "マコト", "2030/12/17 14:59:59", "2024/03/31 12:00:00", 10142, 129801, 214, 6, 214, 1);
