INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, "エリコ", 7, 24, 0.0, 100, 10.85, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 100);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, "エリコ", 2, 24, 0.0, 101, 11.15, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 101);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, "エリコ", 5, 24, 0.0, 102, 6.4, 7, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 102);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, "エリコ", 3, 24, 0.0, 103, 0.0, 3, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 103);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, "エリコ", 8, 24, 0.0, 104, 2.43, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 104);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, "エリコ", 3, 24, 0.0, 105, 9.08, 4, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 105);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, "エリコ", 6, 24, 0.0, 106, 0.0, 6, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 106);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, "エリコ", 8, 24, 0.0, 107, 10.6, 3, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 107);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, "エリコ", 4, 24, 0.0, 108, 7.54, 6, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 108);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, "エリコ", 7, 24, 0.0, 109, 11.5, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 109);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, "エリコ", 1, 24, 0.0, 110, 8.83, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 110);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, "エリコ", 2, 24, 0.0, 111, 6.68, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 111);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, "エリコ", 2, 24, 0.0, 112, 8.8, 6, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 112);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, "エリコ", 1, 24, 0.0, 113, 8.9, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 113);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, "エリコ", 5, 24, 0.0, 114, 3.7, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 114);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, "マコト", 2, 24, 0.0, 200, 7.6, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 200);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, "マコト", 1, 24, 0.0, 201, 2.25, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 201);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, "マコト", 1, 24, 0.0, 202, 3.28, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 202);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, "マコト", 1, 24, 0.0, 203, 2.98, 4, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 203);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, "マコト", 1, 24, 0.0, 204, 3.0, 4, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 204);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, "マコト", 2, 24, 0.0, 205, 10.3, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 205);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, "マコト", 1, 24, 0.0, 206, 2.93, 6, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 206);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, "マコト", 1, 24, 0.0, 207, 9.3, 3, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 207);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, "マコト", 1, 24, 0.0, 208, 6.9, 4, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 208);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, "マコト", 2, 24, 0.0, 209, 0.0, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 209);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, "マコト", 2, 24, 0.0, 210, 5.95, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 210);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, "マコト", 1, 24, 0.0, 211, 6.73, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 211);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, "マコト", 2, 24, 0.0, 212, 0.0, 2, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 212);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, "マコト", 2, 24, 0.0, 213, 6.65, 1, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 213);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, "マコト", 1, 24, 0.0, 214, 3.05, 6, "2024/03/31 12:00:00", 0.0, "2030/12/17 14:59:59", 214);
