INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (100, "2030/12/17 14:59:59", 0.0, 100, 1, "2024/03/31 12:00:00", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10.85, 0.0, 10142, "エリコ", 24, 129901, 7);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (101, "2030/12/17 14:59:59", 0.0, 101, 1, "2024/03/31 12:00:00", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 11.15, 0.0, 10142, "エリコ", 24, 129901, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (102, "2030/12/17 14:59:59", 0.0, 102, 7, "2024/03/31 12:00:00", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 6.4, 0.0, 10142, "エリコ", 24, 129901, 5);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (103, "2030/12/17 14:59:59", 0.0, 103, 3, "2024/03/31 12:00:00", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 0.0, 10142, "エリコ", 24, 129901, 3);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (104, "2030/12/17 14:59:59", 0.0, 104, 2, "2024/03/31 12:00:00", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2.43, 0.0, 10142, "エリコ", 24, 129901, 8);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (105, "2030/12/17 14:59:59", 0.0, 105, 4, "2024/03/31 12:00:00", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 9.08, 0.0, 10142, "エリコ", 24, 129901, 3);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (106, "2030/12/17 14:59:59", 0.0, 106, 6, "2024/03/31 12:00:00", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 0.0, 10142, "エリコ", 24, 129901, 6);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (107, "2030/12/17 14:59:59", 0.0, 107, 3, "2024/03/31 12:00:00", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10.6, 0.0, 10142, "エリコ", 24, 129901, 8);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (108, "2030/12/17 14:59:59", 0.0, 108, 6, "2024/03/31 12:00:00", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 7.54, 0.0, 10142, "エリコ", 24, 129901, 4);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (109, "2030/12/17 14:59:59", 0.0, 109, 2, "2024/03/31 12:00:00", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 11.5, 0.0, 10142, "エリコ", 24, 129901, 7);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (110, "2030/12/17 14:59:59", 0.0, 110, 2, "2024/03/31 12:00:00", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 8.83, 0.0, 10142, "エリコ", 24, 129901, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (111, "2030/12/17 14:59:59", 0.0, 111, 1, "2024/03/31 12:00:00", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 6.68, 0.0, 10142, "エリコ", 24, 129901, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (112, "2030/12/17 14:59:59", 0.0, 112, 6, "2024/03/31 12:00:00", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 8.8, 0.0, 10142, "エリコ", 24, 129901, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (113, "2030/12/17 14:59:59", 0.0, 113, 2, "2024/03/31 12:00:00", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 8.9, 0.0, 10142, "エリコ", 24, 129901, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (114, "2030/12/17 14:59:59", 0.0, 114, 2, "2024/03/31 12:00:00", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 3.7, 0.0, 10142, "エリコ", 24, 129901, 5);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (200, "2030/12/17 14:59:59", 0.0, 200, 1, "2024/03/31 12:00:00", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 7.6, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (201, "2030/12/17 14:59:59", 0.0, 201, 2, "2024/03/31 12:00:00", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2.25, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (202, "2030/12/17 14:59:59", 0.0, 202, 2, "2024/03/31 12:00:00", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 3.28, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (203, "2030/12/17 14:59:59", 0.0, 203, 4, "2024/03/31 12:00:00", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 2.98, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (204, "2030/12/17 14:59:59", 0.0, 204, 4, "2024/03/31 12:00:00", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 3.0, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (205, "2030/12/17 14:59:59", 0.0, 205, 1, "2024/03/31 12:00:00", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10.3, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (206, "2030/12/17 14:59:59", 0.0, 206, 6, "2024/03/31 12:00:00", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 2.93, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (207, "2030/12/17 14:59:59", 0.0, 207, 3, "2024/03/31 12:00:00", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 9.3, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (208, "2030/12/17 14:59:59", 0.0, 208, 4, "2024/03/31 12:00:00", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 6.9, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (209, "2030/12/17 14:59:59", 0.0, 209, 2, "2024/03/31 12:00:00", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (210, "2030/12/17 14:59:59", 0.0, 210, 1, "2024/03/31 12:00:00", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 5.95, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (211, "2030/12/17 14:59:59", 0.0, 211, 2, "2024/03/31 12:00:00", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 6.73, 0.0, 10142, "マコト", 24, 129801, 1);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (212, "2030/12/17 14:59:59", 0.0, 212, 2, "2024/03/31 12:00:00", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (213, "2030/12/17 14:59:59", 0.0, 213, 1, "2024/03/31 12:00:00", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 6.65, 0.0, 10142, "マコト", 24, 129801, 2);
INSERT INTO `v1_3c2756f74c1c2a28093845ee07cb10735b8100585a7f2426d6d52b1eec1fa628` VALUES (214, "2030/12/17 14:59:59", 0.0, 214, 6, "2024/03/31 12:00:00", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 3.05, 0.0, 10142, "マコト", 24, 129801, 1);
