INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (101, 1, 1, 1, 0, 110, 1, 122601, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 105, 0.029, 160, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (102, 0, 3, 2, 1, 105, 2, 122401, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 111, 0.014, 120, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (103, 0, 2, 3, 0, 110, 3, 122501, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 105, 0.017, 350, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (104, 0, 3, 4, 1, 110, 4, 107501, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 105, 0.016, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (105, 1, 1, 5, 0, 110, 5, 107601, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 105, 0.014, 160, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (106, 0, 2, 6, 0, 120, 6, 107801, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 103, 0.029, 255, 1, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (107, 1, 1, 7, 0, 120, 7, 110601, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 103, 0.013, 160, 0, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (108, 0, 2, 8, 0, 110, 8, 110401, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 103, 0.014, 350, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (109, 0, 3, 9, 1, 105, 9, 110501, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 100, 0.012, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (110, 1, 1, 10, 0, 105, 10, 115701, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 103, 0.014, 200, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (111, 0, 2, 11, 0, 105, 11, 113601, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 103, 0.071, 350, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (112, 0, 3, 12, 1, 120, 12, 107901, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 103, 0.014, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (113, 1, 2, 13, 0, 110, 13, 108001, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 105, 0.022, 200, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (114, 1, 1, 14, 0, 110, 14, 110301, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 108, 0.031, 160, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (115, 0, 1, 15, 1, 110, 15, 107701, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 105, 0.036, 130, 0, 0.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (116, 0, 2, 16, 0, 110, 16, 113101, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 95, 0.048, 350, 1, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (117, 0, 3, 17, 1, 105, 17, 113301, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105, 0.043, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (118, 0, 2, 18, 1, 105, 18, 113201, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 95, 0.018, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (119, 1, 2, 19, 0, 110, 19, 117001, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 108, 0.021, 160, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (120, 0, 1, 20, 0, 110, 20, 113501, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 105, 0.036, 255, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (121, 0, 3, 21, 0, 105, 21, 113401, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105, 0.02, 350, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (122, 0, 1, 22, 1, 110, 22, 110101, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 105, 0.018, 130, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (123, 1, 3, 23, 0, 105, 23, 110001, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 111, 0.03, 160, 0, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (124, 0, 1, 24, 1, 110, 24, 117201, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 103, 0.027, 130, 0, 1.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (125, 1, 2, 25, 0, 105, 25, 117301, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 108, 0.02, 160, 0, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (126, 0, 3, 26, 0, 110, 26, 117401, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 105, 0.04, 350, 1, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (127, 0, 1, 27, 0, 110, 27, 117101, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 105, 0.009, 350, 1, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (128, 0, 1, 28, 0, 120, 28, 122901, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 105, 0.022, 255, 1, 3.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (129, 0, 3, 29, 1, 110, 29, 122801, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 105, 0.028, 130, 0, 2.5);
INSERT INTO `v1_3fa07c4edb49c9f0b7633927ff8d30b331e82fd17f37bc7da849b07076873ec1` VALUES (130, 1, 2, 30, 0, 105, 30, 122701, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 108, 0.023, 130, 0, 3.5);
