INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 401, 24001, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 102301, 10046, 0.0, 2, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 402, 24002, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 2.21, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 102301, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 403, 24003, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 102301, 10046, 0.0, 2, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 701, 24004, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 1.99, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 192201, 10046, 0.0, 1, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 702, 24005, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 2.21, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 192201, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 703, 24006, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 3.33, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 192201, 10046, 0.0, 1, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 801, 24007, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 192301, 10046, 0.0, 2, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 802, 24008, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 2.68, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 192301, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 803, 24009, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 192301, 10046, 0.0, 2, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 501, 24010, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 0.0, "やった！", 192501, 10046, 0.0, 1, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 502, 24011, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 0.0, "勝ったよ！", 192501, 10046, 0.0, 1, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 503, 24012, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 0.0, "いい調子！", 192501, 10046, 0.0, 1, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 111, 24013, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 5.33, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 113501, 10046, 0.0, 2, 6, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 112, 24014, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 6.85, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 113501, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 113, 24015, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 3.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 113501, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 611, 24016, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 6.11, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 192401, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 612, 24017, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 6.75, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 192401, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 613, 24018, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 192401, 10046, 0.0, 2, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 211, 24019, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 4.52, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 113401, 10046, 0.0, 1, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 212, 24020, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 3.89, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 113401, 10046, 0.0, 1, 2, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 213, 24021, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 13.49, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 113401, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 311, 24022, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 3.17, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 106501, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 312, 24023, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 4.42, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 106501, 10046, 0.0, 2, 1, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 313, 24024, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 2.89, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 106501, 10046, 0.0, 1, 6, 0.0);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (24, 421, 24025, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 3.71, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 102301, 10046, 0.0, 3, 4, 0.0);
