INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24, 0.0, 102301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24001, "アヤネ", 401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24, 2.21, 102301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24002, "アヤネ", 402);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24, 0.0, 102301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24003, "アヤネ", 403);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24, 1.99, 192201, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24004, "アサヒ", 701);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24, 2.21, 192201, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24005, "アサヒ", 702);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24, 3.33, 192201, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24006, "アサヒ", 703);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24, 0.0, 192301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24007, "部員３", 801);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24, 2.68, 192301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24008, "部員３", 802);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24, 0.0, 192301, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24009, "部員３", 803);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "やった！", 24, 0.0, 192501, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24010, "", 501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "勝ったよ！", 24, 0.0, 192501, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24011, "", 502);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "いい調子！", 24, 0.0, 192501, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24012, "", 503);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (6, 10046, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24, 5.33, 113501, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24013, "ミサト", 111);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24, 6.85, 113501, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24014, "ミサト", 112);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24, 3.0, 113501, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24015, "ミサト", 113);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24, 6.11, 192401, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24016, "ダイチ", 611);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24, 6.75, 192401, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24017, "ダイチ", 612);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24, 0.0, 192401, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24018, "ダイチ", 613);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24, 4.52, 113401, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24019, "ハツネ", 211);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24, 3.89, 113401, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24020, "ハツネ", 212);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24, 13.49, 113401, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24021, "ハツネ", 213);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24, 3.17, 106501, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24022, "カヤ", 311);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24, 4.42, 106501, 0.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24023, "カヤ", 312);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (6, 10046, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24, 2.89, 106501, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24024, "カヤ", 313);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (4, 10046, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24, 3.71, 102301, 0.0, 0.0, "2020/07/31 12:00:00", 3, "2030/12/17 14:59:59", 24025, "アヤネ", 421);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24, 5.1, 192201, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24026, "アサヒ", 721);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 24, 4.34, 192301, 0.0, 0.0, "2020/07/31 12:00:00", 3, "2030/12/17 14:59:59", 24027, "部員３", 821);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "いい勝負だったよ\nまたやろうね", 24, 0.0, 192501, 0.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24028, "", 521);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 24, 5.62, 113501, -360.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24029, "ミサト", 101);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 24, 9.58, 113501, -360.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24030, "ミサト", 102);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 24, 0.0, 113501, -360.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24031, "ミサト", 103);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (2, 10046, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 24, 9.13, 113501, -360.0, 0.0, "2020/07/31 12:00:00", 1, "2030/12/17 14:59:59", 24032, "ミサト", 104);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (1, 10046, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 24, 8.27, 113501, -360.0, 0.0, "2020/07/31 12:00:00", 2, "2030/12/17 14:59:59", 24033, "ミサト", 105);
