INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (401, 2, 2, 24001, 24, 0.0, 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 102301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (402, 1, 2, 24002, 24, 2.21, 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 102301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (403, 2, 2, 24003, 24, 0.0, 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 102301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (701, 2, 1, 24004, 24, 1.99, 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 192201);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (702, 1, 2, 24005, 24, 2.21, 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 192201);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (703, 2, 1, 24006, 24, 3.33, 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 192201);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (801, 2, 2, 24007, 24, 0.0, 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 192301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (802, 1, 2, 24008, 24, 2.68, 0.0, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 192301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (803, 2, 2, 24009, 24, 0.0, 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 192301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (501, 1, 1, 24010, 24, 0.0, 0.0, "やった！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 192501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (502, 1, 1, 24011, 24, 0.0, 0.0, "勝ったよ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 192501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (503, 1, 1, 24012, 24, 0.0, 0.0, "いい調子！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 192501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (111, 6, 2, 24013, 24, 5.33, 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (112, 1, 2, 24014, 24, 6.85, 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (113, 1, 2, 24015, 24, 3.0, 0.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (611, 1, 2, 24016, 24, 6.11, 0.0, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 192401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (612, 1, 2, 24017, 24, 6.75, 0.0, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 192401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (613, 2, 2, 24018, 24, 0.0, 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 192401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (211, 2, 1, 24019, 24, 4.52, 0.0, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 113401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (212, 2, 1, 24020, 24, 3.89, 0.0, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 113401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (213, 1, 2, 24021, 24, 13.49, 0.0, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ハツネ", 113401);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (311, 1, 2, 24022, 24, 3.17, 0.0, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 106501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (312, 1, 2, 24023, 24, 4.42, 0.0, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 106501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (313, 6, 1, 24024, 24, 2.89, 0.0, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "カヤ", 106501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (421, 4, 3, 24025, 24, 3.71, 0.0, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 102301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (721, 2, 1, 24026, 24, 5.1, 0.0, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 192201);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (821, 1, 3, 24027, 24, 4.34, 0.0, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 192301);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (521, 1, 1, 24028, 24, 0.0, 0.0, "いい勝負だったよ\nまたやろうね", 0.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "", 192501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (101, 1, 2, 24029, 24, 5.62, 0.0, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", -360.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (102, 1, 2, 24030, 24, 9.58, 0.0, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", -360.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (103, 2, 2, 24031, 24, 0.0, 0.0, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", -360.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (104, 2, 1, 24032, 24, 9.13, 0.0, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", -360.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
INSERT INTO `v1_41e0966e655ea5b31ee03936fafa86625da445be4d0982b6530e77edf7b7c5ef` VALUES (105, 1, 2, 24033, 24, 8.27, 0.0, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", -360.0, 10046, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ミサト", 113501);
