INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (101, 1, 0, 160, 122601, 1.5, 105, 0.029, 1, 1, 1, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (102, 0, 1, 120, 122401, 2.5, 111, 0.014, 2, 2, 3, 0, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (103, 0, 0, 350, 122501, 2.5, 105, 0.017, 3, 3, 2, 1, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (104, 0, 1, 130, 107501, 1.5, 105, 0.016, 4, 4, 3, 0, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (105, 1, 0, 160, 107601, 2.5, 105, 0.014, 5, 5, 1, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (106, 0, 0, 255, 107801, 3.5, 103, 0.029, 6, 6, 2, 1, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 120);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (107, 1, 0, 160, 110601, 3.5, 103, 0.013, 7, 7, 1, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 120);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (108, 0, 0, 350, 110401, 2.5, 103, 0.014, 8, 8, 2, 1, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (109, 0, 1, 130, 110501, 1.5, 100, 0.012, 9, 9, 3, 0, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (110, 1, 0, 200, 115701, 2.5, 103, 0.014, 10, 10, 1, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (111, 0, 0, 350, 113601, 2.5, 103, 0.071, 11, 11, 2, 1, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (112, 0, 1, 130, 107901, 1.5, 103, 0.014, 12, 12, 3, 0, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 120);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (113, 1, 0, 200, 108001, 1.5, 105, 0.022, 13, 13, 2, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (114, 1, 0, 160, 110301, 2.5, 108, 0.031, 14, 14, 1, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (115, 0, 1, 130, 107701, 0.5, 105, 0.036, 15, 15, 1, 0, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (116, 0, 0, 350, 113101, 3.5, 95, 0.048, 16, 16, 2, 1, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (117, 0, 1, 130, 113301, 1.5, 105, 0.043, 17, 17, 3, 0, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (118, 0, 1, 130, 113201, 1.5, 95, 0.018, 18, 18, 2, 0, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (119, 1, 0, 160, 117001, 2.5, 108, 0.021, 19, 19, 2, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (120, 0, 0, 255, 113501, 2.5, 105, 0.036, 20, 20, 1, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (121, 0, 0, 350, 113401, 2.5, 105, 0.02, 21, 21, 3, 1, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (122, 0, 1, 130, 110101, 2.5, 105, 0.018, 22, 22, 1, 0, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (123, 1, 0, 160, 110001, 3.5, 111, 0.03, 23, 23, 3, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (124, 0, 1, 130, 117201, 1.5, 103, 0.027, 24, 24, 1, 0, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (125, 1, 0, 160, 117301, 3.5, 108, 0.02, 25, 25, 2, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 105);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (126, 0, 0, 350, 117401, 2.5, 105, 0.04, 26, 26, 3, 1, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (127, 0, 0, 350, 117101, 3.5, 105, 0.009, 27, 27, 1, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (128, 0, 0, 255, 122901, 3.5, 105, 0.022, 28, 28, 1, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 120);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (129, 0, 1, 130, 122801, 2.5, 105, 0.028, 29, 29, 3, 0, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 110);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (130, 1, 0, 130, 122701, 3.5, 108, 0.023, 30, 30, 2, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 105);
