INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (1, 122601, 105, 101, 0, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 110, 1, 0.029, 1.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (2, 122401, 111, 102, 0, 3, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 0, 105, 2, 0.014, 2.5, 120, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (3, 122501, 105, 103, 1, 2, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 0, 110, 3, 0.017, 2.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (4, 107501, 105, 104, 0, 3, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 0, 110, 4, 0.016, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (5, 107601, 105, 105, 0, 1, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 110, 5, 0.014, 2.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (6, 107801, 103, 106, 1, 2, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 0, 120, 6, 0.029, 3.5, 255, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (7, 110601, 103, 107, 0, 1, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 120, 7, 0.013, 3.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (8, 110401, 103, 108, 1, 2, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 0, 110, 8, 0.014, 2.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (9, 110501, 100, 109, 0, 3, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 0, 105, 9, 0.012, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (10, 115701, 103, 110, 0, 1, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 105, 10, 0.014, 2.5, 200, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (11, 113601, 103, 111, 1, 2, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 0, 105, 11, 0.071, 2.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (12, 107901, 103, 112, 0, 3, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 0, 120, 12, 0.014, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (13, 108001, 105, 113, 0, 2, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 1, 110, 13, 0.022, 1.5, 200, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (14, 110301, 108, 114, 0, 1, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 110, 14, 0.031, 2.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (15, 107701, 105, 115, 0, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 0, 110, 15, 0.036, 0.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (16, 113101, 95, 116, 1, 2, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 0, 110, 16, 0.048, 3.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (17, 113301, 105, 117, 0, 3, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 0, 105, 17, 0.043, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (18, 113201, 95, 118, 0, 2, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 0, 105, 18, 0.018, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (19, 117001, 108, 119, 0, 2, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 1, 110, 19, 0.021, 2.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (20, 113501, 105, 120, 1, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 0, 110, 20, 0.036, 2.5, 255, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (21, 113401, 105, 121, 1, 3, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 0, 105, 21, 0.02, 2.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (22, 110101, 105, 122, 0, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 0, 110, 22, 0.018, 2.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (23, 110001, 111, 123, 0, 3, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 1, 105, 23, 0.03, 3.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (24, 117201, 103, 124, 0, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 0, 110, 24, 0.027, 1.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (25, 117301, 108, 125, 0, 2, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 1, 105, 25, 0.02, 3.5, 160, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (26, 117401, 105, 126, 1, 3, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 0, 110, 26, 0.04, 2.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (27, 117101, 105, 127, 1, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 0, 110, 27, 0.009, 3.5, 350, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (28, 122901, 105, 128, 1, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 0, 120, 28, 0.022, 3.5, 255, 0);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (29, 122801, 105, 129, 0, 3, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 0, 110, 29, 0.028, 2.5, 130, 1);
INSERT INTO `v1_4208afa062f1912cad2df581f16a32085775f1cf5dc089060df51e9b91fcc57b` VALUES (30, 122701, 108, 130, 0, 2, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 1, 105, 30, 0.023, 3.5, 130, 0);
