INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1, 100101, 0, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2, 100101, 0, 1, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3, 100101, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4, 100101, 0, 1, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5, 100101, 0, 1, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6, 100101, 0, 4, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7, 100101, 0, 8, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8, 100101, 0, 1, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 9, 100101, 0, 1, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 10, 100101, 0, 1, "えっとね…しっぽは\n触っちゃダメだよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 11, 100101, 0, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 12, 100101, 0, 4, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 13, 100101, 0, 8, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 14, 100101, 0, 1, "おはよー！\n今日も元気に\nいっちゃうよー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 15, 100101, 0, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 16, 100101, 0, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 17, 100101, 0, 1, "ちゃんとケアして\n傷が残らないように\nしないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 18, 100101, 0, 1, "一回休むと\nこのままずるずる\nいきそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 19, 100101, 0, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 20, 100101, 0, 1, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 21, 100101, 0, 1, "あったかい所に\nいると体が丸く\nなっちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 22, 100101, 0, 1, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 23, 100101, 0, 1, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 24, 100101, 0, 1, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 26, 100102, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 27, 100102, 0, 1, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 28, 100102, 0, 1, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 29, 100102, 0, 1, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 30, 100102, 0, 1, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 31, 100102, 0, 8, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 32, 100102, 0, 8, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 33, 100102, 0, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 34, 100102, 0, 1, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 35, 100102, 0, 1, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 36, 100102, 0, 1, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 37, 100102, 0, 8, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 38, 100102, 0, 8, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 39, 100102, 0, 1, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 1);
