INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 42, 100102, 0, 1, "これでよし♪\n騎士くん\n気づくかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 43, 100102, 0, 1, "一回休むと\nこのままずるずる\nいきそう…", 0);
