INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 49, 100102, 0, 1, "色んなことを\n知っておくのは\n大事だよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 51, 100201, 0, 1, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 52, 100201, 0, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 53, 100201, 0, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 54, 100201, 0, 1, "戦いは\n得意じゃないけど\nお料理なら…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 55, 100201, 0, 1, "こういう平和な\n時間が、もっと\n続くといいな", 0);
