INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 58, 100201, 0, 1, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 59, 100201, 0, 1, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 60, 100201, 0, 1, "困ったことが\nあったら\nいつでも話してね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 61, 100201, 0, 1, "え！　む、虫っ！？\n取って取って！\nはやく～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 62, 100201, 0, 4, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 63, 100201, 0, 8, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 64, 100201, 0, 1, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 65, 100201, 0, 1, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 66, 100201, 0, 1, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 67, 100201, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 68, 100201, 0, 1, "がんばることも\n必要だけど、\nお休みも大切だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 69, 100201, 0, 1, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 70, 100201, 0, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 71, 100201, 0, 1, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 72, 100201, 0, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 73, 100201, 0, 1, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 74, 100201, 0, 1, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 0);
