INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 78, 100202, 0, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 79, 100202, 0, 1, "今日はどんな料理に\n挑戦しよう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 80, 100202, 0, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 0);
