INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 85, 100202, 0, 1, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 86, 100202, 0, 1, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 87, 100202, 0, 8, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 88, 100202, 0, 8, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 89, 100202, 0, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 90, 100202, 0, 1, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 91, 100202, 0, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 92, 100202, 0, 1, "よし…\nこれなら\n褒めてもらえるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 93, 100202, 0, 1, "がんばることも\n必要だけど\nお休みも大切だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 94, 100202, 0, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 95, 100202, 0, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 96, 100202, 0, 1, "あの…！\nよ、良かったら\n一緒に…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 97, 100202, 0, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 98, 100202, 0, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 99, 100202, 0, 1, "…やっぱり\n月刊恋愛指南は\nためになる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 101, 100301, 0, 1, "装備の手入れでも\nしようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 102, 100301, 0, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 103, 100301, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 104, 100301, 0, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 105, 100301, 0, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 106, 100301, 0, 4, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 107, 100301, 0, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 108, 100301, 0, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 109, 100301, 0, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 110, 100301, 0, 1, "なに？　私に相談？\n戦いについてなら\n喜んで", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 111, 100301, 0, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 112, 100301, 0, 4, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 113, 100301, 0, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 114, 100301, 0, 1, "おはよう\n今日も充実した\n一日にしていこう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 115, 100301, 0, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 116, 100301, 0, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 117, 100301, 0, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 118, 100301, 0, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 119, 100301, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 120, 100301, 0, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 121, 100301, 0, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 122, 100301, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 123, 100301, 0, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 124, 100301, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 126, 100302, 0, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 127, 100302, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 128, 100302, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 129, 100302, 0, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 130, 100302, 0, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 131, 100302, 0, 8, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 132, 100302, 0, 8, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 133, 100302, 0, 1, "どうしたの？\nなんでもない？\nそれなら良かった", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 134, 100302, 0, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 135, 100302, 0, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 136, 100302, 0, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 137, 100302, 0, 8, "ひょっとして\n今のも一本だなんて\n言わないよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 138, 100302, 0, 8, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 139, 100302, 0, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 140, 100302, 0, 1, "休息も時には\n必要だと、仲間から\n教わったよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 141, 100302, 0, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 142, 100302, 0, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 143, 100302, 0, 1, "休息があってこそ\n万全な状態で\n戦えるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 144, 100302, 0, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 145, 100302, 0, 1, "体と同様に\n心もほぐれるね…\nはぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 146, 100302, 0, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 147, 100302, 0, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 148, 100302, 0, 1, "修行のせいか\n最近食べる量が\n増えてきたような…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 149, 100302, 0, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 151, 100401, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 152, 100401, 0, 1, "落とし穴…どこに\n作ったんだっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 153, 100401, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 154, 100401, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 155, 100401, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 156, 100401, 0, 4, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 157, 100401, 0, 8, "ミソギと遊ぶ？\nミソギに遊ばれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 158, 100401, 0, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 159, 100401, 0, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 160, 100401, 0, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 161, 100401, 0, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 162, 100401, 0, 4, "ここ、ミソギ探検隊の\n基地にしようよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 163, 100401, 0, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 164, 100401, 0, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 165, 100401, 0, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 166, 100401, 0, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 167, 100401, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 168, 100401, 0, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 169, 100401, 0, 1, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 170, 100401, 0, 1, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 171, 100401, 0, 1, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 0);
