INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 174, 100401, 0, 1, "こういう所に\n宝の地図が\n隠れてるんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 176, 100402, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 177, 100402, 0, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 178, 100402, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 179, 100402, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 180, 100402, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 181, 100402, 0, 8, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 182, 100402, 0, 8, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 183, 100402, 0, 1, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 184, 100402, 0, 1, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 185, 100402, 0, 1, "将来の夢は探検家！\n探したい物はないけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 186, 100402, 0, 1, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 187, 100402, 0, 8, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 188, 100402, 0, 8, "ここ、ミソギ探検隊の\n基地にしようよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 189, 100402, 0, 1, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 190, 100402, 0, 1, "にいちゃんが昼寝したら\nイタズラし放題♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 191, 100402, 0, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 192, 100402, 0, 1, "イタズラには\nお色気も必要…\nうん、完璧！", 0);
