INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 226, 100602, 0, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 227, 100602, 0, 1, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 0);
