INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 249, 100602, 0, 1, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 251, 100701, 0, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 0);
