INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 256, 100701, 0, 4, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 257, 100701, 0, 8, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 258, 100701, 0, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 259, 100701, 0, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 260, 100701, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 261, 100701, 0, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 262, 100701, 0, 4, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 263, 100701, 0, 8, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 264, 100701, 0, 1, "う～　眠いの…\n二度目の二度寝を\nするの…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 265, 100701, 0, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 266, 100701, 0, 1, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 267, 100701, 0, 1, "体が透けちゃうから\nうまく使えないの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 268, 100701, 0, 1, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 269, 100701, 0, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 270, 100701, 0, 1, "は～　いい気分なの…\n成仏しそうなの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 271, 100701, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 272, 100701, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 273, 100701, 0, 1, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 274, 100701, 0, 1, "プリンの写真集が\nあればいいのに\nなの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 276, 100702, 0, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 277, 100702, 0, 1, "プリン♪　プリン♪\nプリン♪　なの～♪　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 278, 100702, 0, 1, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 279, 100702, 0, 1, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 280, 100702, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 281, 100702, 0, 8, "アイツがいて\nプリンもある\nきっとここが天国なの", 0);
