INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 283, 100702, 0, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 0);
