INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 296, 100702, 0, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 297, 100702, 0, 1, "幽霊には、体力も\n筋力も関係ないの～！　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 298, 100702, 0, 1, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 299, 100702, 0, 1, "プリンの写真集が\nあればいいのに\nなの～ ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 301, 100801, 0, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 302, 100801, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 303, 100801, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 304, 100801, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 305, 100801, 0, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 306, 100801, 0, 4, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 307, 100801, 0, 8, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 308, 100801, 0, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 309, 100801, 0, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 310, 100801, 0, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 311, 100801, 0, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 312, 100801, 0, 4, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 313, 100801, 0, 8, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 314, 100801, 0, 1, "美しいボクの\n美しい一日が\n始まるよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 315, 100801, 0, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 316, 100801, 0, 1, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 317, 100801, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 318, 100801, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 319, 100801, 0, 1, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 320, 100801, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 321, 100801, 0, 1, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 322, 100801, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 323, 100801, 0, 1, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 324, 100801, 0, 1, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 326, 100802, 0, 1, "ボクの美しさは\nみんなのために\nあるんだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 327, 100802, 0, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 328, 100802, 0, 1, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 329, 100802, 0, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 330, 100802, 0, 1, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 331, 100802, 0, 8, "このボクがいるだけで\nあの人への\nおもてなしだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 332, 100802, 0, 8, "あの人も今\n可愛いボクのこと\n考えてるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 333, 100802, 0, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 334, 100802, 0, 1, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 335, 100802, 0, 1, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 336, 100802, 0, 1, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 337, 100802, 0, 8, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 338, 100802, 0, 8, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 339, 100802, 0, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 340, 100802, 0, 1, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 341, 100802, 0, 1, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 342, 100802, 0, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 343, 100802, 0, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 344, 100802, 0, 1, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 345, 100802, 0, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 346, 100802, 0, 1, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 347, 100802, 0, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 348, 100802, 0, 1, "ボクが食べると\n食べ物まで\n可愛いなあ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 349, 100802, 0, 1, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 351, 100901, 0, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 352, 100901, 0, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 353, 100901, 0, 1, "ここでは風が\n吹いてこないな…\n換気したい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 354, 100901, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 355, 100901, 0, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 356, 100901, 0, 4, "シグルド…\n今頃何処で何を\nしているのだろう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 357, 100901, 0, 8, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 358, 100901, 0, 1, "辞書を知らないか？\n…探している\n理由は聞くな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 359, 100901, 0, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 360, 100901, 0, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 361, 100901, 0, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 362, 100901, 0, 4, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 363, 100901, 0, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 364, 100901, 0, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 365, 100901, 0, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 366, 100901, 0, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 367, 100901, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 368, 100901, 0, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 369, 100901, 0, 1, "このようなものに\n懐柔されるものか！\n…されるものか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 370, 100901, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 371, 100901, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 372, 100901, 0, 1, "この程度\n前世での激闘に\n比べれば…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 373, 100901, 0, 1, "最近金欠でな…\nいやぁありがたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 374, 100901, 0, 1, "ここにも\n前世の記憶が\n眠っていたとは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 376, 100902, 0, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 377, 100902, 0, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 378, 100902, 0, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 379, 100902, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 380, 100902, 0, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 381, 100902, 0, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 382, 100902, 0, 8, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 383, 100902, 0, 8, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 384, 100902, 0, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 385, 100902, 0, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 386, 100902, 0, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 387, 100902, 0, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 388, 100902, 0, 8, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 389, 100902, 0, 8, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 390, 100902, 0, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 391, 100902, 0, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 392, 100902, 0, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 393, 100902, 0, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 394, 100902, 0, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 395, 100902, 0, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 396, 100902, 0, 1, "これが我らの求めた\n安寧か…\n悪くない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 397, 100902, 0, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 398, 100902, 0, 1, "食事は魔力に\n変換する体力の\n元になるのだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 399, 100902, 0, 1, "フフフ…\nこれを覚えれば\nもっと強く…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 401, 101001, 0, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 402, 101001, 0, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 403, 101001, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 404, 101001, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 405, 101001, 0, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 406, 101001, 0, 4, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 407, 101001, 0, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 408, 101001, 0, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 409, 101001, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 410, 101001, 0, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 411, 101001, 0, 1, "うちの話し方\nのんびりしてます\nやろか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 412, 101001, 0, 4, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 413, 101001, 0, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 414, 101001, 0, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 415, 101001, 0, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 416, 101001, 0, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 417, 101001, 0, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 418, 101001, 0, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 419, 101001, 0, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 420, 101001, 0, 1, "心地ええどす。まるで\nお花畑にいるようやね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 421, 101001, 0, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 422, 101001, 0, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 423, 101001, 0, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 424, 101001, 0, 1, "絵本、絵本～\n幸せやわぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 426, 101002, 0, 1, "王子はんにとって\n自慢の姫になるえ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 427, 101002, 0, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 428, 101002, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 429, 101002, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 430, 101002, 0, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 431, 101002, 0, 8, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 432, 101002, 0, 8, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 433, 101002, 0, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 434, 101002, 0, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 435, 101002, 0, 1, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 436, 101002, 0, 1, "絵本読むんも\nええなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 437, 101002, 0, 8, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 438, 101002, 0, 8, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 439, 101002, 0, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 440, 101002, 0, 1, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 441, 101002, 0, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 442, 101002, 0, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 443, 101002, 0, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 444, 101002, 0, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 445, 101002, 0, 1, "癒しアイテムって\nほんま、好っきゃわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 446, 101002, 0, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 447, 101002, 0, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 448, 101002, 0, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 449, 101002, 0, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 451, 101101, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 452, 101101, 0, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 453, 101101, 0, 1, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 454, 101101, 0, 1, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 455, 101101, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 456, 101101, 0, 4, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 457, 101101, 0, 8, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 458, 101101, 0, 1, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 459, 101101, 0, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 460, 101101, 0, 1, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 461, 101101, 0, 1, "服がほつれてますよ？\n直してあげます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 462, 101101, 0, 4, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 463, 101101, 0, 8, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 464, 101101, 0, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 465, 101101, 0, 1, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 466, 101101, 0, 1, "ねむいです～\nでももうちょっと\n勉強しないと～…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 467, 101101, 0, 1, "この服、かわいい♪\n今度作って\nみよっと～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 468, 101101, 0, 1, "勉強で疲れたから\n休憩しよ～っと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 469, 101101, 0, 1, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 470, 101101, 0, 1, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 471, 101101, 0, 1, "のんびりしたら、\nまた勉強しよっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 472, 101101, 0, 1, "運動すると勉強も\nはかどるよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 473, 101101, 0, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 474, 101101, 0, 1, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 476, 101102, 0, 1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 477, 101102, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 478, 101102, 0, 1, "い、いけない！\n縫い針落としちゃった", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 479, 101102, 0, 1, "笑う時には服着ます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 480, 101102, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 481, 101102, 0, 8, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 482, 101102, 0, 8, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 483, 101102, 0, 1, "勉強、見てくれるん\nですか？　やった～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 484, 101102, 0, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 485, 101102, 0, 1, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 486, 101102, 0, 1, "勉強は…\n明日にしようっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 487, 101102, 0, 8, "妹だけど、大人な\nレディですからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 488, 101102, 0, 8, "な、ナデナデとか\n恥ずかしいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 489, 101102, 0, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 490, 101102, 0, 1, "お昼休憩に\n甘いものが\n食べたいです～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 491, 101102, 0, 1, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 492, 101102, 0, 1, "服装や髪形が変わると\n気分も変わるよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 493, 101102, 0, 1, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 494, 101102, 0, 1, "かわいい♪\n私が服を作って\nあげよっか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 495, 101102, 0, 1, "いまならどんな試験も\n合格できそうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 496, 101102, 0, 1, "のんびりしたら\nまた勉強しよっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 497, 101102, 0, 1, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 498, 101102, 0, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 499, 101102, 0, 1, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 501, 101201, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 502, 101201, 0, 1, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 503, 101201, 0, 1, "…はっ！？\nね、寝てないよ？\nホントだよっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 504, 101201, 0, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 505, 101201, 0, 1, "もし、超能力のことが\nバレたら…ブルブル", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 506, 101201, 0, 4, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 507, 101201, 0, 8, "キミを抱き枕に…\nじょ、冗談冗談！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 508, 101201, 0, 1, "ん～、なになに？\n一緒にお昼寝する？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 509, 101201, 0, 1, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 0);
