INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 597, 101602, 0, 1, "食べた分は\n消費しないとね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 598, 101602, 0, 1, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 599, 101602, 0, 1, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 601, 101701, 0, 1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 0);
