INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 605, 101701, 0, 1, "さぁ、みんなで歌って\n踊るさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 606, 101701, 0, 4, "毎日、最高に幸せさ～\nキミのおかげだねー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 607, 101701, 0, 8, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 608, 101701, 0, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 609, 101701, 0, 1, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 610, 101701, 0, 1, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 611, 101701, 0, 1, "空手の型の\n練習するさー\nキミもどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 612, 101701, 0, 4, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 613, 101701, 0, 8, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 614, 101701, 0, 1, "ハイターイ！\n今日も一日、\nがんばるさー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 615, 101701, 0, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 616, 101701, 0, 1, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 617, 101701, 0, 1, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 618, 101701, 0, 1, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 0);
