INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 657, 101801, 0, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 658, 101801, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 659, 101801, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 660, 101801, 0, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 661, 101801, 0, 1, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 662, 101801, 0, 4, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 663, 101801, 0, 8, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 664, 101801, 0, 1, "学校に\n遅刻しないように\n早く準備しないとね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 665, 101801, 0, 1, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 666, 101801, 0, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 667, 101801, 0, 1, "もしかして…\n少し露出多いかしら？", 0);
