INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 669, 101801, 0, 1, "かわいい♪　あなたも\n私の生徒になる？", 0);
