INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 677, 101802, 0, 1, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 678, 101802, 0, 1, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 679, 101802, 0, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 680, 101802, 0, 1, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 681, 101802, 0, 8, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 682, 101802, 0, 8, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 683, 101802, 0, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 684, 101802, 0, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 685, 101802, 0, 1, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 686, 101802, 0, 1, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 687, 101802, 0, 8, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 688, 101802, 0, 8, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 689, 101802, 0, 1, "朝の特別授業\nしてあげましょうか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 690, 101802, 0, 1, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 691, 101802, 0, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 692, 101802, 0, 1, "もしかして…\n少し露出多いかしら？", 0);
