INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 845, 102202, 0, 1, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 846, 102202, 0, 1, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 847, 102202, 0, 1, "最近ゲームばかりだし\nたまにはいいかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 848, 102202, 0, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 849, 102202, 0, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 851, 102301, 0, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 852, 102301, 0, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 853, 102301, 0, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 854, 102301, 0, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 855, 102301, 0, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 856, 102301, 0, 4, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 857, 102301, 0, 8, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 858, 102301, 0, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 859, 102301, 0, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 860, 102301, 0, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 861, 102301, 0, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 862, 102301, 0, 4, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 863, 102301, 0, 8, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 864, 102301, 0, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 865, 102301, 0, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 866, 102301, 0, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 868, 102301, 0, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 869, 102301, 0, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 870, 102301, 0, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 871, 102301, 0, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 873, 102301, 0, 1, "おいしい～\nぷうきちも\n食べてみる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 874, 102301, 0, 1, "ぷうきち！\nこの本、読んで～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 901, 102501, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 902, 102501, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 903, 102501, 0, 1, "どうしたら転ばなく\nなるんでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 904, 102501, 0, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 905, 102501, 0, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 906, 102501, 0, 4, "おいしい料理で\nお出迎えしなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 907, 102501, 0, 8, "一生懸命\nご奉仕しますよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 908, 102501, 0, 1, "耳かきしましょうか？\nえ？　こわい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 909, 102501, 0, 1, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 910, 102501, 0, 1, "おかえりなさいませ\nご主人さま♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 911, 102501, 0, 1, "すみません\nすみません\nすみません～～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 912, 102501, 0, 4, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 913, 102501, 0, 8, "私のこと、\n専属メイドと思って\n頼ってくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 914, 102501, 0, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 915, 102501, 0, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 916, 102501, 0, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 917, 102501, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 918, 102501, 0, 1, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 919, 102501, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 920, 102501, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 921, 102501, 0, 1, "ここでお洗濯ものを\n乾かしましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 922, 102501, 0, 1, "あわわわっ…\nこ、転びそうです～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 923, 102501, 0, 1, "これくらい\nおいしいものを\n作ってみたいです～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 924, 102501, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 926, 102502, 0, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 927, 102502, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 928, 102502, 0, 1, "どうやったら、立派な\nメイドになれるのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 929, 102502, 0, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 930, 102502, 0, 1, "ご用があれば\nなんでも言って\nくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 931, 102502, 0, 8, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 932, 102502, 0, 8, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 933, 102502, 0, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 934, 102502, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 935, 102502, 0, 1, "私、またなにか\nやっちゃいましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 936, 102502, 0, 1, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 937, 102502, 0, 8, "転んだふりであの人に\n…って！\nなんでもないですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 938, 102502, 0, 8, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 939, 102502, 0, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 940, 102502, 0, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 941, 102502, 0, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 942, 102502, 0, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 943, 102502, 0, 1, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 944, 102502, 0, 1, "かわいい♪\nあとでお洗濯\nしてあげますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 945, 102502, 0, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 946, 102502, 0, 1, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 947, 102502, 0, 1, "運動しようとすると\nいつも怪我するん\nですよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 948, 102502, 0, 1, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 949, 102502, 0, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 951, 102701, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 952, 102701, 0, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 953, 102701, 0, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 954, 102701, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 955, 102701, 0, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 956, 102701, 0, 4, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0);
