INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1039, 102802, 0, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1040, 102802, 0, 1, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1041, 102802, 0, 1, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1042, 102802, 0, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1043, 102802, 0, 1, "これで仕事は一段落ね\nちょっと休憩…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1044, 102802, 0, 1, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1045, 102802, 0, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1046, 102802, 0, 1, "風邪を引かないように\n身体を温めなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1047, 102802, 0, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1048, 102802, 0, 1, "しっかり食べて\n栄養補給、栄養補給！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1049, 102802, 0, 1, "知識から過去を学んで\n未来に活かすわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1051, 102901, 0, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1052, 102901, 0, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1053, 102901, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1054, 102901, 0, 1, "彼とデュエット\nしてみたいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1055, 102901, 0, 1, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1056, 102901, 0, 4, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1057, 102901, 0, 8, "写真集見なくても、\n本物がここに\nいるのにな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1058, 102901, 0, 1, "握手会の練習、\n付き合ってくれない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1059, 102901, 0, 1, "写真集のポーズ\n練習中～！\nど、どうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1060, 102901, 0, 1, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1061, 102901, 0, 1, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1062, 102901, 0, 4, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1063, 102901, 0, 8, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1064, 102901, 0, 1, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1);
