INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1101, 103001, 0, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 0);
