INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1103, 103001, 0, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1104, 103001, 0, 1, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1105, 103001, 0, 1, "クニトリ合戦、\n楽しみデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1106, 103001, 0, 4, "ショーグンと\n出会えて、\nワタシは果報者デス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1107, 103001, 0, 8, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1108, 103001, 0, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1109, 103001, 0, 1, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1110, 103001, 0, 1, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1111, 103001, 0, 1, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1112, 103001, 0, 4, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1113, 103001, 0, 8, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1114, 103001, 0, 1, "おはようデス！\n今日も一日\n忍法日和デス！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1115, 103001, 0, 1, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1116, 103001, 0, 1, "明日もきっと\nいい日デスよ、\nショーグン！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1117, 103001, 0, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1118, 103001, 0, 1, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1119, 103001, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1120, 103001, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1121, 103001, 0, 1, "身体を暖めて\nイクサに向かうデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1122, 103001, 0, 1, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1123, 103001, 0, 1, "うーん、\n美味しいデス…\n幸せデス～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1124, 103001, 0, 1, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1126, 103002, 0, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1127, 103002, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1128, 103002, 0, 1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1129, 103002, 0, 1, "むむ、アクダイカンの\n気配がするデース", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1130, 103002, 0, 1, "クニトリ合戦\n楽しみデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1131, 103002, 0, 8, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1132, 103002, 0, 8, "ショーグンの身は\n何があってもワタシが\n守るデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1133, 103002, 0, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1134, 103002, 0, 1, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1135, 103002, 0, 1, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1136, 103002, 0, 1, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1137, 103002, 0, 8, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1138, 103002, 0, 8, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1139, 103002, 0, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1140, 103002, 0, 1, "もうお昼デス\nお昼寝するデスか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1141, 103002, 0, 1, "そろそろおネム\nデース…\n一緒に寝るデス？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1142, 103002, 0, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1143, 103002, 0, 1, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1144, 103002, 0, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1145, 103002, 0, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1146, 103002, 0, 1, "身体を暖めてイクサに\n向かうデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1147, 103002, 0, 1, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1148, 103002, 0, 1, "美味しいデス…\n酢昆布を和えて\nみたいデス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1149, 103002, 0, 1, "物事を知って\n来たるべきイクサに\n活かすデス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1151, 103101, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1152, 103101, 0, 1, "父は、いつも男性に\nきびしいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1153, 103101, 0, 1, "占ってほしい人は、\nいつでも言って\nください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1154, 103101, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1155, 103101, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1156, 103101, 0, 4, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1157, 103101, 0, 8, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1158, 103101, 0, 1, "女難の相が\n出ていますね…\n気をつけてください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1159, 103101, 0, 1, "今日のあなたの運勢は\n大きょ…\n大吉です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1160, 103101, 0, 1, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1161, 103101, 0, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1162, 103101, 0, 4, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1163, 103101, 0, 8, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1164, 103101, 0, 1, "おはようございます\n今日の運勢を\n占いましょうか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1165, 103101, 0, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1166, 103101, 0, 1, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1167, 103101, 0, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1168, 103101, 0, 1, "一休みしましょう。\n憑かれて疲れました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1169, 103101, 0, 1, "かわいい…\n付喪神が宿らないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1170, 103101, 0, 1, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1171, 103101, 0, 1, "気持ちいいです…\n極楽浄土みたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1172, 103101, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1173, 103101, 0, 1, "お父さんも\n食べたいの？\n死んでるのに？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1174, 103101, 0, 1, "この本…\nもしかして予言書？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1176, 103102, 0, 1, "ここにいる幽霊は\nみんな幸せそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1177, 103102, 0, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1178, 103102, 0, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1179, 103102, 0, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1180, 103102, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1181, 103102, 0, 8, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1182, 103102, 0, 8, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1183, 103102, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1184, 103102, 0, 1, "シノブには指一本\n触れさせん！\nガブッ！！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1185, 103102, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1186, 103102, 0, 1, "ラップ音？\nいえ、父のいびきです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1187, 103102, 0, 8, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1188, 103102, 0, 8, "前世って信じますか？\n私とあなたは\nもしかすると…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1189, 103102, 0, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1190, 103102, 0, 1, "明るい時間は\nあまり得意では\nないんです…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1191, 103102, 0, 1, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1192, 103102, 0, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1193, 103102, 0, 1, "うとうと…\nあれ…\n走馬灯が見える…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1194, 103102, 0, 1, "かわいい…\nお父さんみたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1195, 103102, 0, 1, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1196, 103102, 0, 1, "快適です…\n幽霊もどんどん\n集まってきますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1197, 103102, 0, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1198, 103102, 0, 1, "お父さんの分も、\n私が食べるね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1199, 103102, 0, 1, "この本の占い\n私の占いと違う…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1201, 103201, 0, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1202, 103201, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1203, 103201, 0, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1204, 103201, 0, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1205, 103201, 0, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1206, 103201, 0, 4, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1207, 103201, 0, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1208, 103201, 0, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1209, 103201, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1210, 103201, 0, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1211, 103201, 0, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1212, 103201, 0, 4, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1213, 103201, 0, 8, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1214, 103201, 0, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1215, 103201, 0, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1216, 103201, 0, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1217, 103201, 0, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1218, 103201, 0, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1219, 103201, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1220, 103201, 0, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1221, 103201, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1222, 103201, 0, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1223, 103201, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1224, 103201, 0, 1, "経済の仕組みを\nしっかりと\n学ばなくては…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1226, 103202, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1227, 103202, 0, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1228, 103202, 0, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1229, 103202, 0, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1230, 103202, 0, 1, "この狭さも\n慣れれば\n快適ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1231, 103202, 0, 8, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1232, 103202, 0, 8, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1233, 103202, 0, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1234, 103202, 0, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1235, 103202, 0, 1, "資産運用？\nお幾らほど\n増やしたいのです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1236, 103202, 0, 1, "今度一緒に…\nパーティにでも\n行きませんこと？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1237, 103202, 0, 8, "その、よろしければ\n私とお散歩など\nいかがです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1238, 103202, 0, 8, "あなたさまの優しさは\nお金には\n代えられませんわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1239, 103202, 0, 1, "なぜだか\n眠り足りないような\n感じがしますわ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1240, 103202, 0, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1241, 103202, 0, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1242, 103202, 0, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1243, 103202, 0, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1244, 103202, 0, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1245, 103202, 0, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1246, 103202, 0, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1247, 103202, 0, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1248, 103202, 0, 1, "なかなか\n美味ですわ\nシェフはどちらに？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1249, 103202, 0, 1, "商人って、物語では\n悪役にされやすいの\nですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1251, 103301, 0, 1, "家の中のものを使って\n物ボケ、考えるべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1252, 103301, 0, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1253, 103301, 0, 1, "うちに塀ができたら、\nへー、って言いたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1254, 103301, 0, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1255, 103301, 0, 1, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1256, 103301, 0, 4, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1257, 103301, 0, 8, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1258, 103301, 0, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1259, 103301, 0, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1260, 103301, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1261, 103301, 0, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1262, 103301, 0, 4, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1263, 103301, 0, 8, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1264, 103301, 0, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1265, 103301, 0, 1, "お昼だ～腹が減っては\n笑いはできぬ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1266, 103301, 0, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1267, 103301, 0, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1268, 103301, 0, 1, "ナイスなイスに\n座りたい、\nなんちゃって！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1269, 103301, 0, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1270, 103301, 0, 1, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1271, 103301, 0, 1, "ぽかぽかだ…\nウケた会場みてぇだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1272, 103301, 0, 1, "お笑い芸人は、\n体が資本だべさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1273, 103301, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1274, 103301, 0, 1, "ネタ作りのヒントが\n載ってねぇか探すべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1276, 103302, 0, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1277, 103302, 0, 1, "この部屋でも、商品\n売っていいんだべか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1278, 103302, 0, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1279, 103302, 0, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1280, 103302, 0, 1, "エリザベスも\n連れてきてぇ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1281, 103302, 0, 8, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1282, 103302, 0, 8, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1283, 103302, 0, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1284, 103302, 0, 1, "あんちゃん、百連\nツッコミ特訓だ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1285, 103302, 0, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1286, 103302, 0, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1287, 103302, 0, 8, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1288, 103302, 0, 8, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1289, 103302, 0, 1, "おはよう！\n今日もいい\nネタ作り日和だ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1290, 103302, 0, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1291, 103302, 0, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1292, 103302, 0, 1, "うーん…衣装、\nずれてねぇべか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1293, 103302, 0, 1, "ナイスなイスに\n座りたい\nなんちゃって！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1294, 103302, 0, 1, "はう～\nなまらめんこいべや～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1295, 103302, 0, 1, "必要以上に\n癒されちまうべや～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1296, 103302, 0, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1297, 103302, 0, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1298, 103302, 0, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1299, 103302, 0, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1301, 103401, 0, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1302, 103401, 0, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1303, 103401, 0, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1304, 103401, 0, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1305, 103401, 0, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1306, 103401, 0, 4, "デートとか…\n誘っても嫌がられ\nないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1307, 103401, 0, 8, "もしかして運命の\n相手だったり…\nするのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1308, 103401, 0, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1309, 103401, 0, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1310, 103401, 0, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1311, 103401, 0, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1312, 103401, 0, 4, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1313, 103401, 0, 8, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1314, 103401, 0, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1315, 103401, 0, 1, "おっひるー♪\nなに食べよっかな～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1316, 103401, 0, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1317, 103401, 0, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1318, 103401, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1319, 103401, 0, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1320, 103401, 0, 1, "うわあ、\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1321, 103401, 0, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1322, 103401, 0, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1323, 103401, 0, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1324, 103401, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1326, 103402, 0, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1327, 103402, 0, 1, "今日は\nのんびり\n過ごそうかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1328, 103402, 0, 1, "あ…\nお皿、洗って\nなかった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1329, 103402, 0, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1330, 103402, 0, 1, "素敵な彼氏…\n欲しいなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1331, 103402, 0, 8, "彼と私\n釣り合うかな…\n大丈夫よね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1332, 103402, 0, 8, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1333, 103402, 0, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1334, 103402, 0, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1335, 103402, 0, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1336, 103402, 0, 1, "お姉さんが～\n可愛がって\nあげようか～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1337, 103402, 0, 8, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1338, 103402, 0, 8, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1339, 103402, 0, 1, "さーて、今日も\nいっちょやるぞー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1340, 103402, 0, 1, "後でのんびり\nストレッチでも\nしようっと♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1341, 103402, 0, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1342, 103402, 0, 1, "仕事の前に、髪とか\n整えないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1343, 103402, 0, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1344, 103402, 0, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1345, 103402, 0, 1, "うわあ\nこれ、私の好きな\n香りだー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1346, 103402, 0, 1, "あったかーい…\nだ、だらけ\nちゃいそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1347, 103402, 0, 1, "地道な努力が\nリア充への\n第一歩！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1348, 103402, 0, 1, "おーいしっ♪\nこのために\n生きてるわねえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1349, 103402, 0, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1351, 103801, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1352, 103801, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1353, 103801, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1354, 103801, 0, 1, "ちょっと、休憩…\n本でも読もうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1355, 103801, 0, 1, "もっとたくさんの本に\n囲まれて生活したいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1356, 103801, 0, 4, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1357, 103801, 0, 8, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1358, 103801, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1359, 103801, 0, 1, "あ…お姉ちゃんと\n約束があったんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1360, 103801, 0, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1361, 103801, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1362, 103801, 0, 4, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1363, 103801, 0, 8, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1364, 103801, 0, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1365, 103801, 0, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1366, 103801, 0, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1367, 103801, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1368, 103801, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1369, 103801, 0, 1, "かわいいものが\n枕元にあると\n安心できるんですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1370, 103801, 0, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1371, 103801, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1372, 103801, 0, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1373, 103801, 0, 1, "少食ですけど\nこれならたくさん\n食べられそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1374, 103801, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1376, 103802, 0, 1, "しっかり休まないと\n早く回復するように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1377, 103802, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1378, 103802, 0, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1379, 103802, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1380, 103802, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1381, 103802, 0, 8, "むかしむかしある所に\nシオリと\nあの人が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1382, 103802, 0, 8, "あの人の\nためにも早く\n病気を治さないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1383, 103802, 0, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1384, 103802, 0, 1, "新記録です！\n一週間連続、\n平熱を保ちました！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1385, 103802, 0, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1386, 103802, 0, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1387, 103802, 0, 8, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1388, 103802, 0, 8, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1389, 103802, 0, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1390, 103802, 0, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1391, 103802, 0, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1392, 103802, 0, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1393, 103802, 0, 1, "少し休もう\n…やっぱり\nたくさん休もう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1394, 103802, 0, 1, "この子がいれば\n病室に一人でも\n寂しくないですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1395, 103802, 0, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1396, 103802, 0, 1, "こうして温まって\nいると、病気も\n治る気がします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1397, 103802, 0, 1, "私も、少しは\n体力をつけないと\nいけませんよね　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1398, 103802, 0, 1, "いっぱい食べて\n健康な体にならないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1399, 103802, 0, 1, "続きが気になって\n読むのを止める\nタイミングが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1401, 104001, 0, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1402, 104001, 0, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1403, 104001, 0, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1404, 104001, 0, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1405, 104001, 0, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1406, 104001, 0, 4, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1407, 104001, 0, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1408, 104001, 0, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1409, 104001, 0, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1410, 104001, 0, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1411, 104001, 0, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1412, 104001, 0, 4, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1413, 104001, 0, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1414, 104001, 0, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1415, 104001, 0, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1416, 104001, 0, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1417, 104001, 0, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1418, 104001, 0, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1419, 104001, 0, 1, "かわいいなあ。\nこの子とも\n友達になりたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1420, 104001, 0, 1, "いい香りです…\n精神統一して\n友達のイメトレ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1421, 104001, 0, 1, "灯りを見てると、\n心がなごみます～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1422, 104001, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1423, 104001, 0, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1424, 104001, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1426, 104002, 0, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1427, 104002, 0, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1428, 104002, 0, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1429, 104002, 0, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1430, 104002, 0, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1431, 104002, 0, 8, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1432, 104002, 0, 8, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1433, 104002, 0, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1434, 104002, 0, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1435, 104002, 0, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1436, 104002, 0, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1437, 104002, 0, 8, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1438, 104002, 0, 8, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1439, 104002, 0, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1440, 104002, 0, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1441, 104002, 0, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1442, 104002, 0, 1, "私…見た目\nそこまで変じゃ\nないですよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1443, 104002, 0, 1, "ほわあ～…\nふかふかって\nいいものですね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1444, 104002, 0, 1, "この子みたいな\nあいくるしさを\n目指したいです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1445, 104002, 0, 1, "灯りを見てると\n心がなごみます～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1446, 104002, 0, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1447, 104002, 0, 1, "身体を鍛えて\nみなさんを\n守ります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1448, 104002, 0, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1449, 104002, 0, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1451, 104201, 0, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1452, 104201, 0, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1453, 104201, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1454, 104201, 0, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1455, 104201, 0, 1, "あとで曲を復習して\nおかないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1456, 104201, 0, 4, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1457, 104201, 0, 8, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1458, 104201, 0, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1459, 104201, 0, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 0);
