INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1543, 104302, 0, 1, "ふぅ～♪\n疲れがとれるな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1544, 104302, 0, 1, "うっ！　か、かわいい\nじゃねぇか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1545, 104302, 0, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1546, 104302, 0, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1547, 104302, 0, 1, "オラオラオラーーー！\n燃えるぜーーー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1548, 104302, 0, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1549, 104302, 0, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1551, 104501, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1552, 104501, 0, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1553, 104501, 0, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1554, 104501, 0, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1555, 104501, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1556, 104501, 0, 4, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1557, 104501, 0, 8, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1558, 104501, 0, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1559, 104501, 0, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1560, 104501, 0, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1561, 104501, 0, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1562, 104501, 0, 4, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1563, 104501, 0, 8, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1564, 104501, 0, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1565, 104501, 0, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1566, 104501, 0, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1567, 104501, 0, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1568, 104501, 0, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1569, 104501, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1570, 104501, 0, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1571, 104501, 0, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1572, 104501, 0, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1573, 104501, 0, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1574, 104501, 0, 1, "この本の下に、\nそういう本を\nカムフラージュ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1576, 104502, 0, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1577, 104502, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1578, 104502, 0, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1579, 104502, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1580, 104502, 0, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1581, 104502, 0, 8, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1582, 104502, 0, 8, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1583, 104502, 0, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1584, 104502, 0, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1585, 104502, 0, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1586, 104502, 0, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1587, 104502, 0, 8, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1588, 104502, 0, 8, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1589, 104502, 0, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1590, 104502, 0, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1591, 104502, 0, 1, "今夜は一晩中…\nなんですね？\nぐふふ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1592, 104502, 0, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1593, 104502, 0, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1594, 104502, 0, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1595, 104502, 0, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1596, 104502, 0, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1597, 104502, 0, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1598, 104502, 0, 1, "こんなにいっぱい\n入りません…\nお腹にっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1599, 104502, 0, 1, "どんな本も\nよく見ると\nいやらしいです…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1601, 104601, 0, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1602, 104601, 0, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1603, 104601, 0, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1604, 104601, 0, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1605, 104601, 0, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1606, 104601, 0, 4, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1607, 104601, 0, 8, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1608, 104601, 0, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1609, 104601, 0, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1610, 104601, 0, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1611, 104601, 0, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1612, 104601, 0, 4, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1613, 104601, 0, 8, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1614, 104601, 0, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1615, 104601, 0, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1616, 104601, 0, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1617, 104601, 0, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1618, 104601, 0, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1619, 104601, 0, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1620, 104601, 0, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1621, 104601, 0, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1622, 104601, 0, 1, "本当は外で\n運動したいん\nだけどにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1623, 104601, 0, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1624, 104601, 0, 1, "ふむふむ…猫の\nしつけ方は、と…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1626, 104602, 0, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1627, 104602, 0, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1628, 104602, 0, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1629, 104602, 0, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1630, 104602, 0, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1631, 104602, 0, 8, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1632, 104602, 0, 8, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1633, 104602, 0, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1634, 104602, 0, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1635, 104602, 0, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1636, 104602, 0, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1637, 104602, 0, 8, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1638, 104602, 0, 8, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1639, 104602, 0, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1640, 104602, 0, 1, "そろそろおっひる～\nにゃーんか\n食べたい…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1641, 104602, 0, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1642, 104602, 0, 1, "にゃにゃ…\n髪が乱れてるにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1643, 104602, 0, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1644, 104602, 0, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1645, 104602, 0, 1, "猫でもこわくない\n優しい灯りにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1646, 104602, 0, 1, "ぽっかぽか\nしあわせな気分にゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1647, 104602, 0, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1648, 104602, 0, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1649, 104602, 0, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1651, 104801, 0, 1, "内職手伝える人\nいないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1652, 104801, 0, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1653, 104801, 0, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1654, 104801, 0, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1655, 104801, 0, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1656, 104801, 0, 4, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1657, 104801, 0, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1658, 104801, 0, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1659, 104801, 0, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1660, 104801, 0, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1661, 104801, 0, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1662, 104801, 0, 4, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1663, 104801, 0, 8, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1664, 104801, 0, 1, "おはよう！\n今日も朝から\nバイトよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1665, 104801, 0, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1666, 104801, 0, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1667, 104801, 0, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1668, 104801, 0, 1, "はぁ～バイトで\n疲れたから癒される～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1669, 104801, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1670, 104801, 0, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1671, 104801, 0, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1672, 104801, 0, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1673, 104801, 0, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1674, 104801, 0, 1, "日々絶えず学んで、\n生活に活かすのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1676, 104802, 0, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1677, 104802, 0, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1678, 104802, 0, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1679, 104802, 0, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1680, 104802, 0, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1681, 104802, 0, 8, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1682, 104802, 0, 8, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1683, 104802, 0, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1684, 104802, 0, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1685, 104802, 0, 1, "今から\n休憩するところなの\n一緒にどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1686, 104802, 0, 1, "ん？　どうしたの？\n私のことが\n気になるの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1687, 104802, 0, 8, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1688, 104802, 0, 8, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1689, 104802, 0, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1690, 104802, 0, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1691, 104802, 0, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1692, 104802, 0, 1, "女の子らしく…\nどうも忘れちゃう\nのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1693, 104802, 0, 1, "ここで少し\n休んだら\nまたバイトよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1694, 104802, 0, 1, "これを内職で\n作ったら\n儲かるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1695, 104802, 0, 1, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1696, 104802, 0, 1, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1697, 104802, 0, 1, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1698, 104802, 0, 1, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1699, 104802, 0, 1, "日々絶えず学んで\n生活に活かすのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1701, 104901, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1702, 104901, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1703, 104901, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1704, 104901, 0, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1705, 104901, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1706, 104901, 0, 4, "この部屋を、\n私と弟くんの\n愛の巣に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1707, 104901, 0, 8, "はぁ～…弟くん\n禁断症状だよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1708, 104901, 0, 1, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1709, 104901, 0, 1, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1710, 104901, 0, 1, "姉と弟の外出は\nデートって\nいうのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1711, 104901, 0, 1, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 0);
