INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1794, 105002, 0, 1, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1795, 105002, 0, 1, "オシャレ～♪\n大人なレディって\n感じよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1796, 105002, 0, 1, "あったかーい♪\nここで宿題\nしようかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1797, 105002, 0, 1, "運動したら成長\nしないかしら？\n背とか胸とか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1798, 105002, 0, 1, "レディは、食べ物にも\n気を使うのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1799, 105002, 0, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1801, 105101, 0, 1, "魔法と科学\nどっちも極めて\nみせるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1802, 105101, 0, 1, "仕事も片付いたし\n部屋で\n実験実験…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1803, 105101, 0, 1, "たまった治療代の\n請求書を\nまとめないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1804, 105101, 0, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1805, 105101, 0, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1806, 105101, 0, 4, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1807, 105101, 0, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1808, 105101, 0, 1, "あら、ヒマなら\n薬草の整理を\n手伝って", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1809, 105101, 0, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1810, 105101, 0, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1811, 105101, 0, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1812, 105101, 0, 4, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1813, 105101, 0, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1814, 105101, 0, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1815, 105101, 0, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1816, 105101, 0, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1817, 105101, 0, 1, "医者たるもの\nいつだって\n清潔に…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1818, 105101, 0, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1819, 105101, 0, 1, "ぬいぐるみなら\n動物も…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1820, 105101, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1821, 105101, 0, 1, "ここにいると\nついウトウト\nしちゃうわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1822, 105101, 0, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1823, 105101, 0, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1824, 105101, 0, 1, "実験もいいけど\nいつか成果を\n本にしないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1826, 105102, 0, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1827, 105102, 0, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1828, 105102, 0, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1829, 105102, 0, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1830, 105102, 0, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1831, 105102, 0, 8, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1832, 105102, 0, 8, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1833, 105102, 0, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1834, 105102, 0, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1835, 105102, 0, 1, "欲しい薬が\nあったら言ってね\n調合してみるから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1836, 105102, 0, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1837, 105102, 0, 8, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1838, 105102, 0, 8, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1839, 105102, 0, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1840, 105102, 0, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1841, 105102, 0, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1842, 105102, 0, 1, "身なりについて\n研究するのも\n楽しそうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1843, 105102, 0, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1844, 105102, 0, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1845, 105102, 0, 1, "いい香り…\n仕事場にも\n置いてみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1846, 105102, 0, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1847, 105102, 0, 1, "運動しないと\n体型の維持が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1848, 105102, 0, 1, "適度な食事も\n大切よね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1849, 105102, 0, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1851, 105201, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1852, 105201, 0, 1, "いっけな～い！\nひづめで床に傷が！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1853, 105201, 0, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1854, 105201, 0, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1855, 105201, 0, 1, "みんな、体重\nどれくらいなのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1856, 105201, 0, 4, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1857, 105201, 0, 8, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1858, 105201, 0, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1859, 105201, 0, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1860, 105201, 0, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1861, 105201, 0, 1, "力仕事ならまかせて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1862, 105201, 0, 4, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1863, 105201, 0, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1864, 105201, 0, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1865, 105201, 0, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1866, 105201, 0, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1867, 105201, 0, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1868, 105201, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1869, 105201, 0, 1, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1870, 105201, 0, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1871, 105201, 0, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1872, 105201, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1873, 105201, 0, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1874, 105201, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1876, 105202, 0, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1877, 105202, 0, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1878, 105202, 0, 1, "動物苑…って\n嫌なギルド名ね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1879, 105202, 0, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1880, 105202, 0, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1881, 105202, 0, 8, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1882, 105202, 0, 8, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1883, 105202, 0, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1884, 105202, 0, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1885, 105202, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1886, 105202, 0, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1887, 105202, 0, 8, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1888, 105202, 0, 8, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1889, 105202, 0, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1890, 105202, 0, 1, "ダイエット中だから\nお昼は抜いてるの～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1891, 105202, 0, 1, "お肌のケアに\nパックしながら\n寝ようかな～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1892, 105202, 0, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1893, 105202, 0, 1, "ミシッっていった！\n私、重すぎるかも！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1894, 105202, 0, 1, "あ！　あなたも\n美人さんね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1895, 105202, 0, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1896, 105202, 0, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1897, 105202, 0, 1, "な、なんだか野生に\n帰っちゃいそう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1898, 105202, 0, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1899, 105202, 0, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1901, 105301, 0, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1902, 105301, 0, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1903, 105301, 0, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1904, 105301, 0, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1905, 105301, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1906, 105301, 0, 4, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1907, 105301, 0, 8, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1908, 105301, 0, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1909, 105301, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1910, 105301, 0, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1911, 105301, 0, 1, "私ともっと高く、\n共に飛ぼう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1912, 105301, 0, 4, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1913, 105301, 0, 8, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1914, 105301, 0, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1915, 105301, 0, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1916, 105301, 0, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1917, 105301, 0, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1918, 105301, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1919, 105301, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1920, 105301, 0, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1921, 105301, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1922, 105301, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1923, 105301, 0, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1924, 105301, 0, 1, "これを読んで、新たな\n戦術を編み出すぞっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1926, 105302, 0, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1927, 105302, 0, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1928, 105302, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1929, 105302, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1930, 105302, 0, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1931, 105302, 0, 8, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1932, 105302, 0, 8, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1933, 105302, 0, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1934, 105302, 0, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1935, 105302, 0, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1936, 105302, 0, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1937, 105302, 0, 8, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1938, 105302, 0, 8, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1939, 105302, 0, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1940, 105302, 0, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1941, 105302, 0, 1, "寝る前に…あと少し\nだけ、鍛錬を…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 1942, 105302, 0, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 1943, 105302, 0, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 1944, 105302, 0, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 1945, 105302, 0, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 1946, 105302, 0, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 1947, 105302, 0, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 1948, 105302, 0, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 1949, 105302, 0, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 1951, 105701, 0, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 1952, 105701, 0, 1, "団員募集の\nポスター\n作ろうっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 1953, 105701, 0, 1, "父さん…\n今頃どこで\n何してるんだろ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 1954, 105701, 0, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 1955, 105701, 0, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 1956, 105701, 0, 4, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 1957, 105701, 0, 8, "父さん…\n私にもね、大切な\n仲間ができたよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 1958, 105701, 0, 1, "仕事の依頼？\nすぐに支度\nしてくるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 1959, 105701, 0, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 1960, 105701, 0, 1, "なーにー？\nどうしたの？\n今呼んだよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 1961, 105701, 0, 1, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 1962, 105701, 0, 4, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 1963, 105701, 0, 8, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 0);
