INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2135, 105902, 0, 1, "お出かけですか？\nわたくしもお供\nいたします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2136, 105902, 0, 1, "ひゃん！\n主さま…\nいじわるです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2137, 105902, 0, 8, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2138, 105902, 0, 8, "主さまの優しさに\nわたくしはいつも\n癒されています…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2139, 105902, 0, 1, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2140, 105902, 0, 1, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2141, 105902, 0, 1, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 3, 2142, 105902, 0, 1, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2143, 105902, 0, 1, "このふかふかの優しさ\n主さまのようです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2144, 105902, 0, 1, "かわいい♪\n主さまにそっくり♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2145, 105902, 0, 1, "この心地よさ…\n主さまのようです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2146, 105902, 0, 1, "ここで洗濯物を\n乾かせるでしょうか　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 6, 3, 2147, 105902, 0, 1, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2148, 105902, 0, 1, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2149, 105902, 0, 1, "都会のことを\n学ばないと…\nふむふむ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2151, 190801, 0, 1, "ふぁ～…あっ\n見られちゃい\nましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2152, 190801, 0, 1, "ギルドハウスの\n使い心地は\nどうですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2153, 190801, 0, 1, "ご用があれば\nなんでも言って\nくださいね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2154, 190801, 0, 1, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2155, 190801, 0, 1, "おはようございます！\nお掃除の方、\nはじめていきますね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2156, 190801, 0, 1, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2157, 190801, 0, 1, "ここは夜でも\nにぎやかですね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2158, 104701, 0, 1, "…\n少しうとうと\nしてしまった", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2159, 104701, 0, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2160, 104701, 0, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2161, 104701, 0, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2162, 104701, 0, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2163, 104701, 0, 4, "少年のことを考えると\n顔がにやけてしまう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2164, 104701, 0, 8, "少年は今頃\n平和に過ごせて\nいるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2165, 104701, 0, 1, "ん？　悪いが\n兜は脱がないぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2166, 104701, 0, 1, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2167, 104701, 0, 1, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2168, 104701, 0, 1, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2169, 104701, 0, 4, "君といると、兜の中が\nいつもより暑いんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2170, 104701, 0, 8, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2171, 104701, 0, 1, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2172, 104701, 0, 1, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2173, 104701, 0, 1, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2175, 104701, 0, 1, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2176, 104701, 0, 1, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2177, 104701, 0, 1, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2178, 104701, 0, 1, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2180, 104701, 0, 1, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2181, 104701, 0, 1, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2183, 102601, 0, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2184, 102601, 0, 1, "ドングリの木\n近くに生えてない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2185, 102601, 0, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2186, 102601, 0, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2187, 102601, 0, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2188, 102601, 0, 4, "あんパン買ったし\nあいつにも\n分けてあげよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2189, 102601, 0, 8, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2190, 102601, 0, 1, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2191, 102601, 0, 1, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2192, 102601, 0, 1, "ドングリあげるから\nあんパン買ってきて～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2193, 102601, 0, 1, "う～\n疲れちゃったよ～\nおんぶして運んで～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2194, 102601, 0, 4, "あんたといると\nリア充って\n感じがするよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2195, 102601, 0, 8, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2196, 102601, 0, 1, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2197, 102601, 0, 1, "そろそろお昼寝でも\nしたい気分だねぇ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2198, 102601, 0, 1, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2200, 102601, 0, 1, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2201, 102601, 0, 1, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2202, 102601, 0, 1, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2203, 102601, 0, 1, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2205, 102601, 0, 1, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2206, 102601, 0, 1, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2208, 106301, 0, 1, "ランドソル…\nおっきな街だなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2209, 106301, 0, 1, "ロザリア…\nきっと、いるよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2210, 106301, 0, 1, "最近、弓の練習\nサボっちゃってるな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2211, 106301, 0, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2212, 106301, 0, 1, "ちょっとだけ\n慣れてきたかも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2213, 106301, 0, 4, "いつか、\nあの人と一緒に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2214, 106301, 0, 8, "…なんか最近、\nあの人のこと\nばっかり…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2215, 106301, 0, 1, "もしよかったら\n街に出かけない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2216, 106301, 0, 1, "この部屋、\n妖精さんも\n気に入るかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2217, 106301, 0, 1, "今日は\nなににしよっか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2218, 106301, 0, 1, "今日はどこ行く？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2219, 106301, 0, 4, "たまには二人で…\nな、なんちゃって！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2220, 106301, 0, 8, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2221, 106301, 0, 1, "おはよう！\n今日も一緒に\n頑張ろうね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2222, 106301, 0, 1, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2223, 106301, 0, 1, "ランドソルは\n夜でも賑やかだね！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2225, 106301, 0, 1, "わぁっ！\nすっごく\nふわふわだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2226, 106301, 0, 1, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2227, 106301, 0, 1, "くんくん\nなんか…いい匂い？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2228, 106301, 0, 1, "すっごいなー\nあったかいなー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2230, 106301, 0, 1, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2231, 106301, 0, 1, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2233, 103601, 0, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2234, 103601, 0, 1, "カップケーキが\n食べたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2235, 103601, 0, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2236, 103601, 0, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2237, 103601, 0, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2238, 103601, 0, 4, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2239, 103601, 0, 8, "早く大人になりたいな\n…あの人と同じくらい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2240, 103601, 0, 1, "ぼ、防犯魔石を\n鳴らしますよ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2241, 103601, 0, 1, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2242, 103601, 0, 1, "これ宿題です\nやっておいて\nくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2243, 103601, 0, 1, "あ、遅刻ですよ\n待っていたんですから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2244, 103601, 0, 4, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2245, 103601, 0, 8, "あの…何歳まで\n子供だと思いますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2246, 103601, 0, 1, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2247, 103601, 0, 1, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2248, 103601, 0, 1, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2250, 103601, 0, 1, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2251, 103601, 0, 1, "つ、つぶらな瞳には\n弱いんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2252, 103601, 0, 1, "リラックスできますね\n集中力も\n上がりそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2253, 103601, 0, 1, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2255, 103601, 0, 1, "ピーマンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2256, 103601, 0, 1, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2258, 101501, 0, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2259, 101501, 0, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2260, 101501, 0, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2261, 101501, 0, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2262, 101501, 0, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2263, 101501, 0, 4, "彼は優しいから、\nきっと良い\nお父さんになるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2264, 101501, 0, 8, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2265, 101501, 0, 1, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2266, 101501, 0, 1, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2267, 101501, 0, 1, "よかったら一緒に\n絵本を作らない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2268, 101501, 0, 1, "あなたはいつも元気ね\n先生も見習わないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2269, 101501, 0, 4, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2270, 101501, 0, 8, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2271, 101501, 0, 1, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2272, 101501, 0, 1, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2273, 101501, 0, 1, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2275, 101501, 0, 1, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2276, 101501, 0, 1, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2277, 101501, 0, 1, "いい香りね～\n保育園にも\n置こうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2278, 101501, 0, 1, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2280, 101501, 0, 1, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2281, 101501, 0, 1, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2283, 104401, 0, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2284, 104401, 0, 1, "拠点とはやはり\n斯くあるべきじゃの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2285, 104401, 0, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2286, 104401, 0, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2287, 104401, 0, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2288, 104401, 0, 4, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2289, 104401, 0, 8, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2290, 104401, 0, 1, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2291, 104401, 0, 1, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2292, 104401, 0, 1, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2293, 104401, 0, 1, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2294, 104401, 0, 4, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2295, 104401, 0, 8, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2296, 104401, 0, 1, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2297, 104401, 0, 1, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2298, 104401, 0, 1, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2300, 104401, 0, 1, "よい座り心地じゃ\nわらわの玉座としよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2301, 104401, 0, 1, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2302, 104401, 0, 1, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2303, 104401, 0, 1, "あたたかい…\n身も心も\n浄化されるようじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2305, 104401, 0, 1, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2306, 104401, 0, 1, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2308, 105401, 0, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2309, 105401, 0, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2310, 105401, 0, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2311, 105401, 0, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2312, 105401, 0, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2313, 105401, 0, 4, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2314, 105401, 0, 8, "まさか私が\nアイドルになるなんて\n思ってなかったです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2315, 105401, 0, 1, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2316, 105401, 0, 1, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2317, 105401, 0, 1, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2318, 105401, 0, 1, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2319, 105401, 0, 4, "どうしました？\n新しい服が\n欲しくなりましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2320, 105401, 0, 8, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2321, 105401, 0, 1, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2322, 105401, 0, 1, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2323, 105401, 0, 1, "夜の方が\n作業が捗るのは\n私だけですかね？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2325, 105401, 0, 1, "このふかふか具合\nいい素材を\n使ってますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2326, 105401, 0, 1, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2327, 105401, 0, 1, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2328, 105401, 0, 1, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2330, 105401, 0, 1, "何をするにも\n食事は大切！\n体力が資本です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2331, 105401, 0, 1, "私もいつか\nデザインを\nまとめて本に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2333, 107501, 0, 1, "またお魚を\n捕りにいこうかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2334, 107501, 0, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2335, 107501, 0, 1, "ここなら、\n日焼けの心配は\nありませんね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2336, 107501, 0, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2337, 107501, 0, 1, "泳ぐのも食べるのも\n大得意です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2338, 107501, 0, 4, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2339, 107501, 0, 8, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2340, 107501, 0, 1, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2341, 107501, 0, 1, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2342, 107501, 0, 1, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2343, 107501, 0, 1, "あなたの水着、\n私が選んであげます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2344, 107501, 0, 4, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2345, 107501, 0, 8, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2346, 107501, 0, 1, "朝の海も\n幻想的で\nいいですよねぇ～！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2347, 107501, 0, 1, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2348, 107501, 0, 1, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2350, 107501, 0, 1, "泳ぎ疲れたので\nちょっと休憩です～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2351, 107501, 0, 1, "あなたにも、\n可愛い水着を\n着せてあげたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2352, 107501, 0, 1, "なんだか、\nリゾート気分です…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2353, 107501, 0, 1, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2355, 107501, 0, 1, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2356, 107501, 0, 1, "南国のフルーツ図鑑\n…じゅるり…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2358, 107501, 0, 1, "わ～♪　おいしそ…\nか、かわいいですね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2359, 107501, 0, 1, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2360, 107601, 0, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2361, 107601, 0, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2362, 107601, 0, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2363, 107601, 0, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2364, 107601, 0, 1, "クロールの練習なら、\n部屋でもできそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2365, 107601, 0, 4, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2366, 107601, 0, 8, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2367, 107601, 0, 1, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2368, 107601, 0, 1, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2369, 107601, 0, 1, "海の美しさは、\n森とはちがう\n魅力がありました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2370, 107601, 0, 1, "この髪飾りですか？\n造花ですから\n水の中も平気です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2371, 107601, 0, 4, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2372, 107601, 0, 8, "はじめての海が、\n主さまと一緒で\n幸せです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2373, 107601, 0, 1, "おはようございます。\n泳ぎに行く準備は\n万端です", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2374, 107601, 0, 1, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2375, 107601, 0, 1, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2377, 107601, 0, 1, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2378, 107601, 0, 1, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2379, 107601, 0, 1, "この香り…\nきっと主さまも\n気に入ります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2380, 107601, 0, 1, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2382, 107601, 0, 1, "泳いだ後の食事は\n一段と美味しく\n感じます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2383, 107601, 0, 1, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2385, 107601, 0, 1, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2386, 107601, 0, 1, "ふかふかですね\n主さまのようです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2387, 107701, 0, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2388, 107701, 0, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2389, 107701, 0, 1, "今日はドジを\nしませんように！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2390, 107701, 0, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2391, 107701, 0, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2392, 107701, 0, 4, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2393, 107701, 0, 8, "水着でご奉仕したら、\nあの人は喜ぶかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2394, 107701, 0, 1, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2395, 107701, 0, 1, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2396, 107701, 0, 1, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2397, 107701, 0, 1, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2398, 107701, 0, 4, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2399, 107701, 0, 8, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2400, 107701, 0, 1, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2401, 107701, 0, 1, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2402, 107701, 0, 1, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2404, 107701, 0, 1, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2405, 107701, 0, 1, "きれいにすれば\nかわいさも倍増\nですよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2406, 107701, 0, 1, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2407, 107701, 0, 1, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2409, 107701, 0, 1, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2410, 107701, 0, 1, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2412, 107701, 0, 1, "えへへ♪\n南の島まで\n乗せてください！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2413, 107701, 0, 1, "なんだか、\n人魚姫に\nなった気分です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2414, 107801, 0, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2415, 107801, 0, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2416, 107801, 0, 1, "ビーチにいると\n日焼けが心配だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2417, 107801, 0, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2418, 107801, 0, 1, "お、泳ぎの練習なんて\nしないからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2419, 107801, 0, 4, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2420, 107801, 0, 8, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2421, 107801, 0, 1, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2422, 107801, 0, 1, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2423, 107801, 0, 1, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2424, 107801, 0, 1, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2425, 107801, 0, 4, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2426, 107801, 0, 8, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2427, 107801, 0, 1, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2428, 107801, 0, 1, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2429, 107801, 0, 1, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2431, 107801, 0, 1, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2432, 107801, 0, 1, "こんな魔物がいたら\n操るのも楽しいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2433, 107801, 0, 1, "にゃう～♪\nまるで南国にいる\n気分ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2434, 107801, 0, 1, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2436, 107801, 0, 1, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2437, 107801, 0, 1, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2439, 107801, 0, 1, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2440, 107801, 0, 1, "リゾートリゾートっ\nのんびりしましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2441, 108001, 0, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2442, 108001, 0, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2443, 108001, 0, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2444, 108001, 0, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2445, 108001, 0, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2446, 108001, 0, 4, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2447, 108001, 0, 8, "キミだけね…\n私のことをここまで\n理解してくれるのは", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2448, 108001, 0, 1, "普段と違う格好\nだからって\nそんなに見ないでよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2449, 108001, 0, 1, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2450, 108001, 0, 1, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2451, 108001, 0, 1, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2452, 108001, 0, 4, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2453, 108001, 0, 8, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2454, 108001, 0, 1, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2455, 108001, 0, 1, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2456, 108001, 0, 1, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2458, 108001, 0, 1, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2459, 108001, 0, 1, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2460, 108001, 0, 1, "いい香りね…\n働く意欲が\n湧いてくるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2461, 108001, 0, 1, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2463, 108001, 0, 1, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2464, 108001, 0, 1, "座学も大切ね\nもっと知識を\n身につけないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2466, 108001, 0, 1, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2467, 108001, 0, 1, "落ち着くわ…\n海とひとつになった\n気分ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2468, 107901, 0, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2469, 107901, 0, 1, "猫グッズで\n海の家を飾りたいにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2470, 107901, 0, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2471, 107901, 0, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2472, 107901, 0, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2473, 107901, 0, 4, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2474, 107901, 0, 8, "あたしの宝は\nここにあるにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2475, 107901, 0, 1, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2476, 107901, 0, 1, "今からキミを\nあたしの相棒に\n任命するにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2477, 107901, 0, 1, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2478, 107901, 0, 1, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2479, 107901, 0, 4, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2480, 107901, 0, 8, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2481, 107901, 0, 1, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2482, 107901, 0, 1, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2483, 107901, 0, 1, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2485, 107901, 0, 1, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2486, 107901, 0, 1, "にゃにゃ～\nなんてかわいさにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2487, 107901, 0, 1, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2488, 107901, 0, 1, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2490, 107901, 0, 1, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2491, 107901, 0, 1, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 2493, 107901, 0, 1, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 2494, 107901, 0, 1, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2495, 103701, 0, 1, "人の家って、道場とは\n違った香りがするね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2496, 103701, 0, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2497, 103701, 0, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2498, 103701, 0, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2499, 103701, 0, 1, "もっと\n上手く立ち回って\n出世したいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2500, 103701, 0, 4, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2501, 103701, 0, 8, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2502, 103701, 0, 1, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2503, 103701, 0, 1, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2504, 103701, 0, 1, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2505, 103701, 0, 1, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2506, 103701, 0, 4, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2507, 103701, 0, 8, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2508, 103701, 0, 1, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2509, 103701, 0, 1, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2510, 103701, 0, 1, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2512, 103701, 0, 1, "いざという時のため\nにも、休憩は大事だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2513, 103701, 0, 1, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2514, 103701, 0, 1, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2515, 103701, 0, 1, "あったかいなぁ。\nたまにはのんびり\nしようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2517, 103701, 0, 1, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2518, 103701, 0, 1, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2520, 100501, 0, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2521, 100501, 0, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2522, 100501, 0, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2523, 100501, 0, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2524, 100501, 0, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2525, 100501, 0, 4, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2526, 100501, 0, 8, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2527, 100501, 0, 1, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2528, 100501, 0, 1, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2529, 100501, 0, 1, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2530, 100501, 0, 1, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2531, 100501, 0, 4, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2532, 100501, 0, 8, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2533, 100501, 0, 1, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2534, 100501, 0, 1, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2535, 100501, 0, 1, "夜のパトロールで\n一緒に平和を\n守るッス！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2537, 100501, 0, 1, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2538, 100501, 0, 1, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2539, 100501, 0, 1, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2540, 100501, 0, 1, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2542, 100501, 0, 1, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2543, 100501, 0, 1, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2545, 108101, 0, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2546, 108101, 0, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2547, 108101, 0, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2548, 108101, 0, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2549, 108101, 0, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2550, 108101, 0, 4, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2551, 108101, 0, 8, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2552, 108101, 0, 1, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2553, 108101, 0, 1, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2554, 108101, 0, 1, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2555, 108101, 0, 1, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2556, 108101, 0, 4, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2557, 108101, 0, 8, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2558, 108101, 0, 1, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2559, 108101, 0, 1, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2560, 108101, 0, 1, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2562, 108101, 0, 1, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2563, 108101, 0, 1, "つん、つん。ふふっ♪\n癒やされます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2564, 108101, 0, 1, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2565, 108101, 0, 1, "あったかいね\nお父さん…\nえ？　分からない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2567, 108101, 0, 1, "霊のみなさんは\n食べられないので…\n私がいただきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2568, 108101, 0, 1, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 2570, 108101, 0, 1, "わぁ、お菓子が沢山…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2572, 108201, 0, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2573, 108201, 0, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2574, 108201, 0, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2575, 108201, 0, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2576, 108201, 0, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2577, 108201, 0, 4, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2578, 108201, 0, 8, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2579, 108201, 0, 1, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2580, 108201, 0, 1, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2581, 108201, 0, 1, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2582, 108201, 0, 1, "プリンの可能性は\n無限大なの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2583, 108201, 0, 4, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2584, 108201, 0, 8, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2585, 108201, 0, 1, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2586, 108201, 0, 1, "ランチは豪華に\nプリンアラモードなの", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2587, 108201, 0, 1, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2589, 108201, 0, 1, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2590, 108201, 0, 1, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2591, 108201, 0, 1, "癒されるの～\nまるで天国なの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2592, 108201, 0, 1, "ここでなら、\n焼きプリンが\n作れるの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2594, 108201, 0, 1, "腹が減っては\nハロウィンは\nできぬなの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2595, 108201, 0, 1, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 2597, 108201, 0, 1, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2599, 108301, 0, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2600, 108301, 0, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2601, 108301, 0, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2602, 108301, 0, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2603, 108301, 0, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2604, 108301, 0, 4, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2605, 108301, 0, 8, "アイツを魅了する\n魔法とかないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2606, 108301, 0, 1, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2607, 108301, 0, 1, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2608, 108301, 0, 1, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2609, 108301, 0, 1, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2610, 108301, 0, 4, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2611, 108301, 0, 8, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2612, 108301, 0, 1, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2613, 108301, 0, 1, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2614, 108301, 0, 1, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2616, 108301, 0, 1, "あう～、お菓子の\n食べすぎで\n動けな～い", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2617, 108301, 0, 1, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2618, 108301, 0, 1, "大人な香りに\n包まれてる～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2619, 108301, 0, 1, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2621, 108301, 0, 1, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2622, 108301, 0, 1, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 2624, 108301, 0, 1, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2626, 107101, 0, 1, "次はどんな策で団長を\nからかってみようか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2627, 107101, 0, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2628, 107101, 0, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2629, 107101, 0, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2630, 107101, 0, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2631, 107101, 0, 4, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2632, 107101, 0, 8, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2633, 107101, 0, 1, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2634, 107101, 0, 1, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2635, 107101, 0, 1, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2636, 107101, 0, 1, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2637, 107101, 0, 4, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2638, 107101, 0, 8, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2639, 107101, 0, 1, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2640, 107101, 0, 1, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2641, 107101, 0, 1, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2643, 107101, 0, 1, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2644, 107101, 0, 1, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2645, 107101, 0, 1, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2646, 107101, 0, 1, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2648, 107101, 0, 1, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2649, 107101, 0, 1, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2651, 105601, 0, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2652, 105601, 0, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2653, 105601, 0, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2654, 105601, 0, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2655, 105601, 0, 1, "ふあぁ～…\n昼寝でもするか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2656, 105601, 0, 4, "ふふっ…\nあの子、見た目より\n根性あるんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2657, 105601, 0, 8, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2658, 105601, 0, 1, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2659, 105601, 0, 1, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2660, 105601, 0, 1, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2661, 105601, 0, 1, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2662, 105601, 0, 4, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2663, 105601, 0, 8, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2664, 105601, 0, 1, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2665, 105601, 0, 1, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2666, 105601, 0, 1, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2668, 105601, 0, 1, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2669, 105601, 0, 1, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2670, 105601, 0, 1, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2671, 105601, 0, 1, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2673, 105601, 0, 1, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2674, 105601, 0, 1, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2676, 101301, 0, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2677, 101301, 0, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2678, 101301, 0, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2679, 101301, 0, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2680, 101301, 0, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2681, 101301, 0, 4, "次はどの漫画を\n貸そうかなー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2682, 101301, 0, 8, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2683, 101301, 0, 1, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2684, 101301, 0, 1, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2685, 101301, 0, 1, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2686, 101301, 0, 1, "新魔法思いついた！\n私って天才！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2687, 101301, 0, 4, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2688, 101301, 0, 8, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2689, 101301, 0, 1, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2690, 101301, 0, 1, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2691, 101301, 0, 1, "夜からが、\nオタ活本番でしょ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2693, 101301, 0, 1, "ふいー！\nくつろいで本を読む！\n最高だねー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2694, 101301, 0, 1, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2695, 101301, 0, 1, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2696, 101301, 0, 1, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2698, 101301, 0, 1, "ウマ―！\nこれの詳細きぼんぬ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2699, 101301, 0, 1, "こ、古代語の本！？\n現代語でおk…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2701, 190901, 0, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2702, 190901, 0, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2703, 190901, 0, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2704, 190901, 0, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2705, 190901, 0, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2706, 190901, 0, 1, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2707, 190901, 0, 1, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2708, 190901, 0, 1, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2709, 190901, 0, 1, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2710, 190901, 0, 1, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2711, 190901, 0, 1, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2712, 190901, 0, 1, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2714, 190901, 0, 1, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2715, 190901, 0, 1, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2716, 190901, 0, 1, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2717, 190901, 0, 1, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2719, 190901, 0, 1, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2720, 190901, 0, 1, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2722, 108401, 0, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2723, 108401, 0, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2724, 108401, 0, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2725, 108401, 0, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2726, 108401, 0, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2727, 108401, 0, 4, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2728, 108401, 0, 8, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2729, 108401, 0, 1, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2730, 108401, 0, 1, "歌を届ける\nサンタさんに\nなりたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2731, 108401, 0, 1, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2732, 108401, 0, 1, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2733, 108401, 0, 4, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2734, 108401, 0, 8, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2735, 108401, 0, 1, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2736, 108401, 0, 1, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2737, 108401, 0, 1, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2739, 108401, 0, 1, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2740, 108401, 0, 1, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2741, 108401, 0, 1, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2742, 108401, 0, 1, "この光みたいに\n灯火になるような\n歌を歌いたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2744, 108401, 0, 1, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2745, 108401, 0, 1, "物語には\n曲作りのヒントが\n詰まっているんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 2747, 108401, 0, 1, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 2748, 108401, 0, 1, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2749, 108501, 0, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2750, 108501, 0, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2751, 108501, 0, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2752, 108501, 0, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2753, 108501, 0, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2754, 108501, 0, 4, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2755, 108501, 0, 8, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2756, 108501, 0, 1, "メリー\nクリスマスです、\nお兄ちゃん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2757, 108501, 0, 1, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2758, 108501, 0, 1, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2759, 108501, 0, 1, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2760, 108501, 0, 4, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2761, 108501, 0, 8, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2762, 108501, 0, 1, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2763, 108501, 0, 1, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2764, 108501, 0, 1, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2766, 108501, 0, 1, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2767, 108501, 0, 1, "この子と一緒に\nおままごとしたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2768, 108501, 0, 1, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2769, 108501, 0, 1, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2771, 108501, 0, 1, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2772, 108501, 0, 1, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 2774, 108501, 0, 1, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 2775, 108501, 0, 1, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2776, 108601, 0, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2777, 108601, 0, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2778, 108601, 0, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2779, 108601, 0, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2780, 108601, 0, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2781, 108601, 0, 4, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2782, 108601, 0, 8, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2783, 108601, 0, 1, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2784, 108601, 0, 1, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2785, 108601, 0, 1, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2786, 108601, 0, 1, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2787, 108601, 0, 4, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2788, 108601, 0, 8, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2789, 108601, 0, 1, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2790, 108601, 0, 1, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2791, 108601, 0, 1, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2793, 108601, 0, 1, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2794, 108601, 0, 1, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2795, 108601, 0, 1, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2796, 108601, 0, 1, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2798, 108601, 0, 1, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2799, 108601, 0, 1, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 2801, 108601, 0, 1, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 2802, 108601, 0, 1, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2803, 108901, 0, 1, "着物も慣れると\n動きやすいものさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2804, 108901, 0, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2805, 108901, 0, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2806, 108901, 0, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2807, 108901, 0, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2808, 108901, 0, 4, "これからもずっと\n仲間と、彼と\n一緒に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2809, 108901, 0, 8, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2810, 108901, 0, 1, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2811, 108901, 0, 1, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2812, 108901, 0, 1, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2813, 108901, 0, 1, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2814, 108901, 0, 4, "キミといると\n気持ちが段々と\n安らいでいくよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2815, 108901, 0, 8, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2816, 108901, 0, 1, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2817, 108901, 0, 1, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2818, 108901, 0, 1, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2820, 108901, 0, 1, "…疲れていたのかな\nもう少しで眠る\nところだった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2821, 108901, 0, 1, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2822, 108901, 0, 1, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2823, 108901, 0, 1, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2825, 108901, 0, 1, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2826, 108901, 0, 1, "外を出歩くのも\nいいけど、中で\n読書もありだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2828, 108801, 0, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2829, 108801, 0, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2830, 108801, 0, 1, "今年は\nどんな福袋を\n買おうかな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2831, 108801, 0, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2832, 108801, 0, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2833, 108801, 0, 4, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2834, 108801, 0, 8, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2835, 108801, 0, 1, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2836, 108801, 0, 1, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2837, 108801, 0, 1, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2838, 108801, 0, 1, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2839, 108801, 0, 4, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2840, 108801, 0, 8, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2841, 108801, 0, 1, "おはよう\n太陽の光に当たると\n元気が出るよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2842, 108801, 0, 1, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2843, 108801, 0, 1, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2845, 108801, 0, 1, "ふぅ…\n帯も少し\nゆるめちゃおう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2846, 108801, 0, 1, "お正月用に\n着物を縫って\nあげたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2847, 108801, 0, 1, "これでお肌が\n保湿出来たら\nいいのになぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2848, 108801, 0, 1, "ん～…\nぬくぬくしてて\n動きたくないよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2850, 108801, 0, 1, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2851, 108801, 0, 1, "年末年始は\n告白のチャンス…\nむ、無理無理！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2853, 108701, 0, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2854, 108701, 0, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2855, 108701, 0, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2856, 108701, 0, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2857, 108701, 0, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2858, 108701, 0, 4, "うーん…\n騎士くんはお正月\n何してるんだろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2859, 108701, 0, 8, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2860, 108701, 0, 1, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2861, 108701, 0, 1, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2862, 108701, 0, 1, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2863, 108701, 0, 1, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2864, 108701, 0, 4, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2865, 108701, 0, 8, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2866, 108701, 0, 1, "おはよー！\n今日も明るく元気に\nがんばろー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2867, 108701, 0, 1, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2868, 108701, 0, 1, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2870, 108701, 0, 1, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2871, 108701, 0, 1, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2872, 108701, 0, 1, "はぁー♪\nいい香りー…\n気分スッキリ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2873, 108701, 0, 1, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2875, 108701, 0, 1, "ど、どうしよう…！\n手が、止まんないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2876, 108701, 0, 1, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2878, 109001, 0, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2879, 109001, 0, 1, "ここを私のチョコで\n埋め尽くしますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2880, 109001, 0, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2881, 109001, 0, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2882, 109001, 0, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2883, 109001, 0, 4, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2884, 109001, 0, 8, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2885, 109001, 0, 1, "あなた様。私に\n隠しているものなど\nありませんよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2886, 109001, 0, 1, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2887, 109001, 0, 1, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2888, 109001, 0, 1, "解毒薬はどこに\nいれたかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2889, 109001, 0, 4, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2890, 109001, 0, 8, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2891, 109001, 0, 1, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2892, 109001, 0, 1, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2893, 109001, 0, 1, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2895, 109001, 0, 1, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2896, 109001, 0, 1, "この中、何か\n入りそうですわね\n…チョコレート、とか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2897, 109001, 0, 1, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2898, 109001, 0, 1, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2900, 109001, 0, 1, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2901, 109001, 0, 1, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2903, 109101, 0, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2904, 109101, 0, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2905, 109101, 0, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2906, 109101, 0, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2907, 109101, 0, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2908, 109101, 0, 4, "お姉ちゃんのチョコ\n食べさせてあげたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2909, 109101, 0, 8, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2910, 109101, 0, 1, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2911, 109101, 0, 1, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2912, 109101, 0, 1, "ナデナデするのは\nお姉ちゃんの仕事！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2913, 109101, 0, 1, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2914, 109101, 0, 4, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2915, 109101, 0, 8, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2916, 109101, 0, 1, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2917, 109101, 0, 1, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2918, 109101, 0, 1, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2920, 109101, 0, 1, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2921, 109101, 0, 1, "このかわいさは\nお姉ちゃんも大満足♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2922, 109101, 0, 1, "この香り、チョコに\n使えるかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2923, 109101, 0, 1, "弟くんと一緒に\nぬくぬくしたいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2925, 109101, 0, 1, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2926, 109101, 0, 1, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2928, 106101, 0, 1, "アタシって…\nここにいても\nいいのか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2929, 106101, 0, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2930, 106101, 0, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2931, 106101, 0, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2932, 106101, 0, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2933, 106101, 0, 4, "みんなが早く\nアタシのこと\n思い出さないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2934, 106101, 0, 8, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2935, 106101, 0, 1, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2936, 106101, 0, 1, "どうした？\n戦うのか！？\n…なんだ、違うのか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2937, 106101, 0, 1, "身長のことは\nあんまり言うな！\n気にしてるからな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2938, 106101, 0, 1, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2939, 106101, 0, 4, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2940, 106101, 0, 8, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2941, 106101, 0, 1, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2942, 106101, 0, 1, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2943, 106101, 0, 1, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2945, 106101, 0, 1, "ふぃ～…\n極楽極楽…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2946, 106101, 0, 1, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2947, 106101, 0, 1, "くんくん…\nたまにはこういうのも\n悪くないかもな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2948, 106101, 0, 1, "あー…\nもっと前にコイツと\n出会いたかった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2950, 106101, 0, 1, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2951, 106101, 0, 1, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2953, 101401, 0, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2954, 101401, 0, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2955, 101401, 0, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2956, 101401, 0, 1, "この前の\n捜査資料…\nどこにあったかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2957, 101401, 0, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2958, 101401, 0, 4, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2959, 101401, 0, 8, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2960, 101401, 0, 1, "珈琲、淹れたてだよ\n一杯どうだい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2961, 101401, 0, 1, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2962, 101401, 0, 1, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2963, 101401, 0, 1, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2964, 101401, 0, 4, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2965, 101401, 0, 8, "助手くんは\n私にとって\n最高の相棒だよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2966, 101401, 0, 1, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2967, 101401, 0, 1, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2968, 101401, 0, 1, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2970, 101401, 0, 1, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2971, 101401, 0, 1, "この愛らしさ…\n要捜査対象だね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2972, 101401, 0, 1, "思考が活性化される\n素敵な香りだね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2973, 101401, 0, 1, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 2975, 101401, 0, 1, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 2976, 101401, 0, 1, "探偵だからね\nミステリには\n一家言あるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 2978, 105501, 0, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 2979, 105501, 0, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 2980, 105501, 0, 1, "先輩を観察しやすい\n場所はあるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 2981, 105501, 0, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 2982, 105501, 0, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 2983, 105501, 0, 4, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 2984, 105501, 0, 8, "せ、先輩になら\n観察されてもいいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2985, 105501, 0, 1, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2986, 105501, 0, 1, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2987, 105501, 0, 1, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 2988, 105501, 0, 1, "ま、また…\n先輩と一緒に\nバイトしたい…です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 2989, 105501, 0, 4, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 2990, 105501, 0, 8, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 2991, 105501, 0, 1, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 2992, 105501, 0, 1, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 2993, 105501, 0, 1, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 2995, 105501, 0, 1, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 2996, 105501, 0, 1, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 2997, 105501, 0, 1, "ここから先輩の\n香りがすればなあ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 2998, 105501, 0, 1, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3000, 105501, 0, 1, "おいし～\nこれは\n先輩も好きな味かな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3001, 105501, 0, 1, "す、好きな人に\n近づく方法…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3003, 109201, 0, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3004, 109201, 0, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3005, 109201, 0, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3006, 109201, 0, 1, "わー！\nあれもこれも\n気になっちゃうな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3007, 109201, 0, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3008, 109201, 0, 4, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3009, 109201, 0, 8, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3010, 109201, 0, 1, "歩き方がきれい？\nえへへ、そうかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3011, 109201, 0, 1, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3012, 109201, 0, 1, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3013, 109201, 0, 1, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3014, 109201, 0, 4, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3015, 109201, 0, 8, "お茶の時間に\nしたいな～…\n一緒にどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3016, 109201, 0, 1, "おはようっ！\n今日も素敵な\n一日にしようね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3017, 109201, 0, 1, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3018, 109201, 0, 1, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3020, 109201, 0, 1, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3021, 109201, 0, 1, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3022, 109201, 0, 1, "いい香り～！\nなんだか気持ちが\n落ち着くね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3023, 109201, 0, 1, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3025, 109201, 0, 1, "こ、これ美味し～！\n止められないよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3026, 109201, 0, 1, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3028, 109301, 0, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3029, 109301, 0, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3030, 109301, 0, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3031, 109301, 0, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3032, 109301, 0, 1, "なにか面白そうなもの\nないですかね～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3033, 109301, 0, 4, "ここのみんなとも\nもうお友だちですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3034, 109301, 0, 8, "おなかが減ったです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3035, 109301, 0, 1, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3036, 109301, 0, 1, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3037, 109301, 0, 1, "あなたもお勉強\n苦手ですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3038, 109301, 0, 1, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3039, 109301, 0, 4, "お友だちいっぱいで、\nルゥはうれしいです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3040, 109301, 0, 8, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3041, 109301, 0, 1, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3042, 109301, 0, 1, "午後もいっぱい\n遊ぶですよ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3043, 109301, 0, 1, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3045, 109301, 0, 1, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3046, 109301, 0, 1, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3047, 109301, 0, 1, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3048, 109301, 0, 1, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3050, 109301, 0, 1, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3051, 109301, 0, 1, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3053, 109401, 0, 1, "ピアノ…\nどこかにないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3054, 109401, 0, 1, "ふぅ…\n落ち着くな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3055, 109401, 0, 1, "マナリアはいつも\n賑やかなんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3056, 109401, 0, 1, "みんな…\n今頃何してるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3057, 109401, 0, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3058, 109401, 0, 4, "人と話すのは\n得意じゃないな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3059, 109401, 0, 8, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3060, 109401, 0, 1, "やっぱり、私の翼や\n尻尾…気になる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3061, 109401, 0, 1, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3062, 109401, 0, 1, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3063, 109401, 0, 1, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3064, 109401, 0, 4, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3065, 109401, 0, 8, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3066, 109401, 0, 1, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3067, 109401, 0, 1, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3068, 109401, 0, 1, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3070, 109401, 0, 1, "羽を伸ばしたく\nなっちゃうね…ふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3071, 109401, 0, 1, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3072, 109401, 0, 1, "いい香り…\nこれ、\nアンも好きそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3073, 109401, 0, 1, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3075, 109401, 0, 1, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3076, 109401, 0, 1, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3078, 109501, 0, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3079, 109501, 0, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3080, 109501, 0, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3081, 109501, 0, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3082, 109501, 0, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3083, 109501, 0, 4, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3084, 109501, 0, 8, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3085, 109501, 0, 1, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3086, 109501, 0, 1, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3087, 109501, 0, 1, "こんな場所で\nなんというプレイを！\n…妄想でした", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3088, 109501, 0, 1, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3089, 109501, 0, 4, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3090, 109501, 0, 8, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3091, 109501, 0, 1, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3092, 109501, 0, 1, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3093, 109501, 0, 1, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3095, 109501, 0, 1, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3096, 109501, 0, 1, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3097, 109501, 0, 1, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3098, 109501, 0, 1, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3100, 109501, 0, 1, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3101, 109501, 0, 1, "クウカの探している\nジャンルはどこに\nありますかね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3103, 109601, 0, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3104, 109601, 0, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3105, 109601, 0, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3106, 109601, 0, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3107, 109601, 0, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3108, 109601, 0, 4, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3109, 109601, 0, 8, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3110, 109601, 0, 1, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3111, 109601, 0, 1, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3112, 109601, 0, 1, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3113, 109601, 0, 1, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3114, 109601, 0, 4, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3115, 109601, 0, 8, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3116, 109601, 0, 1, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3117, 109601, 0, 1, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3118, 109601, 0, 1, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3120, 109601, 0, 1, "天下分け目の\n戦いに備えて…\nマッタリデース", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3121, 109601, 0, 1, "口寄せの術で\n召喚したいくらい\nかわいいデース", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3122, 109601, 0, 1, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3123, 109601, 0, 1, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3125, 109601, 0, 1, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3126, 109601, 0, 1, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3128, 109701, 0, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3129, 109701, 0, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3130, 109701, 0, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3131, 109701, 0, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3132, 109701, 0, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3133, 109701, 0, 4, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3134, 109701, 0, 8, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3135, 109701, 0, 1, "お出かけでしたら、\nレムも同行\nさせてくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3136, 109701, 0, 1, "レムにできること\nでしたら、なんでも\nお申し付けください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3137, 109701, 0, 1, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3138, 109701, 0, 1, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3139, 109701, 0, 4, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3140, 109701, 0, 8, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3141, 109701, 0, 1, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3142, 109701, 0, 1, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3143, 109701, 0, 1, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3145, 109701, 0, 1, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3146, 109701, 0, 1, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3147, 109701, 0, 1, "よい香りですね\n魔女教の悪臭とは\n大違いです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3148, 109701, 0, 1, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3150, 109701, 0, 1, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3151, 109701, 0, 1, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3153, 109801, 0, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3154, 109801, 0, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3155, 109801, 0, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3156, 109801, 0, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3157, 109801, 0, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3158, 109801, 0, 4, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3159, 109801, 0, 8, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3160, 109801, 0, 1, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3161, 109801, 0, 1, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3162, 109801, 0, 1, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3163, 109801, 0, 1, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3164, 109801, 0, 4, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3165, 109801, 0, 8, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3166, 109801, 0, 1, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3167, 109801, 0, 1, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3168, 109801, 0, 1, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3170, 109801, 0, 1, "…ここから動かない\nという、誓いを\n立てたくなるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3171, 109801, 0, 1, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3172, 109801, 0, 1, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3173, 109801, 0, 1, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3175, 109801, 0, 1, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3176, 109801, 0, 1, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3178, 109901, 0, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3179, 109901, 0, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3180, 109901, 0, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3181, 109901, 0, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3182, 109901, 0, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3183, 109901, 0, 4, "また、みんなで\n一緒に買い物に\n行きたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3184, 109901, 0, 8, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3185, 109901, 0, 1, "今からパックの\n毛繕いをするけど…\n一緒にする？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3186, 109901, 0, 1, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3187, 109901, 0, 1, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3188, 109901, 0, 1, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3189, 109901, 0, 4, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3190, 109901, 0, 8, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3191, 109901, 0, 1, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3192, 109901, 0, 1, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3193, 109901, 0, 1, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3195, 109901, 0, 1, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3196, 109901, 0, 1, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3197, 109901, 0, 1, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3198, 109901, 0, 1, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3200, 109901, 0, 1, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3201, 109901, 0, 1, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3203, 110001, 0, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3204, 110001, 0, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3205, 110001, 0, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3206, 110001, 0, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3207, 110001, 0, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3208, 110001, 0, 4, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3209, 110001, 0, 8, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3210, 110001, 0, 1, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3211, 110001, 0, 1, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3212, 110001, 0, 1, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3213, 110001, 0, 1, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3214, 110001, 0, 4, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3215, 110001, 0, 8, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3216, 110001, 0, 1, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3217, 110001, 0, 1, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3218, 110001, 0, 1, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3220, 110001, 0, 1, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3221, 110001, 0, 1, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3222, 110001, 0, 1, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3223, 110001, 0, 1, "あったか～い♪\n夏の太陽みたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3225, 110001, 0, 1, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3226, 110001, 0, 1, "夏のために\n最新ファッションを\n勉強ちう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3228, 110001, 0, 1, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3229, 110001, 0, 1, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3230, 110101, 0, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3231, 110101, 0, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3232, 110101, 0, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3233, 110101, 0, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3234, 110101, 0, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3235, 110101, 0, 4, "海が楽しいって\n思えたのも\nあの子のおかげね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3236, 110101, 0, 8, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3237, 110101, 0, 1, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3238, 110101, 0, 1, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3239, 110101, 0, 1, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3240, 110101, 0, 1, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3241, 110101, 0, 4, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3242, 110101, 0, 8, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3243, 110101, 0, 1, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3244, 110101, 0, 1, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3245, 110101, 0, 1, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3247, 110101, 0, 1, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3248, 110101, 0, 1, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3249, 110101, 0, 1, "この匂い…\nなんだか夏の海を\n思い出すわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3250, 110101, 0, 1, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3252, 110101, 0, 1, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3253, 110101, 0, 1, "夏休み中でも\nしっかりお勉強\nしないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3255, 110101, 0, 1, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3256, 110101, 0, 1, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3257, 110301, 0, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3258, 110301, 0, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3259, 110301, 0, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3260, 110301, 0, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3261, 110301, 0, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3262, 110301, 0, 4, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3263, 110301, 0, 8, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3264, 110301, 0, 1, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3265, 110301, 0, 1, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3266, 110301, 0, 1, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3267, 110301, 0, 1, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3268, 110301, 0, 4, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3269, 110301, 0, 8, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3270, 110301, 0, 1, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3271, 110301, 0, 1, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3272, 110301, 0, 1, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3274, 110301, 0, 1, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3275, 110301, 0, 1, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3276, 110301, 0, 1, "夏らしい\nいい香りね…\nなんの香りかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3277, 110301, 0, 1, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3279, 110301, 0, 1, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3280, 110301, 0, 1, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3282, 110301, 0, 1, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3283, 110301, 0, 1, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3284, 110401, 0, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3285, 110401, 0, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3286, 110401, 0, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3287, 110401, 0, 1, "夏ならではの\n料理…か\n何がいいかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3288, 110401, 0, 1, "やっぱりここは\n涼しくて快適だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3289, 110401, 0, 4, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3290, 110401, 0, 8, "水着で部屋の中に\nいるのって\n変な感じだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3291, 110401, 0, 1, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3292, 110401, 0, 1, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3293, 110401, 0, 1, "夏バテしてないか？\nここで休んでけよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3294, 110401, 0, 1, "…そう見られると\n照れるっていうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3295, 110401, 0, 4, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3296, 110401, 0, 8, "おまえと\n夏の思い出ができて\nよかったよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3297, 110401, 0, 1, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3298, 110401, 0, 1, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3299, 110401, 0, 1, "今日もお疲れ\n明日のパトロールも\nよろしくな", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3301, 110401, 0, 1, "泳ぎ疲れたからな\n少し休憩だ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3302, 110401, 0, 1, "か、かわいい…\nいや、なんでもねぇ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3303, 110401, 0, 1, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3304, 110401, 0, 1, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3306, 110401, 0, 1, "海で食べると\nまた一味違うよな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3307, 110401, 0, 1, "夏のサバイバル特集…\nちょっと読んでみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3309, 110401, 0, 1, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3310, 110401, 0, 1, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3311, 110501, 0, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3312, 110501, 0, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3313, 110501, 0, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3314, 110501, 0, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3315, 110501, 0, 1, "クロールも\n犬かきも\n大得意さ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3316, 110501, 0, 4, "なんだか歌いたい\n気分さ～\nキミもどう～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3317, 110501, 0, 8, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3318, 110501, 0, 1, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3319, 110501, 0, 1, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3320, 110501, 0, 1, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3321, 110501, 0, 1, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3322, 110501, 0, 4, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3323, 110501, 0, 8, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3324, 110501, 0, 1, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3325, 110501, 0, 1, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3326, 110501, 0, 1, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3328, 110501, 0, 1, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3329, 110501, 0, 1, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3330, 110501, 0, 1, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3331, 110501, 0, 1, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3333, 110501, 0, 1, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3334, 110501, 0, 1, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3336, 110501, 0, 1, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3337, 110501, 0, 1, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3338, 105801, 0, 12, "あの人が\nわたしの騎士だったら\nいいのにな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3339, 105801, 0, 12, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3340, 105901, 0, 12, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3341, 105901, 0, 12, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3342, 106001, 0, 12, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3343, 106001, 0, 12, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3344, 110601, 0, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3345, 110601, 0, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3346, 110601, 0, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3347, 110601, 0, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3348, 110601, 0, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3349, 110601, 0, 4, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3350, 110601, 0, 8, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3351, 110601, 0, 1, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3352, 110601, 0, 1, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3353, 110601, 0, 1, "日が落ちたら\n線香花火\nやりたいわぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3354, 110601, 0, 1, "木陰でのんびり\nお昼寝…\n最高やね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3355, 110601, 0, 4, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3356, 110601, 0, 8, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3357, 110601, 0, 1, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3358, 110601, 0, 1, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3359, 110601, 0, 1, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3361, 110601, 0, 1, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3362, 110601, 0, 1, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3363, 110601, 0, 1, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3364, 110601, 0, 1, "これで\n冷えた身体も\nポカポカやねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3366, 110601, 0, 1, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3367, 110601, 0, 1, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 3369, 110601, 0, 1, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 3370, 110601, 0, 1, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3371, 107001, 0, 1, "真似したくなる\n人がここには\n大勢いますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3372, 107001, 0, 1, "偽物を一人\n紛れこませました\n…冗談です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3373, 107001, 0, 1, "動物に変身して\nその辺りを\n散歩してきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3374, 107001, 0, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3375, 107001, 0, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3376, 107001, 0, 4, "次は彼に\nどんなことを\n教えましょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3377, 107001, 0, 8, "知識はともかく\n私の力はまだ\n必要なようですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3378, 107001, 0, 1, "どうしました？\n…何でもない？\n…そうですか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3379, 107001, 0, 1, "退屈しています\n芸でもして\n楽しませなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3380, 107001, 0, 1, "研究のためです\n少し話し相手に\nなりなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3381, 107001, 0, 1, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3382, 107001, 0, 4, "貴方にはまだ色々と\n教えるべきことが\nありますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3383, 107001, 0, 8, "随分多くの女性と\n知り合いなのですね\n驚きました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3384, 107001, 0, 1, "…\nおはようございます\n…まだ朝ですよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3385, 107001, 0, 1, "食事にします\n…どうしました？\n早く持ってきなさい", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3386, 107001, 0, 1, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3388, 107001, 0, 1, "マサキより\n座り心地は\nいいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3389, 107001, 0, 1, "意外に複雑な\n構造ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3390, 107001, 0, 1, "真似るなら\n香りまで\n再現しなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3391, 107001, 0, 1, "発火現象の\n模倣…なかなか\n難しいものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3393, 107001, 0, 1, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3394, 107001, 0, 1, "暇つぶしには\nちょうどよさそうです\n…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3396, 110701, 0, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3397, 110701, 0, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3398, 110701, 0, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3399, 110701, 0, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3400, 110701, 0, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3401, 110701, 0, 4, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3402, 110701, 0, 8, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3403, 110701, 0, 1, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3404, 110701, 0, 1, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3405, 110701, 0, 1, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3406, 110701, 0, 1, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3407, 110701, 0, 4, "学院でも、\nBB団の活動を\nしていきましょうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3408, 110701, 0, 8, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3409, 110701, 0, 1, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3410, 110701, 0, 1, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3411, 110701, 0, 1, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3413, 110701, 0, 1, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3414, 110701, 0, 1, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3415, 110701, 0, 1, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3416, 110701, 0, 1, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3418, 110701, 0, 1, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3419, 110701, 0, 1, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3421, 110801, 0, 1, "あんた、何してんの。\nいいけど、なんでも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3422, 110801, 0, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3423, 110801, 0, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3424, 110801, 0, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3425, 110801, 0, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3426, 110801, 0, 4, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3427, 110801, 0, 8, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3428, 110801, 0, 1, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3429, 110801, 0, 1, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3430, 110801, 0, 1, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3431, 110801, 0, 1, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3432, 110801, 0, 4, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3433, 110801, 0, 8, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3434, 110801, 0, 1, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3435, 110801, 0, 1, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3436, 110801, 0, 1, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3438, 110801, 0, 1, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3439, 110801, 0, 1, "なんだこれ\nかわいいかよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3440, 110801, 0, 1, "ふーん。\nこの匂い…\nなんかいいじゃん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3441, 110801, 0, 1, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3443, 110801, 0, 1, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3444, 110801, 0, 1, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3446, 111101, 0, 1, "イタズラってなにを\nしたらいいのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3447, 111101, 0, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3448, 111101, 0, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3449, 111101, 0, 1, "オバケ…いないよね？\n怖くなんてないけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3450, 111101, 0, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3451, 111101, 0, 4, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3452, 111101, 0, 8, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3453, 111101, 0, 1, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3454, 111101, 0, 1, "触りましたね？\nイタズラしましたね？\nへんた～い！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3455, 111101, 0, 1, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3456, 111101, 0, 1, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3457, 111101, 0, 4, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3458, 111101, 0, 8, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3459, 111101, 0, 1, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3460, 111101, 0, 1, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3461, 111101, 0, 1, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3463, 111101, 0, 1, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3464, 111101, 0, 1, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3465, 111101, 0, 1, "甘い匂いがいっぱい…\nお菓子の香りかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3466, 111101, 0, 1, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3468, 111101, 0, 1, "ニンジンもこれくらい\nおいしければ\nいいのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3469, 111101, 0, 1, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 3471, 111101, 0, 1, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3473, 111201, 0, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3474, 111201, 0, 1, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3475, 111201, 0, 1, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3476, 111201, 0, 1, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3477, 111201, 0, 1, "どうすればみんなを\nびっくりさせられる\nかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3478, 111201, 0, 4, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3479, 111201, 0, 8, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3480, 111201, 0, 1, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3481, 111201, 0, 1, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3482, 111201, 0, 1, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3483, 111201, 0, 1, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3484, 111201, 0, 4, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3485, 111201, 0, 8, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3486, 111201, 0, 1, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3487, 111201, 0, 1, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3488, 111201, 0, 1, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3490, 111201, 0, 1, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3491, 111201, 0, 1, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3492, 111201, 0, 1, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3493, 111201, 0, 1, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3495, 111201, 0, 1, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3496, 111201, 0, 1, "本のなかに\nいたずらのヒント\nないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 3498, 111201, 0, 1, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3500, 101101, 0, 12, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3501, 101101, 0, 12, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3502, 111301, 0, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3503, 111301, 0, 1, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3504, 111301, 0, 1, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3505, 111301, 0, 1, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3506, 111301, 0, 1, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3507, 111301, 0, 4, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3508, 111301, 0, 8, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3509, 111301, 0, 1, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3510, 111301, 0, 1, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3511, 111301, 0, 1, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3512, 111301, 0, 1, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3513, 111301, 0, 4, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3514, 111301, 0, 8, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3515, 111301, 0, 1, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3516, 111301, 0, 1, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3517, 111301, 0, 1, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3519, 111301, 0, 1, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3520, 111301, 0, 1, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3521, 111301, 0, 1, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3522, 111301, 0, 1, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3524, 111301, 0, 1, "おいしい～♪\nおにいちゃんも\n食べようよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3525, 111301, 0, 1, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 3527, 111301, 0, 1, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3529, 111401, 0, 1, "ここ、好きだよ。\nみんな優しいから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3530, 111401, 0, 1, "なんかさみしいな", 0);
