INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3548, 111401, 0, 1, "なんかいい匂い！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3549, 111401, 0, 1, "あったかいねー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3551, 111401, 0, 1, "ふふっ\nおいしいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3552, 111401, 0, 1, "ルナ、\n眠くなっちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3554, 105201, 0, 12, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3555, 105201, 0, 12, "あなたとなら\nどんなことだって\nできる気がするわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3556, 106501, 0, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3557, 106501, 0, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3558, 106501, 0, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3559, 106501, 0, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3560, 106501, 0, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3561, 106501, 0, 4, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3562, 106501, 0, 8, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3563, 106501, 0, 1, "よっす！\nなんかオイシイ話でも\nあったりしない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3564, 106501, 0, 1, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3565, 106501, 0, 1, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3566, 106501, 0, 1, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3567, 106501, 0, 4, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3568, 106501, 0, 8, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3569, 106501, 0, 1, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3570, 106501, 0, 1, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3571, 106501, 0, 1, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3573, 106501, 0, 1, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3574, 106501, 0, 1, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3575, 106501, 0, 1, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3576, 106501, 0, 1, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3578, 106501, 0, 1, "いつでも全力で\nケンカするために\n力を蓄えないとな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3579, 106501, 0, 1, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3581, 111501, 0, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3582, 111501, 0, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3583, 111501, 0, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3584, 111501, 0, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3585, 111501, 0, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3586, 111501, 0, 4, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3587, 111501, 0, 8, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3588, 111501, 0, 1, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3589, 111501, 0, 1, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3590, 111501, 0, 1, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3591, 111501, 0, 1, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3592, 111501, 0, 4, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3593, 111501, 0, 8, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3594, 111501, 0, 1, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3595, 111501, 0, 1, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3596, 111501, 0, 1, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3598, 111501, 0, 1, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3599, 111501, 0, 1, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3600, 111501, 0, 1, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3601, 111501, 0, 1, "体が温まってくると\nひと暴れしたくなるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3603, 111501, 0, 1, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3604, 111501, 0, 1, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 3606, 111501, 0, 1, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 3607, 111501, 0, 1, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3608, 111601, 0, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3609, 111601, 0, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3610, 111601, 0, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3611, 111601, 0, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3612, 111601, 0, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3613, 111601, 0, 4, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3614, 111601, 0, 8, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3615, 111601, 0, 1, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3616, 111601, 0, 1, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3617, 111601, 0, 1, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3618, 111601, 0, 1, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3619, 111601, 0, 4, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3620, 111601, 0, 8, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3621, 111601, 0, 1, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3622, 111601, 0, 1, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3623, 111601, 0, 1, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3625, 111601, 0, 1, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3626, 111601, 0, 1, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3627, 111601, 0, 1, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3628, 111601, 0, 1, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3630, 111601, 0, 1, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3631, 111601, 0, 1, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 3633, 111601, 0, 1, "面白ーい！\nこの演出、ライブでも\nできないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 3634, 111601, 0, 1, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3635, 101801, 0, 12, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3636, 101801, 0, 12, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3637, 111701, 0, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3638, 111701, 0, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3639, 111701, 0, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3640, 111701, 0, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3641, 111701, 0, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3642, 111701, 0, 4, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3643, 111701, 0, 8, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3644, 111701, 0, 1, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3645, 111701, 0, 1, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3646, 111701, 0, 1, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3647, 111701, 0, 1, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3648, 111701, 0, 4, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3649, 111701, 0, 8, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3650, 111701, 0, 1, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3651, 111701, 0, 1, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3652, 111701, 0, 1, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3654, 111701, 0, 1, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3655, 111701, 0, 1, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3656, 111701, 0, 1, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3657, 111701, 0, 1, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3659, 111701, 0, 1, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3660, 111701, 0, 1, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 3662, 111701, 0, 1, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 3663, 111701, 0, 1, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3664, 112001, 0, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3665, 112001, 0, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3666, 112001, 0, 1, "初日の出って\n特別な感じがして\nいいわよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3667, 112001, 0, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3668, 112001, 0, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3669, 112001, 0, 4, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3670, 112001, 0, 8, "今年もあいつと\n居られること…\n結構うれしいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3671, 112001, 0, 1, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3672, 112001, 0, 1, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3673, 112001, 0, 1, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3674, 112001, 0, 1, "門松飾るから\nちょっと手伝って\nくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3675, 112001, 0, 4, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3676, 112001, 0, 8, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3677, 112001, 0, 1, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3678, 112001, 0, 1, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3679, 112001, 0, 1, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3681, 112001, 0, 1, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3682, 112001, 0, 1, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3683, 112001, 0, 1, "いい匂いね\n落ち着いて\n眠れそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3684, 112001, 0, 1, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3686, 112001, 0, 1, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3687, 112001, 0, 1, "魔物を使わない\nおせちを\n調べておかなきゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3689, 112101, 0, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3690, 112101, 0, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3691, 112101, 0, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3692, 112101, 0, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3693, 112101, 0, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3694, 112101, 0, 4, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3695, 112101, 0, 8, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3696, 112101, 0, 1, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3697, 112101, 0, 1, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3698, 112101, 0, 1, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3699, 112101, 0, 1, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3700, 112101, 0, 4, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3701, 112101, 0, 8, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3702, 112101, 0, 1, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3703, 112101, 0, 1, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3704, 112101, 0, 1, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3706, 112101, 0, 1, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3707, 112101, 0, 1, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3708, 112101, 0, 1, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3709, 112101, 0, 1, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3711, 112101, 0, 1, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3712, 112101, 0, 1, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3714, 103401, 0, 12, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3715, 103401, 0, 12, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3716, 111901, 0, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3717, 111901, 0, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3718, 111901, 0, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3719, 111901, 0, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3720, 111901, 0, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3721, 111901, 0, 4, "主さまの温もりは\n心まで温めてくれる\nようです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3722, 111901, 0, 8, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3723, 111901, 0, 1, "主さま、この晴れ着は\n似合っている\nでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3724, 111901, 0, 1, "お餅はゆっくりと\n召し上がって\nくださいまし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3725, 111901, 0, 1, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3726, 111901, 0, 1, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3727, 111901, 0, 4, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3728, 111901, 0, 8, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3729, 111901, 0, 1, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3730, 111901, 0, 1, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3731, 111901, 0, 1, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3733, 111901, 0, 1, "ふかふか…\nこのまま\n寝てしまいそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3734, 111901, 0, 1, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3735, 111901, 0, 1, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3736, 111901, 0, 1, "風邪をひきませんよう\n暖かくいたしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3738, 111901, 0, 1, "食べ過ぎは\n正月太りの\nもとだというのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3739, 111901, 0, 1, "主さまのために\n参拝の作法を\n予習しておきましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3741, 112201, 0, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3742, 112201, 0, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3743, 112201, 0, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3744, 112201, 0, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3745, 112201, 0, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3746, 112201, 0, 4, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3747, 112201, 0, 8, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3748, 112201, 0, 1, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3749, 112201, 0, 1, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3750, 112201, 0, 1, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3751, 112201, 0, 1, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3752, 112201, 0, 4, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3753, 112201, 0, 8, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3754, 112201, 0, 1, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3755, 112201, 0, 1, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3756, 112201, 0, 1, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3758, 112201, 0, 1, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3759, 112201, 0, 1, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3760, 112201, 0, 1, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3761, 112201, 0, 1, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3763, 112201, 0, 1, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3764, 112201, 0, 1, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3766, 112301, 0, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3767, 112301, 0, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3768, 112301, 0, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3769, 112301, 0, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3770, 112301, 0, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3771, 112301, 0, 4, "あの人がいると\nなんだかホッとする\n不思議…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3772, 112301, 0, 8, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3773, 112301, 0, 1, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3774, 112301, 0, 1, "よかったら魔法の\n練習に付き合って\nくれませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3775, 112301, 0, 1, "あの…\nこの格好どうですか？\n似合ってると…いいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3776, 112301, 0, 1, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3777, 112301, 0, 4, "私、あなたのそばに\nいられたら、それで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3778, 112301, 0, 8, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3779, 112301, 0, 1, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3780, 112301, 0, 1, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3781, 112301, 0, 1, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3783, 112301, 0, 1, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3784, 112301, 0, 1, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3785, 112301, 0, 1, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3786, 112301, 0, 1, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3788, 112301, 0, 1, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3789, 112301, 0, 1, "ふふ…\n魔法少女になっても\n読書はやめられないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3791, 101001, 0, 12, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3792, 101001, 0, 12, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3793, 180401, 0, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3794, 180401, 0, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3795, 180401, 0, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3796, 180401, 0, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3797, 180401, 0, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3798, 180401, 0, 4, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3799, 180401, 0, 8, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3800, 180401, 0, 1, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3801, 180401, 0, 1, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3802, 180401, 0, 1, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3803, 180401, 0, 1, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3804, 180401, 0, 4, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3805, 180401, 0, 8, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3806, 180401, 0, 1, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3807, 180401, 0, 1, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3808, 180401, 0, 1, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3810, 180401, 0, 1, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3811, 180401, 0, 1, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3812, 180401, 0, 1, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3813, 180401, 0, 1, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3815, 180401, 0, 1, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3816, 180401, 0, 1, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3818, 112401, 0, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3819, 112401, 0, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3820, 112401, 0, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3821, 112401, 0, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3822, 112401, 0, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3823, 112401, 0, 4, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3824, 112401, 0, 8, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3825, 112401, 0, 1, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3826, 112401, 0, 1, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3827, 112401, 0, 1, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3828, 112401, 0, 1, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3829, 112401, 0, 4, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3830, 112401, 0, 8, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3831, 112401, 0, 1, "おはようございます！\n今日も一日\n頑張りましょうね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3832, 112401, 0, 1, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3833, 112401, 0, 1, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3835, 112401, 0, 1, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3836, 112401, 0, 1, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3837, 112401, 0, 1, "リラックスできる\nいい香りですね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3838, 112401, 0, 1, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3840, 112401, 0, 1, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3841, 112401, 0, 1, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3843, 112601, 0, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3844, 112601, 0, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3845, 112601, 0, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3846, 112601, 0, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3847, 112601, 0, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3848, 112601, 0, 4, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3849, 112601, 0, 8, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3850, 112601, 0, 1, "なになにー？\nミオちゃんに\n何か用かな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3851, 112601, 0, 1, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3852, 112601, 0, 1, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3853, 112601, 0, 1, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3854, 112601, 0, 4, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3855, 112601, 0, 8, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3856, 112601, 0, 1, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3857, 112601, 0, 1, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3858, 112601, 0, 1, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3860, 112601, 0, 1, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3861, 112601, 0, 1, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3862, 112601, 0, 1, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3863, 112601, 0, 1, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3865, 112601, 0, 1, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3866, 112601, 0, 1, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3868, 100101, 0, 12, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3869, 100101, 0, 12, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3870, 100201, 0, 12, "騎士クンの前で\n好きって言えるように\n練習しないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3871, 100201, 0, 12, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3872, 100301, 0, 12, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3873, 100301, 0, 12, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3874, 112501, 0, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3875, 112501, 0, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3876, 112501, 0, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3877, 112501, 0, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3878, 112501, 0, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3879, 112501, 0, 4, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3880, 112501, 0, 8, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3881, 112501, 0, 1, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3882, 112501, 0, 1, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3883, 112501, 0, 1, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3884, 112501, 0, 1, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3885, 112501, 0, 4, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3886, 112501, 0, 8, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3887, 112501, 0, 1, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3888, 112501, 0, 1, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3889, 112501, 0, 1, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3891, 112501, 0, 1, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3892, 112501, 0, 1, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3893, 112501, 0, 1, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3894, 112501, 0, 1, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3896, 112501, 0, 1, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3897, 112501, 0, 1, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3899, 111001, 0, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3900, 111001, 0, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3901, 111001, 0, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3902, 111001, 0, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3903, 111001, 0, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3904, 111001, 0, 4, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3905, 111001, 0, 8, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3906, 111001, 0, 1, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3907, 111001, 0, 1, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3908, 111001, 0, 1, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3909, 111001, 0, 1, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3910, 111001, 0, 4, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3911, 111001, 0, 8, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3912, 111001, 0, 1, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3913, 111001, 0, 1, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3914, 111001, 0, 1, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3916, 111001, 0, 1, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3917, 111001, 0, 1, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3918, 111001, 0, 1, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3919, 111001, 0, 1, "炎はいいぞ\nそこには人類発展の\nドラマがある", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3921, 111001, 0, 1, "フオオー！\n次から次へと固形食物\n原理気になるー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3922, 111001, 0, 1, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 3924, 101201, 0, 12, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 3925, 101201, 0, 12, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3933, 192101, 0, 1, "ちぇるーん☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3951, 110901, 0, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3952, 110901, 0, 1, "どこかに楽しいもの\n落ちてないかにゃー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3953, 110901, 0, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3954, 110901, 0, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3955, 110901, 0, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3956, 110901, 0, 4, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3957, 110901, 0, 8, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3958, 110901, 0, 1, "え、なんですか？\n告白だったら\n間に合ってますけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3959, 110901, 0, 1, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3960, 110901, 0, 1, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3961, 110901, 0, 1, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3962, 110901, 0, 4, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3963, 110901, 0, 8, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3964, 110901, 0, 1, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3965, 110901, 0, 1, "今日のランチは\nどこで\n映えよっかにゃー", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3966, 110901, 0, 1, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3968, 110901, 0, 1, "ふかふかチエルン\n略して、ふかルン♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3969, 110901, 0, 1, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3970, 110901, 0, 1, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3971, 110901, 0, 1, "マシュマロは\n焼かない方のが\nかわいくないです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3973, 110901, 0, 1, "チエル食べても\n太らない体質\nなんでぇ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3974, 110901, 0, 1, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 3976, 112701, 0, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 3977, 112701, 0, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 3978, 112701, 0, 1, "あたしの代わりに誰か\n働いてくれないかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 3979, 112701, 0, 1, "労働の後に食べる\nあんパン最高～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 3980, 112701, 0, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 3981, 112701, 0, 4, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 3982, 112701, 0, 8, "次の休みはあいつと\n一日中だらだら\nしたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3983, 112701, 0, 1, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3984, 112701, 0, 1, "お、差し入れ？\n気が利くねぇ\nあんがと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3985, 112701, 0, 1, "新しい依頼？\n今日はもう無理～\n閉店で～す", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 3986, 112701, 0, 1, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 3987, 112701, 0, 4, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 3988, 112701, 0, 8, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 3989, 112701, 0, 1, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 3990, 112701, 0, 1, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 3991, 112701, 0, 1, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 3993, 112701, 0, 1, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 3994, 112701, 0, 1, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 3995, 112701, 0, 1, "あんパンの香りの\nアロマとか\nあればいいのにな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 3996, 112701, 0, 1, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 3998, 112701, 0, 1, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 3999, 112701, 0, 1, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4001, 104601, 0, 12, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4002, 104601, 0, 12, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4003, 112801, 0, 1, "フロンティア\nスピリッツを\nお見せするべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4004, 112801, 0, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4005, 112801, 0, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4006, 112801, 0, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4007, 112801, 0, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4008, 112801, 0, 4, "みんなオラより\n若ぇのに働いてて\n立派だべな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4009, 112801, 0, 8, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4010, 112801, 0, 1, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4011, 112801, 0, 1, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4012, 112801, 0, 1, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4013, 112801, 0, 1, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4014, 112801, 0, 4, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4015, 112801, 0, 8, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4016, 112801, 0, 1, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4017, 112801, 0, 1, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4018, 112801, 0, 1, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4020, 112801, 0, 1, "牧草みたいに\nふっかふかだべ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4021, 112801, 0, 1, "この子、目の感じが\nエリザベスに似てるべ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4022, 112801, 0, 1, "いい匂いだべ～\n疲れた体に\nしみるべな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4023, 112801, 0, 1, "あったけぇな～\n牧場にも\n置いてみっかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4025, 112801, 0, 1, "うまいべ！\n生産者のこだわりを\n感じるべさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4026, 112801, 0, 1, "酪農も戦いも\nまずは知識だべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4028, 113001, 0, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4029, 113001, 0, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4030, 113001, 0, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4031, 113001, 0, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4032, 113001, 0, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4033, 113001, 0, 4, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4034, 113001, 0, 8, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4035, 113001, 0, 1, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4036, 113001, 0, 1, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4037, 113001, 0, 1, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4038, 113001, 0, 1, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4039, 113001, 0, 4, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4040, 113001, 0, 8, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4041, 113001, 0, 1, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4042, 113001, 0, 1, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4043, 113001, 0, 1, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4045, 113001, 0, 1, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4046, 113001, 0, 1, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4047, 113001, 0, 1, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4048, 113001, 0, 1, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4050, 113001, 0, 1, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4051, 113001, 0, 1, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4053, 180501, 0, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4054, 180501, 0, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4055, 180501, 0, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4056, 180501, 0, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4057, 180501, 0, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4058, 180501, 0, 4, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4059, 180501, 0, 8, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4060, 180501, 0, 1, "おでかけですか？\nではわたくしも\nお供いたします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4061, 180501, 0, 1, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4062, 180501, 0, 1, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4063, 180501, 0, 1, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4064, 180501, 0, 4, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4065, 180501, 0, 8, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4066, 180501, 0, 1, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4067, 180501, 0, 1, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4068, 180501, 0, 1, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4070, 180501, 0, 1, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4071, 180501, 0, 1, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4072, 180501, 0, 1, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4073, 180501, 0, 1, "主さまの心と\n繋がったときのような\n温もりを感じます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4075, 180501, 0, 1, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4076, 180501, 0, 1, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4078, 104801, 0, 12, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4079, 104801, 0, 12, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4080, 112901, 0, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4081, 112901, 0, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4082, 112901, 0, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4083, 112901, 0, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4084, 112901, 0, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4085, 112901, 0, 4, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4086, 112901, 0, 8, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4087, 112901, 0, 1, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4088, 112901, 0, 1, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4089, 112901, 0, 1, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4090, 112901, 0, 1, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4091, 112901, 0, 4, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4092, 112901, 0, 8, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4093, 112901, 0, 1, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4094, 112901, 0, 1, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4095, 112901, 0, 1, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4097, 112901, 0, 1, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4098, 112901, 0, 1, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4099, 112901, 0, 1, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4100, 112901, 0, 1, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4102, 112901, 0, 1, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4103, 112901, 0, 1, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4105, 106601, 0, 1, "退屈です…\nなにか楽しいことは\nないですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4106, 106601, 0, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4107, 106601, 0, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4108, 106601, 0, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4109, 106601, 0, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4110, 106601, 0, 4, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4111, 106601, 0, 8, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4112, 106601, 0, 1, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4113, 106601, 0, 1, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4114, 106601, 0, 1, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4115, 106601, 0, 1, "お兄さんは\nヒマそうですね…\nうらやましいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4116, 106601, 0, 4, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4117, 106601, 0, 8, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4118, 106601, 0, 1, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4119, 106601, 0, 1, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4120, 106601, 0, 1, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4122, 106601, 0, 1, "はぁ～…\nもうここから一歩も\n動きたくないです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4123, 106601, 0, 1, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4124, 106601, 0, 1, "なんかおしゃれな\n匂いがするですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4125, 106601, 0, 1, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4127, 106601, 0, 1, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4128, 106601, 0, 1, "ふーむ…\nもっと楽しい本は\nないですかね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4130, 113201, 0, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4131, 113201, 0, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4132, 113201, 0, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4133, 113201, 0, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4134, 113201, 0, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4135, 113201, 0, 4, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4136, 113201, 0, 8, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4137, 113201, 0, 1, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4138, 113201, 0, 1, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4139, 113201, 0, 1, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4140, 113201, 0, 1, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4141, 113201, 0, 4, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4142, 113201, 0, 8, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4143, 113201, 0, 1, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4144, 113201, 0, 1, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4145, 113201, 0, 1, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4147, 113201, 0, 1, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4148, 113201, 0, 1, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4149, 113201, 0, 1, "心地よい香りで\n我が精神も安らぐ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4150, 113201, 0, 1, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4152, 113201, 0, 1, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4153, 113201, 0, 1, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4155, 113201, 0, 1, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4156, 113201, 0, 1, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4157, 180201, 0, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4158, 180201, 0, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4159, 180201, 0, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4160, 180201, 0, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4161, 180201, 0, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4162, 180201, 0, 4, "騎士クン…\n今どこにいるの…？\n…会いたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4163, 180201, 0, 8, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4164, 180201, 0, 1, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4165, 180201, 0, 1, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4166, 180201, 0, 1, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4167, 180201, 0, 1, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4168, 180201, 0, 4, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4169, 180201, 0, 8, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4170, 180201, 0, 1, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4171, 180201, 0, 1, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4172, 180201, 0, 1, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4174, 180201, 0, 1, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4175, 180201, 0, 1, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4176, 180201, 0, 1, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4177, 180201, 0, 1, "温かい…\n騎士クンがくれる\n力みたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4179, 180201, 0, 1, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4180, 180201, 0, 1, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4182, 104901, 0, 12, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4183, 104901, 0, 12, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4184, 113101, 0, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4185, 113101, 0, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4186, 113101, 0, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4187, 113101, 0, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4188, 113101, 0, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4189, 113101, 0, 4, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4190, 113101, 0, 8, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4191, 113101, 0, 1, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4192, 113101, 0, 1, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4193, 113101, 0, 1, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4194, 113101, 0, 1, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4195, 113101, 0, 4, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4196, 113101, 0, 8, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4197, 113101, 0, 1, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4198, 113101, 0, 1, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4199, 113101, 0, 1, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4201, 113101, 0, 1, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4202, 113101, 0, 1, "あんたたちも\n海を楽しみたいって\n思っているのかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4203, 113101, 0, 1, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4204, 113101, 0, 1, "こういう格好だと\n少し冷えちまうんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4206, 113101, 0, 1, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4207, 113101, 0, 1, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4209, 113101, 0, 1, "へえ…\nなかなかいい\n乗り心地じゃないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4210, 113101, 0, 1, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4211, 113301, 0, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4212, 113301, 0, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4213, 113301, 0, 1, "今のナナカちゃんは\nたぶん水属性！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4214, 113301, 0, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4215, 113301, 0, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4216, 113301, 0, 4, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4217, 113301, 0, 8, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4218, 113301, 0, 1, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4219, 113301, 0, 1, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4220, 113301, 0, 1, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4221, 113301, 0, 1, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4222, 113301, 0, 4, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4223, 113301, 0, 8, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4224, 113301, 0, 1, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4225, 113301, 0, 1, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4226, 113301, 0, 1, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4228, 113301, 0, 1, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4229, 113301, 0, 1, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4230, 113301, 0, 1, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4231, 113301, 0, 1, "夜になると急に冷えて\n肌寒いんだよね～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4233, 113301, 0, 1, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4234, 113301, 0, 1, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4236, 113301, 0, 1, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4237, 113301, 0, 1, "ナナカちゃんの\n誕生～\nなんちゃって", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4238, 113401, 0, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4239, 113401, 0, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4240, 113401, 0, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4241, 113401, 0, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4242, 113401, 0, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4243, 113401, 0, 4, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4244, 113401, 0, 8, "キミへの応援\nちゃんと\n届いてるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4245, 113401, 0, 1, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4246, 113401, 0, 1, "どうしたの？\n元気がないなら\n私が応援するよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4247, 113401, 0, 1, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4248, 113401, 0, 1, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4249, 113401, 0, 4, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4250, 113401, 0, 8, "キミを応援\nすることができて…\n私、すごく幸せ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4251, 113401, 0, 1, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4252, 113401, 0, 1, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4253, 113401, 0, 1, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4255, 113401, 0, 1, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4256, 113401, 0, 1, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4257, 113401, 0, 1, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4258, 113401, 0, 1, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4260, 113401, 0, 1, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4261, 113401, 0, 1, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4263, 113401, 0, 1, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4264, 113401, 0, 1, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4265, 113501, 0, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4266, 113501, 0, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4267, 113501, 0, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4268, 113501, 0, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4269, 113501, 0, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4270, 113501, 0, 4, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4271, 113501, 0, 8, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4272, 113501, 0, 1, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4273, 113501, 0, 1, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4274, 113501, 0, 1, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4275, 113501, 0, 1, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4276, 113501, 0, 4, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4277, 113501, 0, 8, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4278, 113501, 0, 1, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4279, 113501, 0, 1, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4280, 113501, 0, 1, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4282, 113501, 0, 1, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4283, 113501, 0, 1, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4284, 113501, 0, 1, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4285, 113501, 0, 1, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4287, 113501, 0, 1, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4288, 113501, 0, 1, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4290, 113501, 0, 1, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4291, 113501, 0, 1, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4292, 102301, 0, 12, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4293, 102301, 0, 12, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4294, 113601, 0, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4295, 113601, 0, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4296, 113601, 0, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4297, 113601, 0, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4298, 113601, 0, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4299, 113601, 0, 4, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4300, 113601, 0, 8, "少年との\n海のバカンス…\nまた行きたい\nものだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4301, 113601, 0, 1, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4302, 113601, 0, 1, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4303, 113601, 0, 1, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4304, 113601, 0, 1, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4305, 113601, 0, 4, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4306, 113601, 0, 8, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4307, 113601, 0, 1, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4308, 113601, 0, 1, "昼食のお誘いかい？\nいいよ、付き合おう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4309, 113601, 0, 1, "もう夜か…\n一日が経つのは\nはやいものだね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4311, 113601, 0, 1, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4312, 113601, 0, 1, "うん…ふわふわの\n感触が\nよくわかるな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4313, 113601, 0, 1, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4314, 113601, 0, 1, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4316, 113601, 0, 1, "兜を気にせず\n食べられるのは\n楽でいいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4317, 113601, 0, 1, "ふむ…鎧がないと\nページがめくりやすい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4319, 113601, 0, 1, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4320, 113601, 0, 1, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4321, 106801, 0, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4322, 106801, 0, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4323, 106801, 0, 1, "色んな人を観察できて\n眼福眼福…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4324, 106801, 0, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4325, 106801, 0, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4326, 106801, 0, 4, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4327, 106801, 0, 8, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4328, 106801, 0, 1, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4329, 106801, 0, 1, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4330, 106801, 0, 1, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4331, 106801, 0, 1, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4332, 106801, 0, 4, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4333, 106801, 0, 8, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4334, 106801, 0, 1, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4335, 106801, 0, 1, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4336, 106801, 0, 1, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4338, 106801, 0, 1, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4339, 106801, 0, 1, "…これ\nネネカが変身\nしてないよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4340, 106801, 0, 1, "いい香りだ…\n解析完了\n今度生成しようっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4341, 106801, 0, 1, "安心するな～…\nアジトに置けないから\n羨ましいよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4343, 106801, 0, 1, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4344, 106801, 0, 1, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4346, 113701, 0, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4347, 113701, 0, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4348, 113701, 0, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4349, 113701, 0, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4350, 113701, 0, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4351, 113701, 0, 4, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4352, 113701, 0, 8, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4353, 113701, 0, 1, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4354, 113701, 0, 1, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4355, 113701, 0, 1, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4356, 113701, 0, 1, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4357, 113701, 0, 4, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4358, 113701, 0, 8, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4359, 113701, 0, 1, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4360, 113701, 0, 1, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4361, 113701, 0, 1, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4363, 113701, 0, 1, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4364, 113701, 0, 1, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4365, 113701, 0, 1, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4366, 113701, 0, 1, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4368, 113701, 0, 1, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4369, 113701, 0, 1, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4371, 103201, 0, 12, "あの方の前では\n常に気高く\nありたいですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4372, 103201, 0, 12, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4373, 102801, 0, 12, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4374, 102801, 0, 12, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4375, 113801, 0, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4376, 113801, 0, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4377, 113801, 0, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4378, 113801, 0, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4379, 113801, 0, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4380, 113801, 0, 4, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4381, 113801, 0, 8, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4382, 113801, 0, 1, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4383, 113801, 0, 1, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4384, 113801, 0, 1, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4385, 113801, 0, 1, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4386, 113801, 0, 4, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4387, 113801, 0, 8, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4388, 113801, 0, 1, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4389, 113801, 0, 1, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4390, 113801, 0, 1, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4392, 113801, 0, 1, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4393, 113801, 0, 1, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4394, 113801, 0, 1, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4395, 113801, 0, 1, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4397, 113801, 0, 1, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4398, 113801, 0, 1, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4400, 113901, 0, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4401, 113901, 0, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4402, 113901, 0, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4403, 113901, 0, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4404, 113901, 0, 1, "マントは動きに\nメリハリがついて\nいいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4405, 113901, 0, 4, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4406, 113901, 0, 8, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4407, 113901, 0, 1, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4408, 113901, 0, 1, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4409, 113901, 0, 1, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4410, 113901, 0, 1, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4411, 113901, 0, 4, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4412, 113901, 0, 8, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4413, 113901, 0, 1, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4414, 113901, 0, 1, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4415, 113901, 0, 1, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4417, 113901, 0, 1, "このフィット感\nどうにか服作りにも\n活かせれば…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4418, 113901, 0, 1, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4419, 113901, 0, 1, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4420, 113901, 0, 1, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4422, 113901, 0, 1, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4423, 113901, 0, 1, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 4425, 113901, 0, 1, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4427, 114101, 0, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4428, 114101, 0, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4429, 114101, 0, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4430, 114101, 0, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4431, 114101, 0, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4432, 114101, 0, 4, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4433, 114101, 0, 8, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4434, 114101, 0, 1, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4435, 114101, 0, 1, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4436, 114101, 0, 1, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4437, 114101, 0, 1, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4438, 114101, 0, 4, "あんたも一緒に\n新しい技の練習\nどうッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4439, 114101, 0, 8, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4440, 114101, 0, 1, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4441, 114101, 0, 1, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4442, 114101, 0, 1, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4444, 114101, 0, 1, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4445, 114101, 0, 1, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4446, 114101, 0, 1, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4447, 114101, 0, 1, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4449, 114101, 0, 1, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4450, 114101, 0, 1, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 4452, 114101, 0, 1, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4454, 103001, 0, 12, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4455, 103001, 0, 12, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4456, 114001, 0, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4457, 114001, 0, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4458, 114001, 0, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4459, 114001, 0, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4460, 114001, 0, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4461, 114001, 0, 4, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4462, 114001, 0, 8, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4463, 114001, 0, 1, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4464, 114001, 0, 1, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4465, 114001, 0, 1, "どうしたの？\n出かけるなら\n私も同行しようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4466, 114001, 0, 1, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4467, 114001, 0, 4, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4468, 114001, 0, 8, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4469, 114001, 0, 1, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4470, 114001, 0, 1, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4471, 114001, 0, 1, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4473, 114001, 0, 1, "ハロウィンはまだ\nこれからだ\n一休みしようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4474, 114001, 0, 1, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4475, 114001, 0, 1, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4476, 114001, 0, 1, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4478, 114001, 0, 1, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4479, 114001, 0, 1, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 4481, 114001, 0, 1, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4483, 114201, 0, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4484, 114201, 0, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4485, 114201, 0, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4486, 114201, 0, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4487, 114201, 0, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4488, 114201, 0, 4, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4489, 114201, 0, 8, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4490, 114201, 0, 1, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4491, 114201, 0, 1, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4492, 114201, 0, 1, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4493, 114201, 0, 1, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4494, 114201, 0, 4, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4495, 114201, 0, 8, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4496, 114201, 0, 1, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4497, 114201, 0, 1, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4498, 114201, 0, 1, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4500, 114201, 0, 1, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4501, 114201, 0, 1, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4502, 114201, 0, 1, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4503, 114201, 0, 1, "この温かさ…\n愛と希望に\n通じるものがある…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4505, 114201, 0, 1, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4506, 114201, 0, 1, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4508, 103301, 0, 12, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4509, 103301, 0, 12, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4510, 192501, 0, 1, "ぴっぷる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4511, 192501, 0, 1, "ぴっぷるぴっぷる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4512, 192501, 0, 1, "ぴっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4513, 192501, 0, 1, "ぷっぷー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4514, 192501, 0, 1, "ぴぃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4515, 192501, 0, 1, "ぴっぷるんるん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4516, 192501, 0, 1, "ぷぅ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4517, 192501, 0, 1, "ぴぴっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4518, 192501, 0, 1, "ぷぅー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4519, 192501, 0, 1, "ちゅ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4520, 192501, 0, 1, "ぷはー", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4521, 192501, 0, 1, "ぴぇー", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4531, 114301, 0, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4532, 114301, 0, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4533, 114301, 0, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4534, 114301, 0, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4535, 114301, 0, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4536, 114301, 0, 4, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4537, 114301, 0, 8, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4538, 114301, 0, 1, "もし時間があるなら、\n私と街の見回りに\n行かない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4539, 114301, 0, 1, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4540, 114301, 0, 1, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4541, 114301, 0, 1, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4542, 114301, 0, 4, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4543, 114301, 0, 8, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4544, 114301, 0, 1, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4545, 114301, 0, 1, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4546, 114301, 0, 1, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4548, 114301, 0, 1, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4549, 114301, 0, 1, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4550, 114301, 0, 1, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4551, 114301, 0, 1, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4553, 114301, 0, 1, "こ、これは…\nおいしすぎて手が\n止まらないかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4554, 114301, 0, 1, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4556, 114401, 0, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4557, 114401, 0, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4558, 114401, 0, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4559, 114401, 0, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4560, 114401, 0, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4561, 114401, 0, 4, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4562, 114401, 0, 8, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4563, 114401, 0, 1, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4564, 114401, 0, 1, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4565, 114401, 0, 1, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4566, 114401, 0, 1, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4567, 114401, 0, 4, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4568, 114401, 0, 8, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4569, 114401, 0, 1, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4570, 114401, 0, 1, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4571, 114401, 0, 1, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4573, 114401, 0, 1, "適度な休息は\n成功の秘訣ですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4574, 114401, 0, 1, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4575, 114401, 0, 1, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4576, 114401, 0, 1, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4578, 114401, 0, 1, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4579, 114401, 0, 1, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 4581, 114401, 0, 1, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 4582, 114401, 0, 1, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4583, 114601, 0, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4584, 114601, 0, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4585, 114601, 0, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4586, 114601, 0, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4587, 114601, 0, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4588, 114601, 0, 4, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4589, 114601, 0, 8, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4590, 114601, 0, 1, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4591, 114601, 0, 1, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4592, 114601, 0, 1, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4593, 114601, 0, 1, "お姉さんの\n頼れるところ\n見せてあげるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4594, 114601, 0, 4, "何かあったら\n頼ってくれても\nいいんだからね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4595, 114601, 0, 8, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4596, 114601, 0, 1, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4597, 114601, 0, 1, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4598, 114601, 0, 1, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4600, 114601, 0, 1, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4601, 114601, 0, 1, "私だって…\nかわいいものは\n好きでいいわよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4602, 114601, 0, 1, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4603, 114601, 0, 1, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4605, 114601, 0, 1, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4606, 114601, 0, 1, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 4608, 114601, 0, 1, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 4609, 114601, 0, 1, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4610, 101601, 0, 12, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4611, 101601, 0, 12, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4612, 114501, 0, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4613, 114501, 0, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4614, 114501, 0, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4615, 114501, 0, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4616, 114501, 0, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4617, 114501, 0, 4, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4618, 114501, 0, 8, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4619, 114501, 0, 1, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4620, 114501, 0, 1, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4621, 114501, 0, 1, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4622, 114501, 0, 1, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4623, 114501, 0, 4, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4624, 114501, 0, 8, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4625, 114501, 0, 1, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4626, 114501, 0, 1, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4627, 114501, 0, 1, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4629, 114501, 0, 1, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4630, 114501, 0, 1, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4631, 114501, 0, 1, "華やかな香りね\nクリスマスに\nピッタリだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4632, 114501, 0, 1, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4634, 114501, 0, 1, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4635, 114501, 0, 1, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 4637, 114501, 0, 1, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 4638, 114501, 0, 1, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4639, 114701, 0, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4640, 114701, 0, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4641, 114701, 0, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4642, 114701, 0, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4643, 114701, 0, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4644, 114701, 0, 4, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4645, 114701, 0, 8, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4646, 114701, 0, 1, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4647, 114701, 0, 1, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4648, 114701, 0, 1, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4649, 114701, 0, 1, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4650, 114701, 0, 4, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4651, 114701, 0, 8, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4652, 114701, 0, 1, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4653, 114701, 0, 1, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4654, 114701, 0, 1, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4656, 114701, 0, 1, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4657, 114701, 0, 1, "なんだ？\nオマエも初詣に\n行きたいのか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4658, 114701, 0, 1, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4659, 114701, 0, 1, "コイツがあれば\nうまい魚が\n焼けそうだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4661, 114701, 0, 1, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4662, 114701, 0, 1, "おみくじって\n書いてあることが\nむずかしいんだよな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4664, 180101, 0, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4665, 180101, 0, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4666, 180101, 0, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4667, 180101, 0, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4668, 180101, 0, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4669, 180101, 0, 4, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4670, 180101, 0, 8, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4671, 180101, 0, 1, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4672, 180101, 0, 1, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4673, 180101, 0, 1, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4674, 180101, 0, 1, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4675, 180101, 0, 4, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4676, 180101, 0, 8, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4677, 180101, 0, 1, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4678, 180101, 0, 1, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4679, 180101, 0, 1, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4681, 180101, 0, 1, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4682, 180101, 0, 1, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4683, 180101, 0, 1, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4684, 180101, 0, 1, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4686, 180101, 0, 1, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4687, 180101, 0, 1, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4689, 111801, 0, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4690, 111801, 0, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4691, 111801, 0, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4692, 111801, 0, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4693, 111801, 0, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4694, 111801, 0, 4, "ずっとこうして\n年の始まりを\n過ごしたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4695, 111801, 0, 8, "いつか二人きりで\n初日の出を\n見たいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4696, 111801, 0, 1, "お餅おかわりです！\nいくらでも\n食べられますよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4697, 111801, 0, 1, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4698, 111801, 0, 1, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4699, 111801, 0, 1, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4700, 111801, 0, 4, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4701, 111801, 0, 8, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4702, 111801, 0, 1, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4703, 111801, 0, 1, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4704, 111801, 0, 1, "あたたかくして\n幸せな初夢を\n見ましょうね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4706, 111801, 0, 1, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4707, 111801, 0, 1, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4708, 111801, 0, 1, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4709, 111801, 0, 1, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4711, 111801, 0, 1, "おいしすぎて\n食べる手が\n止まりません～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4712, 111801, 0, 1, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4714, 100601, 0, 12, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4715, 100601, 0, 12, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4716, 115001, 0, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4717, 115001, 0, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4718, 115001, 0, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4719, 115001, 0, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4720, 115001, 0, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4721, 115001, 0, 4, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4722, 115001, 0, 8, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4723, 115001, 0, 1, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4724, 115001, 0, 1, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4725, 115001, 0, 1, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4726, 115001, 0, 1, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4727, 115001, 0, 4, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4728, 115001, 0, 8, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4729, 115001, 0, 1, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4730, 115001, 0, 1, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4731, 115001, 0, 1, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4733, 115001, 0, 1, "少し休みます…\n時間が来たら\n起こすように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4734, 115001, 0, 1, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4735, 115001, 0, 1, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4736, 115001, 0, 1, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4738, 115001, 0, 1, "いかなる強者でも\n兵站、補給は\n必要なものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4739, 115001, 0, 1, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4741, 115501, 0, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4742, 115501, 0, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4743, 115501, 0, 1, "主さまも精霊の召喚を\n試してみますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4744, 115501, 0, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4745, 115501, 0, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4746, 115501, 0, 4, "進化した精霊に\n負けぬよう\n修行に励みます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4747, 115501, 0, 8, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4748, 115501, 0, 1, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4749, 115501, 0, 1, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4750, 115501, 0, 1, "里で採れた果物を\n持って参りました\n召し上がってください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4751, 115501, 0, 1, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4752, 115501, 0, 4, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4753, 115501, 0, 8, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4754, 115501, 0, 1, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4755, 115501, 0, 1, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4756, 115501, 0, 1, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4758, 115501, 0, 1, "この癒し…\n主さまにも味わって\nいただきたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4759, 115501, 0, 1, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4760, 115501, 0, 1, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4761, 115501, 0, 1, "瞑想するのに\nちょうどよい暖かさで\nございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4763, 115501, 0, 1, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4764, 115501, 0, 1, "風の精霊について\nもっと詳しく知って\nおかなければ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4766, 115601, 0, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4767, 115601, 0, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4768, 115601, 0, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4769, 115601, 0, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4770, 115601, 0, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4771, 115601, 0, 4, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4772, 115601, 0, 8, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4773, 115601, 0, 1, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4774, 115601, 0, 1, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4775, 115601, 0, 1, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4776, 115601, 0, 1, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4777, 115601, 0, 4, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4778, 115601, 0, 8, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4779, 115601, 0, 1, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4780, 115601, 0, 1, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4781, 115601, 0, 1, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4783, 115601, 0, 1, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4784, 115601, 0, 1, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4785, 115601, 0, 1, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4786, 115601, 0, 1, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4788, 115601, 0, 1, "自然の恵みに\n感謝して…\nいただきます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4789, 115601, 0, 1, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4791, 102201, 0, 12, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4792, 102201, 0, 12, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4793, 106401, 0, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4794, 106401, 0, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4795, 106401, 0, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4796, 106401, 0, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4797, 106401, 0, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4798, 106401, 0, 4, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4799, 106401, 0, 8, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4800, 106401, 0, 1, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4801, 106401, 0, 1, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4802, 106401, 0, 1, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4803, 106401, 0, 1, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4804, 106401, 0, 4, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4805, 106401, 0, 8, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4806, 106401, 0, 1, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4807, 106401, 0, 1, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4808, 106401, 0, 1, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4810, 106401, 0, 1, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4811, 106401, 0, 1, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4812, 106401, 0, 1, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4813, 106401, 0, 1, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4815, 106401, 0, 1, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4816, 106401, 0, 1, "ん…なるほどね。\n勉強になるわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4818, 115701, 0, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4819, 115701, 0, 1, "依頼があれば\n海でも山でも\n赴くさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4820, 115701, 0, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4821, 115701, 0, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4822, 115701, 0, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4823, 115701, 0, 4, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4824, 115701, 0, 8, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4825, 115701, 0, 1, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4826, 115701, 0, 1, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4827, 115701, 0, 1, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4828, 115701, 0, 1, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4829, 115701, 0, 4, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4830, 115701, 0, 8, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4831, 115701, 0, 1, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4832, 115701, 0, 1, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4833, 115701, 0, 1, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4835, 115701, 0, 1, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4836, 115701, 0, 1, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4837, 115701, 0, 1, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4838, 115701, 0, 1, "ポカポカするね…\n助手くん\n君もおいでよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4840, 115701, 0, 1, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4841, 115701, 0, 1, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 4843, 115701, 0, 1, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 4844, 115701, 0, 1, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4845, 180601, 0, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4846, 180601, 0, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4847, 180601, 0, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4848, 180601, 0, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4849, 180601, 0, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4850, 180601, 0, 4, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4851, 180601, 0, 8, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4852, 180601, 0, 1, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4853, 180601, 0, 1, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4854, 180601, 0, 1, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4855, 180601, 0, 1, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4856, 180601, 0, 4, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4857, 180601, 0, 8, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4858, 180601, 0, 1, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4859, 180601, 0, 1, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4860, 180601, 0, 1, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4862, 180601, 0, 1, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4863, 180601, 0, 1, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4864, 180601, 0, 1, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4865, 180601, 0, 1, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4867, 180601, 0, 1, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4868, 180601, 0, 1, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4870, 115801, 0, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4871, 115801, 0, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4872, 115801, 0, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4873, 115801, 0, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4874, 115801, 0, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4875, 115801, 0, 4, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4876, 115801, 0, 8, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4877, 115801, 0, 1, "どうかしら？\n私、きちんとドレス\n着られてる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4878, 115801, 0, 1, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4879, 115801, 0, 1, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4880, 115801, 0, 1, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4881, 115801, 0, 4, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4882, 115801, 0, 8, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4883, 115801, 0, 1, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4884, 115801, 0, 1, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4885, 115801, 0, 1, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4887, 115801, 0, 1, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4888, 115801, 0, 1, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4889, 115801, 0, 1, "すてきな香り…\nお花かしら？\n森で探してみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4890, 115801, 0, 1, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4892, 115801, 0, 1, "レッスンのために\n栄養補給して\nおかないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4893, 115801, 0, 1, "教養を身に着ける\nことも大切よね！\nなになに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4895, 115901, 0, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4896, 115901, 0, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4897, 115901, 0, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4898, 115901, 0, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4899, 115901, 0, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4900, 115901, 0, 4, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4901, 115901, 0, 8, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4902, 115901, 0, 1, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4903, 115901, 0, 1, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4904, 115901, 0, 1, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4905, 115901, 0, 1, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4906, 115901, 0, 4, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4907, 115901, 0, 8, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4908, 115901, 0, 1, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4909, 115901, 0, 1, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4910, 115901, 0, 1, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4912, 115901, 0, 1, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4913, 115901, 0, 1, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4914, 115901, 0, 1, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4915, 115901, 0, 1, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4917, 115901, 0, 1, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4918, 115901, 0, 1, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4920, 102001, 0, 12, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4921, 102001, 0, 12, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4922, 100401, 0, 12, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4923, 100401, 0, 12, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4924, 103601, 0, 12, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4925, 103601, 0, 12, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4926, 116001, 0, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4927, 116001, 0, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4928, 116001, 0, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4929, 116001, 0, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4930, 116001, 0, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4931, 116001, 0, 4, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4932, 116001, 0, 8, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4933, 116001, 0, 1, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4934, 116001, 0, 1, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4935, 116001, 0, 1, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4936, 116001, 0, 1, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4937, 116001, 0, 4, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4938, 116001, 0, 8, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4939, 116001, 0, 1, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4940, 116001, 0, 1, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4941, 116001, 0, 1, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4943, 116001, 0, 1, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4944, 116001, 0, 1, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4945, 116001, 0, 1, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4946, 116001, 0, 1, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4948, 116001, 0, 1, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4949, 116001, 0, 1, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4951, 116301, 0, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4952, 116301, 0, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4953, 116301, 0, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4954, 116301, 0, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4955, 116301, 0, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4956, 116301, 0, 4, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4957, 116301, 0, 8, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4958, 116301, 0, 1, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4959, 116301, 0, 1, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4960, 116301, 0, 1, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4961, 116301, 0, 1, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4962, 116301, 0, 4, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4963, 116301, 0, 8, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4964, 116301, 0, 1, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4965, 116301, 0, 1, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4966, 116301, 0, 1, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4968, 116301, 0, 1, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4969, 116301, 0, 1, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4970, 116301, 0, 1, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4971, 116301, 0, 1, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 4973, 116301, 0, 1, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 4974, 116301, 0, 1, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 4976, 102901, 0, 12, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 4977, 102901, 0, 12, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 4978, 116201, 0, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 4979, 116201, 0, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 4980, 116201, 0, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 4981, 116201, 0, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 4982, 116201, 0, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 4983, 116201, 0, 4, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 4984, 116201, 0, 8, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4985, 116201, 0, 1, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4986, 116201, 0, 1, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4987, 116201, 0, 1, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 4988, 116201, 0, 1, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 4989, 116201, 0, 4, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 4990, 116201, 0, 8, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 4991, 116201, 0, 1, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 4992, 116201, 0, 1, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 4993, 116201, 0, 1, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 4995, 116201, 0, 1, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 4996, 116201, 0, 1, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 4997, 116201, 0, 1, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 4998, 116201, 0, 1, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5000, 116201, 0, 1, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5001, 116201, 0, 1, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5003, 116501, 0, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5004, 116501, 0, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5005, 116501, 0, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5006, 116501, 0, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5007, 116501, 0, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5008, 116501, 0, 4, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5009, 116501, 0, 8, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5010, 116501, 0, 1, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5011, 116501, 0, 1, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5012, 116501, 0, 1, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5013, 116501, 0, 1, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5014, 116501, 0, 4, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5015, 116501, 0, 8, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5016, 116501, 0, 1, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5017, 116501, 0, 1, "お腹が空きました…\nご飯食べにいきますよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5018, 116501, 0, 1, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5020, 116501, 0, 1, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5021, 116501, 0, 1, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5022, 116501, 0, 1, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5023, 116501, 0, 1, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5025, 116501, 0, 1, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5026, 116501, 0, 1, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5028, 102501, 0, 12, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5029, 102501, 0, 12, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5030, 116601, 0, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5031, 116601, 0, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5032, 116601, 0, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5033, 116601, 0, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5034, 116601, 0, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5035, 116601, 0, 4, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5036, 116601, 0, 8, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5037, 116601, 0, 1, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5038, 116601, 0, 1, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5039, 116601, 0, 1, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5040, 116601, 0, 1, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5041, 116601, 0, 4, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5042, 116601, 0, 8, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5043, 116601, 0, 1, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5044, 116601, 0, 1, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5045, 116601, 0, 1, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5047, 116601, 0, 1, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5048, 116601, 0, 1, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5049, 116601, 0, 1, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5050, 116601, 0, 1, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5052, 116601, 0, 1, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5053, 116601, 0, 1, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5055, 116701, 0, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5056, 116701, 0, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5057, 116701, 0, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5058, 116701, 0, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5059, 116701, 0, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5060, 116701, 0, 4, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5061, 116701, 0, 8, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5062, 116701, 0, 1, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5063, 116701, 0, 1, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5064, 116701, 0, 1, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5065, 116701, 0, 1, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5066, 116701, 0, 4, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5067, 116701, 0, 8, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5068, 116701, 0, 1, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5069, 116701, 0, 1, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5070, 116701, 0, 1, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5072, 116701, 0, 1, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5073, 116701, 0, 1, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5074, 116701, 0, 1, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5075, 116701, 0, 1, "ぽかぽか…\n幸せなひとときですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5077, 116701, 0, 1, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5078, 116701, 0, 1, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5080, 116801, 0, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5081, 116801, 0, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5082, 116801, 0, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5083, 116801, 0, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5084, 116801, 0, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5085, 116801, 0, 4, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5086, 116801, 0, 8, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5087, 116801, 0, 1, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5088, 116801, 0, 1, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5089, 116801, 0, 1, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5090, 116801, 0, 1, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5091, 116801, 0, 4, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5092, 116801, 0, 8, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5093, 116801, 0, 1, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5094, 116801, 0, 1, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5095, 116801, 0, 1, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5097, 116801, 0, 1, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5098, 116801, 0, 1, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5099, 116801, 0, 1, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5100, 116801, 0, 1, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5102, 116801, 0, 1, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5103, 116801, 0, 1, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5105, 102101, 0, 12, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5106, 102101, 0, 12, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5107, 116901, 0, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5108, 116901, 0, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5109, 116901, 0, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5110, 116901, 0, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5111, 116901, 0, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5112, 116901, 0, 4, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5113, 116901, 0, 8, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5114, 116901, 0, 1, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5115, 116901, 0, 1, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5116, 116901, 0, 1, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5117, 116901, 0, 1, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5118, 116901, 0, 4, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5119, 116901, 0, 8, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5120, 116901, 0, 1, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5121, 116901, 0, 1, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5122, 116901, 0, 1, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5124, 116901, 0, 1, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5125, 116901, 0, 1, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5126, 116901, 0, 1, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5127, 116901, 0, 1, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5129, 116901, 0, 1, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5130, 116901, 0, 1, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5132, 180301, 0, 1, "変身している間に\n色々と試しておこう\nまずは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5133, 180301, 0, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5134, 180301, 0, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5135, 180301, 0, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5136, 180301, 0, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5137, 180301, 0, 4, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5138, 180301, 0, 8, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5139, 180301, 0, 1, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5140, 180301, 0, 1, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5141, 180301, 0, 1, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5142, 180301, 0, 1, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5143, 180301, 0, 4, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5144, 180301, 0, 8, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5145, 180301, 0, 1, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5146, 180301, 0, 1, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5147, 180301, 0, 1, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5149, 180301, 0, 1, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5150, 180301, 0, 1, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5151, 180301, 0, 1, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5152, 180301, 0, 1, "ありがたいな\n少し足元が\n冷えていたところだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5154, 180301, 0, 1, "これは…！\n持ち運べるように\n改良できないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5155, 180301, 0, 1, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5157, 117001, 0, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5158, 117001, 0, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5159, 117001, 0, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5160, 117001, 0, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5161, 117001, 0, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5162, 117001, 0, 4, "あのおかたの全てが\n早く私のものに\nなりますように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5163, 117001, 0, 8, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5164, 117001, 0, 1, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5165, 117001, 0, 1, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5166, 117001, 0, 1, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5167, 117001, 0, 1, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5168, 117001, 0, 4, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5169, 117001, 0, 8, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5170, 117001, 0, 1, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5171, 117001, 0, 1, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5172, 117001, 0, 1, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5174, 117001, 0, 1, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5175, 117001, 0, 1, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5176, 117001, 0, 1, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5177, 117001, 0, 1, "あのおかたの\nぬくもりを\n思い出しますわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5179, 117001, 0, 1, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5180, 117001, 0, 1, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 5182, 117001, 0, 1, "二人乗りができると\nいいですわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 5183, 117001, 0, 1, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5184, 101701, 0, 12, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5185, 101701, 0, 12, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5186, 117101, 0, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5187, 117101, 0, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5188, 117101, 0, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5189, 117101, 0, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5190, 117101, 0, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5191, 117101, 0, 4, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5192, 117101, 0, 8, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5193, 117101, 0, 1, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5194, 117101, 0, 1, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5195, 117101, 0, 1, "海で迷子に\nならないように\n手を繋ごうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5196, 117101, 0, 1, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5197, 117101, 0, 4, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5198, 117101, 0, 8, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5199, 117101, 0, 1, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5200, 117101, 0, 1, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5201, 117101, 0, 1, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5203, 117101, 0, 1, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5204, 117101, 0, 1, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5205, 117101, 0, 1, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5206, 117101, 0, 1, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5208, 117101, 0, 1, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5209, 117101, 0, 1, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 5211, 117101, 0, 1, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 5212, 117101, 0, 1, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5213, 117201, 0, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5214, 117201, 0, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5215, 117201, 0, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5216, 117201, 0, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5217, 117201, 0, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5218, 117201, 0, 4, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5219, 117201, 0, 8, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5220, 117201, 0, 1, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5221, 117201, 0, 1, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5222, 117201, 0, 1, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5223, 117201, 0, 1, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5224, 117201, 0, 4, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5225, 117201, 0, 8, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5226, 117201, 0, 1, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5227, 117201, 0, 1, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5228, 117201, 0, 1, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5230, 117201, 0, 1, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5231, 117201, 0, 1, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5232, 117201, 0, 1, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5233, 117201, 0, 1, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5235, 117201, 0, 1, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5236, 117201, 0, 1, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 5238, 117201, 0, 1, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 5239, 117201, 0, 1, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5240, 117401, 0, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5241, 117401, 0, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5242, 117401, 0, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5243, 117401, 0, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5244, 117401, 0, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5245, 117401, 0, 4, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5246, 117401, 0, 8, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5247, 117401, 0, 1, "騎士さんいいところに\nこの衣装\nどう思います？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5248, 117401, 0, 1, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5249, 117401, 0, 1, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5250, 117401, 0, 1, "仕立て屋も\nアイドルも\n諦めません！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5251, 117401, 0, 4, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5252, 117401, 0, 8, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5253, 117401, 0, 1, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5254, 117401, 0, 1, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5255, 117401, 0, 1, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5257, 117401, 0, 1, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5258, 117401, 0, 1, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5259, 117401, 0, 1, "いい香りですね～\n波の音が聞こえて\nきそうです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5260, 117401, 0, 1, "泳いだ後は\n少し寒いので\nちょうどいいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5262, 117401, 0, 1, "夏バテしないように\n栄養はしっかり\n取らないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5263, 117401, 0, 1, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 5265, 117401, 0, 1, "デザインはもちろん\n触り心地も\n最高ですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 5266, 117401, 0, 1, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5267, 104001, 0, 12, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5268, 104001, 0, 12, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5269, 117301, 0, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5270, 117301, 0, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5271, 117301, 0, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5272, 117301, 0, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5273, 117301, 0, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5274, 117301, 0, 4, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5275, 117301, 0, 8, "水着、似合ってるって\n言ってもらえるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5276, 117301, 0, 1, "サインして、ですか？\nいいですけど…\n少し照れますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5277, 117301, 0, 1, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5278, 117301, 0, 1, "なんでしょう？\nもしかして…\nボイトレに興味が？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5279, 117301, 0, 1, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5280, 117301, 0, 4, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5281, 117301, 0, 8, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5282, 117301, 0, 1, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5283, 117301, 0, 1, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5284, 117301, 0, 1, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5286, 117301, 0, 1, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5287, 117301, 0, 1, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5288, 117301, 0, 1, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5289, 117301, 0, 1, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5291, 117301, 0, 1, "アイドルとして、\n食べすぎには\n気をつけないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5292, 117301, 0, 1, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 5294, 117301, 0, 1, "この子に乗りながら\nライブをする…\nというのはどうですか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 5295, 117301, 0, 1, "まるで人魚になった\n気分…なんて、ふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5296, 118301, 0, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5297, 118301, 0, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5298, 118301, 0, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5299, 118301, 0, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5300, 118301, 0, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5301, 118301, 0, 4, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5302, 118301, 0, 8, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5303, 118301, 0, 1, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5304, 118301, 0, 1, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5305, 118301, 0, 1, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5306, 118301, 0, 1, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5307, 118301, 0, 4, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5308, 118301, 0, 8, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5309, 118301, 0, 1, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5310, 118301, 0, 1, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5311, 118301, 0, 1, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5313, 118301, 0, 1, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5314, 118301, 0, 1, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5315, 118301, 0, 1, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5316, 118301, 0, 1, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5318, 118301, 0, 1, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5319, 118301, 0, 1, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5322, 118401, 0, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5323, 118401, 0, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5324, 118401, 0, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5325, 118401, 0, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5326, 118401, 0, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5327, 118401, 0, 4, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5328, 118401, 0, 8, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5329, 118401, 0, 1, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5330, 118401, 0, 1, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5331, 118401, 0, 1, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5332, 118401, 0, 1, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5333, 118401, 0, 4, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5334, 118401, 0, 8, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5335, 118401, 0, 1, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5336, 118401, 0, 1, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5337, 118401, 0, 1, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5339, 118401, 0, 1, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5340, 118401, 0, 1, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5341, 118401, 0, 1, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5342, 118401, 0, 1, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5344, 118401, 0, 1, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5345, 118401, 0, 1, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5348, 117501, 0, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5349, 117501, 0, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5350, 117501, 0, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5351, 117501, 0, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5352, 117501, 0, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5353, 117501, 0, 4, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5354, 117501, 0, 8, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5355, 117501, 0, 1, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5356, 117501, 0, 1, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5357, 117501, 0, 1, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5358, 117501, 0, 1, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5359, 117501, 0, 4, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5360, 117501, 0, 8, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5361, 117501, 0, 1, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5362, 117501, 0, 1, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5363, 117501, 0, 1, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5365, 117501, 0, 1, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5366, 117501, 0, 1, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5367, 117501, 0, 1, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5368, 117501, 0, 1, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5370, 117501, 0, 1, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5371, 117501, 0, 1, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5400, 117601, 0, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5401, 117601, 0, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5402, 117601, 0, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5403, 117601, 0, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5404, 117601, 0, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5405, 117601, 0, 4, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5406, 117601, 0, 8, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5407, 117601, 0, 1, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5408, 117601, 0, 1, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5409, 117601, 0, 1, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5410, 117601, 0, 1, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5411, 117601, 0, 4, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5412, 117601, 0, 8, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5413, 117601, 0, 1, "さすがボク\n目が覚めるような\n美しさでしょ？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5414, 117601, 0, 1, "美は食で作られる\n美容にいいものを\n食べようよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5415, 117601, 0, 1, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5417, 117601, 0, 1, "ふう～…\nリラックスする\nボクも美しい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5418, 117601, 0, 1, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5419, 117601, 0, 1, "美しいボクは\n常にいい香りを\nしていなくちゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5420, 117601, 0, 1, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5422, 117601, 0, 1, "バランスのいい食事が\n美しいボクを\n作るのさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5423, 117601, 0, 1, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5425, 105701, 0, 12, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5426, 105701, 0, 12, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5427, 118001, 0, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5428, 118001, 0, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5429, 118001, 0, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5430, 118001, 0, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5431, 118001, 0, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5432, 118001, 0, 4, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5433, 118001, 0, 8, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5434, 118001, 0, 1, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5435, 118001, 0, 1, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5436, 118001, 0, 1, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5437, 118001, 0, 1, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5438, 118001, 0, 4, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5439, 118001, 0, 8, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5440, 118001, 0, 1, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5441, 118001, 0, 1, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5442, 118001, 0, 1, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5444, 118001, 0, 1, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5445, 118001, 0, 1, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5446, 118001, 0, 1, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5447, 118001, 0, 1, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5449, 118001, 0, 1, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5450, 118001, 0, 1, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5452, 117701, 0, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5453, 117701, 0, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5454, 117701, 0, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5455, 117701, 0, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5456, 117701, 0, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5457, 117701, 0, 4, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5458, 117701, 0, 8, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5459, 117701, 0, 1, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5460, 117701, 0, 1, "この制服、\nハロウィンらしくて\n大好きさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5461, 117701, 0, 1, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5462, 117701, 0, 1, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5463, 117701, 0, 4, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5464, 117701, 0, 8, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5465, 117701, 0, 1, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5466, 117701, 0, 1, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5467, 117701, 0, 1, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5469, 117701, 0, 1, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5470, 117701, 0, 1, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5471, 117701, 0, 1, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5472, 117701, 0, 1, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5474, 117701, 0, 1, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5475, 117701, 0, 1, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 5477, 117701, 0, 1, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5479, 117801, 0, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5480, 117801, 0, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5481, 117801, 0, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5482, 117801, 0, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5483, 117801, 0, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5484, 117801, 0, 4, "幽霊になる前に\n天下統一を\n果たしたいデスね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5485, 117801, 0, 8, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5486, 117801, 0, 1, "ショーグン！\n悪霊退治に\n出かけるデース！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5487, 117801, 0, 1, "妖魔を家来に\nしてみるのは\nどうデス？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5488, 117801, 0, 1, "マントを使って\nウツセミの術の精度を\n高めるデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5489, 117801, 0, 1, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5490, 117801, 0, 4, "あの世でも\nショーグンに\nお仕えしマスよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5491, 117801, 0, 8, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5492, 117801, 0, 1, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5493, 117801, 0, 1, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5494, 117801, 0, 1, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5496, 117801, 0, 1, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5497, 117801, 0, 1, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5498, 117801, 0, 1, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5499, 117801, 0, 1, "パレードの熱気に\n負けないくらい\n熱いデス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5501, 117801, 0, 1, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5502, 117801, 0, 1, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 5504, 117801, 0, 1, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5531, 101301, 0, 12, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5532, 101301, 0, 12, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5533, 117901, 0, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5534, 117901, 0, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5535, 117901, 0, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5536, 117901, 0, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5537, 117901, 0, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5538, 117901, 0, 4, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5539, 117901, 0, 8, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5540, 117901, 0, 1, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5541, 117901, 0, 1, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5542, 117901, 0, 1, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5543, 117901, 0, 1, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5544, 117901, 0, 4, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5545, 117901, 0, 8, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5546, 117901, 0, 1, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5547, 117901, 0, 1, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5548, 117901, 0, 1, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5550, 117901, 0, 1, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5551, 117901, 0, 1, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5552, 117901, 0, 1, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5553, 117901, 0, 1, "やばばば…\nあったかくて\n眠くなっちゃう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5555, 117901, 0, 1, "これ食べて\nもうひとがんばり\nしよーっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5556, 117901, 0, 1, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 5558, 117901, 0, 1, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5560, 118501, 0, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5561, 118501, 0, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5562, 118501, 0, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5563, 118501, 0, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5564, 118501, 0, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5565, 118501, 0, 4, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5566, 118501, 0, 8, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5567, 118501, 0, 1, "何かわからないことが\nあるときは私に\n聞いてくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5568, 118501, 0, 1, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5569, 118501, 0, 1, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5570, 118501, 0, 1, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5571, 118501, 0, 4, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5572, 118501, 0, 8, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5573, 118501, 0, 1, "今日も一日\n管理人として\nがんばります♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5574, 118501, 0, 1, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5575, 118501, 0, 1, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5577, 118501, 0, 1, "良質なお仕事は\n良質な休憩から\nです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5578, 118501, 0, 1, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5579, 118501, 0, 1, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5580, 118501, 0, 1, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5582, 118501, 0, 1, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5583, 118501, 0, 1, "新条例はこのように\n活用されている、と…\nふむふむ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5585, 106701, 0, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5586, 106701, 0, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5587, 106701, 0, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5588, 106701, 0, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5589, 106701, 0, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5590, 106701, 0, 4, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5591, 106701, 0, 8, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5592, 106701, 0, 1, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5593, 106701, 0, 1, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5594, 106701, 0, 1, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5595, 106701, 0, 1, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5596, 106701, 0, 4, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5597, 106701, 0, 8, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5598, 106701, 0, 1, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5599, 106701, 0, 1, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5600, 106701, 0, 1, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5602, 106701, 0, 1, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5603, 106701, 0, 1, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5604, 106701, 0, 1, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5605, 106701, 0, 1, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5607, 106701, 0, 1, "この味付け…\nあっちでも再現\nできるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5608, 106701, 0, 1, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5610, 119001, 0, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5611, 119001, 0, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5612, 119001, 0, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5613, 119001, 0, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5614, 119001, 0, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5615, 119001, 0, 4, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5616, 119001, 0, 8, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5617, 119001, 0, 1, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5618, 119001, 0, 1, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5619, 119001, 0, 1, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5620, 119001, 0, 1, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5621, 119001, 0, 4, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5622, 119001, 0, 8, "君との秘密が\nまた一つ\n増えちゃったね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5623, 119001, 0, 1, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5624, 119001, 0, 1, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5625, 119001, 0, 1, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5627, 119001, 0, 1, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5628, 119001, 0, 1, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5629, 119001, 0, 1, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5630, 119001, 0, 1, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5632, 119001, 0, 1, "生徒の見本に\nなれるように\n好き嫌いはしないの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5633, 119001, 0, 1, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5635, 106301, 0, 12, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5636, 106301, 0, 12, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5637, 105301, 0, 12, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5638, 105301, 0, 12, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5639, 119101, 0, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5640, 119101, 0, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5641, 119101, 0, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5642, 119101, 0, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5643, 119101, 0, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5644, 119101, 0, 4, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5645, 119101, 0, 8, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5646, 119101, 0, 1, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5647, 119101, 0, 1, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5648, 119101, 0, 1, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5649, 119101, 0, 1, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5650, 119101, 0, 4, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5651, 119101, 0, 8, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5652, 119101, 0, 1, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5653, 119101, 0, 1, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5654, 119101, 0, 1, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5656, 119101, 0, 1, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5657, 119101, 0, 1, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5658, 119101, 0, 1, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5659, 119101, 0, 1, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5661, 119101, 0, 1, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5662, 119101, 0, 1, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5664, 119901, 0, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5665, 119901, 0, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5666, 119901, 0, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5667, 119901, 0, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5668, 119901, 0, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5669, 119901, 0, 4, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5670, 119901, 0, 8, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5671, 119901, 0, 1, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5672, 119901, 0, 1, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5673, 119901, 0, 1, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5674, 119901, 0, 1, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5675, 119901, 0, 4, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5676, 119901, 0, 8, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5677, 119901, 0, 1, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5678, 119901, 0, 1, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5679, 119901, 0, 1, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5681, 119901, 0, 1, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5682, 119901, 0, 1, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5683, 119901, 0, 1, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5684, 119901, 0, 1, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5686, 119901, 0, 1, "これはオマエがくれる\nプリンとは\n別腹なの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5687, 119901, 0, 1, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 5689, 119901, 0, 1, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 5690, 119901, 0, 1, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5691, 119201, 0, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5692, 119201, 0, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5693, 119201, 0, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5694, 119201, 0, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5695, 119201, 0, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5696, 119201, 0, 4, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5697, 119201, 0, 8, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5698, 119201, 0, 1, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5699, 119201, 0, 1, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5700, 119201, 0, 1, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5701, 119201, 0, 1, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5702, 119201, 0, 4, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5703, 119201, 0, 8, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5704, 119201, 0, 1, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5705, 119201, 0, 1, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5706, 119201, 0, 1, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5708, 119201, 0, 1, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5709, 119201, 0, 1, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5710, 119201, 0, 1, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5711, 119201, 0, 1, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5713, 119201, 0, 1, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5714, 119201, 0, 1, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 5716, 119201, 0, 1, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 5717, 119201, 0, 1, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5718, 105001, 0, 12, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5719, 105001, 0, 12, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5720, 119301, 0, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5721, 119301, 0, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5722, 119301, 0, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5723, 119301, 0, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5724, 119301, 0, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5725, 119301, 0, 4, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5726, 119301, 0, 8, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5727, 119301, 0, 1, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5728, 119301, 0, 1, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5729, 119301, 0, 1, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5730, 119301, 0, 1, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5731, 119301, 0, 4, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5732, 119301, 0, 8, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5733, 119301, 0, 1, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5734, 119301, 0, 1, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5735, 119301, 0, 1, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5737, 119301, 0, 1, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5738, 119301, 0, 1, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5739, 119301, 0, 1, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5740, 119301, 0, 1, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5742, 119301, 0, 1, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5743, 119301, 0, 1, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 5745, 119301, 0, 1, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 5746, 119301, 0, 1, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5747, 120601, 0, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5748, 120601, 0, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5749, 120601, 0, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5750, 120601, 0, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5751, 120601, 0, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5752, 120601, 0, 4, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5753, 120601, 0, 8, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5754, 120601, 0, 1, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5755, 120601, 0, 1, "きゃあっ！？\nへ、変なところを\n触らないでください！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5756, 120601, 0, 1, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5757, 120601, 0, 1, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5758, 120601, 0, 4, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5759, 120601, 0, 8, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5760, 120601, 0, 1, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5761, 120601, 0, 1, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5762, 120601, 0, 1, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5764, 120601, 0, 1, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5765, 120601, 0, 1, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5766, 120601, 0, 1, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5767, 120601, 0, 1, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5769, 120601, 0, 1, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5770, 120601, 0, 1, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5773, 120401, 0, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5774, 120401, 0, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5775, 120401, 0, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5776, 120401, 0, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5777, 120401, 0, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5778, 120401, 0, 4, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5779, 120401, 0, 8, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5780, 120401, 0, 1, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5781, 120401, 0, 1, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5782, 120401, 0, 1, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5783, 120401, 0, 1, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5784, 120401, 0, 4, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5785, 120401, 0, 8, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5786, 120401, 0, 1, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5787, 120401, 0, 1, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5788, 120401, 0, 1, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5790, 120401, 0, 1, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5791, 120401, 0, 1, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5792, 120401, 0, 1, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5793, 120401, 0, 1, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5795, 120401, 0, 1, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5796, 120401, 0, 1, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5799, 120501, 0, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5800, 120501, 0, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5801, 120501, 0, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5802, 120501, 0, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5803, 120501, 0, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5804, 120501, 0, 4, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5805, 120501, 0, 8, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5806, 120501, 0, 1, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5807, 120501, 0, 1, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5808, 120501, 0, 1, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5809, 120501, 0, 1, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5810, 120501, 0, 4, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5811, 120501, 0, 8, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5812, 120501, 0, 1, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5813, 120501, 0, 1, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5814, 120501, 0, 1, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5816, 120501, 0, 1, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5817, 120501, 0, 1, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5818, 120501, 0, 1, "においをうまく使った\nイタズラって…\nなにかないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5819, 120501, 0, 1, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5821, 120501, 0, 1, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5822, 120501, 0, 1, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5825, 120801, 0, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5826, 120801, 0, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5827, 120801, 0, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5828, 120801, 0, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5829, 120801, 0, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5830, 120801, 0, 4, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5831, 120801, 0, 8, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5832, 120801, 0, 1, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5833, 120801, 0, 1, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5834, 120801, 0, 1, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5835, 120801, 0, 1, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5836, 120801, 0, 4, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5837, 120801, 0, 8, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5838, 120801, 0, 1, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5839, 120801, 0, 1, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5840, 120801, 0, 1, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5842, 120801, 0, 1, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5843, 120801, 0, 1, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5844, 120801, 0, 1, "これは冬の花の香り…\n風情があって\nいいじゃないか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5845, 120801, 0, 1, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5847, 120801, 0, 1, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5848, 120801, 0, 1, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5850, 120701, 0, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5851, 120701, 0, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5852, 120701, 0, 1, "この格好も\n慣れてくれば\n動きやすいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5853, 120701, 0, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5854, 120701, 0, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5855, 120701, 0, 4, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5856, 120701, 0, 8, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5857, 120701, 0, 1, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5858, 120701, 0, 1, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5859, 120701, 0, 1, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5860, 120701, 0, 1, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5861, 120701, 0, 4, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5862, 120701, 0, 8, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5863, 120701, 0, 1, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5864, 120701, 0, 1, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5865, 120701, 0, 1, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5867, 120701, 0, 1, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5868, 120701, 0, 1, "ふふっ\nかわいいものって\n癒されるわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5869, 120701, 0, 1, "…ん\nこれが梅の香り？\n…いい匂いね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5870, 120701, 0, 1, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5872, 120701, 0, 1, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5873, 120701, 0, 1, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 5875, 104201, 0, 12, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 5876, 104201, 0, 12, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5877, 120901, 0, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5878, 120901, 0, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5879, 120901, 0, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5880, 120901, 0, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5881, 120901, 0, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5882, 120901, 0, 4, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5883, 120901, 0, 8, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5884, 120901, 0, 1, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5885, 120901, 0, 1, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5886, 120901, 0, 1, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5887, 120901, 0, 1, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5888, 120901, 0, 4, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5889, 120901, 0, 8, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5890, 120901, 0, 1, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5891, 120901, 0, 1, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5892, 120901, 0, 1, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5894, 120901, 0, 1, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5895, 120901, 0, 1, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5896, 120901, 0, 1, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5897, 120901, 0, 1, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5899, 120901, 0, 1, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5900, 120901, 0, 1, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5902, 121001, 0, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5903, 121001, 0, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5904, 121001, 0, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5905, 121001, 0, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5906, 121001, 0, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5907, 121001, 0, 4, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5908, 121001, 0, 8, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5909, 121001, 0, 1, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5910, 121001, 0, 1, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5911, 121001, 0, 1, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5912, 121001, 0, 1, "あなたの想いが\n全力全開を超える\n力をくれました！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5913, 121001, 0, 4, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5914, 121001, 0, 8, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5915, 121001, 0, 1, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5916, 121001, 0, 1, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5917, 121001, 0, 1, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5919, 121001, 0, 1, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5920, 121001, 0, 1, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5921, 121001, 0, 1, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5922, 121001, 0, 1, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5924, 121001, 0, 1, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5925, 121001, 0, 1, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5927, 121201, 0, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5928, 121201, 0, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5929, 121201, 0, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5930, 121201, 0, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5931, 121201, 0, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5932, 121201, 0, 4, "少年が\n目を覚ましてくれて\n本当によかった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5933, 121201, 0, 8, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5934, 121201, 0, 1, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5935, 121201, 0, 1, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5936, 121201, 0, 1, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5937, 121201, 0, 1, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5938, 121201, 0, 4, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5939, 121201, 0, 8, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5940, 121201, 0, 1, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5941, 121201, 0, 1, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5942, 121201, 0, 1, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5944, 121201, 0, 1, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5945, 121201, 0, 1, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5946, 121201, 0, 1, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5947, 121201, 0, 1, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5949, 121201, 0, 1, "食の大切さは\nいつの食事からも\n感じられるね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5950, 121201, 0, 1, "ふむ…面白い！\n今度全巻\n借りにこようっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5952, 116401, 0, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5953, 116401, 0, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5954, 116401, 0, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5955, 116401, 0, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5956, 116401, 0, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5957, 116401, 0, 4, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5958, 116401, 0, 8, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5959, 116401, 0, 1, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5960, 116401, 0, 1, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5961, 116401, 0, 1, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5962, 116401, 0, 1, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5963, 116401, 0, 4, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5964, 116401, 0, 8, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5965, 116401, 0, 1, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5966, 116401, 0, 1, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5967, 116401, 0, 1, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5969, 116401, 0, 1, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5970, 116401, 0, 1, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5971, 116401, 0, 1, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5972, 116401, 0, 1, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5974, 116401, 0, 1, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 5975, 116401, 0, 1, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 5977, 121101, 0, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 5978, 121101, 0, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 5979, 121101, 0, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 5980, 121101, 0, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 5981, 121101, 0, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 5982, 121101, 0, 4, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 5983, 121101, 0, 8, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5984, 121101, 0, 1, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5985, 121101, 0, 1, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5986, 121101, 0, 1, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 5987, 121101, 0, 1, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 5988, 121101, 0, 4, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 5989, 121101, 0, 8, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 5990, 121101, 0, 1, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 5991, 121101, 0, 1, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 5992, 121101, 0, 1, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 5994, 121101, 0, 1, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 5995, 121101, 0, 1, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 5996, 121101, 0, 1, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 5997, 121101, 0, 1, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 5999, 121101, 0, 1, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6000, 121101, 0, 1, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6002, 118101, 0, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6003, 118101, 0, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6004, 118101, 0, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6005, 118101, 0, 1, "～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6006, 118101, 0, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6007, 118101, 0, 4, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6008, 118101, 0, 8, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6009, 118101, 0, 1, "ええと…\nお腹、空いてる？\nおねむじゃない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6010, 118101, 0, 1, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6011, 118101, 0, 1, "お喋り…？\nわたしでいいの…？\nうれしい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6012, 118101, 0, 1, "あなたの好きな歌…\n教えて…？\n練習、するから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6013, 118101, 0, 4, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6014, 118101, 0, 8, "安心、して…\nわたしの歌で、\nあなたを守るから…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6015, 118101, 0, 1, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6016, 118101, 0, 1, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6017, 118101, 0, 1, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6019, 118101, 0, 1, "こんな特等席…\n恐縮、です…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6020, 118101, 0, 1, "い、今のうちに、\nこの子でお世話の\n練習を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6021, 118101, 0, 1, "いい香り…\n…つらい気持ちも\n癒してくれる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6022, 118101, 0, 1, "あったかい…\nいつまでも、\nこうしていられそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6024, 118101, 0, 1, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6025, 118101, 0, 1, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6027, 121301, 0, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6028, 121301, 0, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6029, 121301, 0, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6030, 121301, 0, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6031, 121301, 0, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6032, 121301, 0, 4, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6033, 121301, 0, 8, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6034, 121301, 0, 1, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6035, 121301, 0, 1, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6036, 121301, 0, 1, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6037, 121301, 0, 1, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6038, 121301, 0, 4, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6039, 121301, 0, 8, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6040, 121301, 0, 1, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6041, 121301, 0, 1, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6042, 121301, 0, 1, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6044, 121301, 0, 1, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6045, 121301, 0, 1, "今日も演技の\n練習に付き合ってね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6046, 121301, 0, 1, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6047, 121301, 0, 1, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6049, 121301, 0, 1, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6050, 121301, 0, 1, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6052, 107501, 0, 12, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6053, 107501, 0, 12, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6054, 107601, 0, 12, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6055, 107601, 0, 12, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6056, 107801, 0, 12, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6057, 107801, 0, 12, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6058, 121401, 0, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6059, 121401, 0, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6060, 121401, 0, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6061, 121401, 0, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6062, 121401, 0, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6063, 121401, 0, 4, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6064, 121401, 0, 8, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6065, 121401, 0, 1, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6066, 121401, 0, 1, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6067, 121401, 0, 1, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6068, 121401, 0, 1, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6069, 121401, 0, 4, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6070, 121401, 0, 8, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6071, 121401, 0, 1, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6072, 121401, 0, 1, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6073, 121401, 0, 1, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6075, 121401, 0, 1, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6076, 121401, 0, 1, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6077, 121401, 0, 1, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6078, 121401, 0, 1, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6080, 121401, 0, 1, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6081, 121401, 0, 1, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6083, 121501, 0, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6084, 121501, 0, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6085, 121501, 0, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6086, 121501, 0, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6087, 121501, 0, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6088, 121501, 0, 4, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6089, 121501, 0, 8, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6090, 121501, 0, 1, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6091, 121501, 0, 1, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6092, 121501, 0, 1, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6093, 121501, 0, 1, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6094, 121501, 0, 4, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6095, 121501, 0, 8, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6096, 121501, 0, 1, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6097, 121501, 0, 1, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6098, 121501, 0, 1, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6100, 121501, 0, 1, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6101, 121501, 0, 1, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6102, 121501, 0, 1, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6103, 121501, 0, 1, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6105, 121501, 0, 1, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6106, 121501, 0, 1, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6108, 104501, 0, 12, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6109, 104501, 0, 12, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6110, 121601, 0, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6111, 121601, 0, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6112, 121601, 0, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6113, 121601, 0, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6114, 121601, 0, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6115, 121601, 0, 4, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6116, 121601, 0, 8, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6117, 121601, 0, 1, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6118, 121601, 0, 1, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6119, 121601, 0, 1, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6120, 121601, 0, 1, "しーっ！\n今、尾行中なんです\n話はあとで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6121, 121601, 0, 4, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6122, 121601, 0, 8, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6123, 121601, 0, 1, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6124, 121601, 0, 1, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6125, 121601, 0, 1, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6127, 121601, 0, 1, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6128, 121601, 0, 1, "くう…！\nこのかわいさは\n盗めないです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6129, 121601, 0, 1, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6130, 121601, 0, 1, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6132, 121601, 0, 1, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6133, 121601, 0, 1, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6135, 121701, 0, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6136, 121701, 0, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6137, 121701, 0, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6138, 121701, 0, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6139, 121701, 0, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6140, 121701, 0, 4, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6141, 121701, 0, 8, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6142, 121701, 0, 1, "ふふ、ごきげんよう\n私、絶好調でしてよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6143, 121701, 0, 1, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6144, 121701, 0, 1, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6145, 121701, 0, 1, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6146, 121701, 0, 4, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6147, 121701, 0, 8, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6148, 121701, 0, 1, "さあ！\n今日も一日\n全力で参りますわ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6149, 121701, 0, 1, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6150, 121701, 0, 1, "もう少しだけ…\n明日の準備をして\nおかないと…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6152, 121701, 0, 1, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6153, 121701, 0, 1, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6154, 121701, 0, 1, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6155, 121701, 0, 1, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6157, 121701, 0, 1, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6158, 121701, 0, 1, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6161, 121801, 0, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6162, 121801, 0, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6163, 121801, 0, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6164, 121801, 0, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6165, 121801, 0, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6166, 121801, 0, 4, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6167, 121801, 0, 8, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6168, 121801, 0, 1, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6169, 121801, 0, 1, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6170, 121801, 0, 1, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6171, 121801, 0, 1, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6172, 121801, 0, 4, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6173, 121801, 0, 8, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6174, 121801, 0, 1, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6175, 121801, 0, 1, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6176, 121801, 0, 1, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6178, 121801, 0, 1, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6179, 121801, 0, 1, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6180, 121801, 0, 1, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6181, 121801, 0, 1, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6183, 121801, 0, 1, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6184, 121801, 0, 1, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6187, 122001, 0, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6188, 122001, 0, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6189, 122001, 0, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6190, 122001, 0, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6191, 122001, 0, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6192, 122001, 0, 4, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6193, 122001, 0, 8, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6194, 122001, 0, 1, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6195, 122001, 0, 1, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6196, 122001, 0, 1, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6197, 122001, 0, 1, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6198, 122001, 0, 4, "船の上では食材が\n限られるので料理の\nいい練習になります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6199, 122001, 0, 8, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6200, 122001, 0, 1, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6201, 122001, 0, 1, "船の上では\n規則正しい生活が\n重要ですから", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6202, 122001, 0, 1, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6204, 122001, 0, 1, "海に揺られるような\n心地よさですね\n気が休まります…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6205, 122001, 0, 1, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6206, 122001, 0, 1, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6207, 122001, 0, 1, "温かい…\n潮風で冷えた体に\n沁みますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6209, 122001, 0, 1, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6210, 122001, 0, 1, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6212, 102601, 0, 12, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6213, 102601, 0, 12, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6214, 121901, 0, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6215, 121901, 0, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6216, 121901, 0, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6217, 121901, 0, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6218, 121901, 0, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6219, 121901, 0, 4, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6220, 121901, 0, 8, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6221, 121901, 0, 1, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6222, 121901, 0, 1, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6223, 121901, 0, 1, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6224, 121901, 0, 1, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6225, 121901, 0, 4, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6226, 121901, 0, 8, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6227, 121901, 0, 1, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6228, 121901, 0, 1, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6229, 121901, 0, 1, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6231, 121901, 0, 1, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6232, 121901, 0, 1, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6233, 121901, 0, 1, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6234, 121901, 0, 1, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6236, 121901, 0, 1, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6237, 121901, 0, 1, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6239, 122101, 0, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6240, 122101, 0, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6241, 122101, 0, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6242, 122101, 0, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6243, 122101, 0, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6244, 122101, 0, 4, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6245, 122101, 0, 8, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6246, 122101, 0, 1, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6247, 122101, 0, 1, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6248, 122101, 0, 1, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6249, 122101, 0, 1, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6250, 122101, 0, 4, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6251, 122101, 0, 8, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6252, 122101, 0, 1, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6253, 122101, 0, 1, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6254, 122101, 0, 1, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6256, 122101, 0, 1, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6257, 122101, 0, 1, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6258, 122101, 0, 1, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6259, 122101, 0, 1, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6261, 122101, 0, 1, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6262, 122101, 0, 1, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6264, 122201, 0, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6265, 122201, 0, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6266, 122201, 0, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6267, 122201, 0, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6268, 122201, 0, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6269, 122201, 0, 4, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6270, 122201, 0, 8, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6271, 122201, 0, 1, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6272, 122201, 0, 1, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6273, 122201, 0, 1, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6274, 122201, 0, 1, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6275, 122201, 0, 4, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6276, 122201, 0, 8, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6277, 122201, 0, 1, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6278, 122201, 0, 1, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6279, 122201, 0, 1, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6281, 122201, 0, 1, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6282, 122201, 0, 1, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6283, 122201, 0, 1, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6284, 122201, 0, 1, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6286, 122201, 0, 1, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6287, 122201, 0, 1, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6289, 100701, 0, 12, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6290, 100701, 0, 12, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6291, 122301, 0, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6292, 122301, 0, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6293, 122301, 0, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6294, 122301, 0, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6295, 122301, 0, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6296, 122301, 0, 4, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6297, 122301, 0, 8, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6298, 122301, 0, 1, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6299, 122301, 0, 1, "よしよし！\n今日のけんぞくも\nいい子だね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6300, 122301, 0, 1, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6301, 122301, 0, 1, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6302, 122301, 0, 4, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6303, 122301, 0, 8, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6304, 122301, 0, 1, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6305, 122301, 0, 1, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6306, 122301, 0, 1, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6308, 122301, 0, 1, "わー！\nふかふかだ！\nきもちぃー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6309, 122301, 0, 1, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6310, 122301, 0, 1, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6311, 122301, 0, 1, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6313, 122301, 0, 1, "はむはむ…\nんー！\nおいしー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6314, 122301, 0, 1, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6316, 122501, 0, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6317, 122501, 0, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6318, 122501, 0, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6319, 122501, 0, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6320, 122501, 0, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6321, 122501, 0, 4, "終わらない夏は\nないけれど…\nつい願ってしまうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6322, 122501, 0, 8, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6323, 122501, 0, 1, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6324, 122501, 0, 1, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6325, 122501, 0, 1, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6326, 122501, 0, 1, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6327, 122501, 0, 4, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6328, 122501, 0, 8, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6329, 122501, 0, 1, "潮騒の音で\n目覚めるのも\nいいものだね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6330, 122501, 0, 1, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6331, 122501, 0, 1, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6333, 122501, 0, 1, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6334, 122501, 0, 1, "肌触りのよさが\nこの格好だと\nよく分かるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6335, 122501, 0, 1, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6336, 122501, 0, 1, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6338, 122501, 0, 1, "夏を楽しみきるには\nエネルギーが\n必要だからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6339, 122501, 0, 1, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6341, 122501, 0, 1, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6342, 122501, 0, 1, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6343, 122401, 0, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6344, 122401, 0, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6345, 122401, 0, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6346, 122401, 0, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6347, 122401, 0, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6348, 122401, 0, 4, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6349, 122401, 0, 8, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6350, 122401, 0, 1, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6351, 122401, 0, 1, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6352, 122401, 0, 1, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6353, 122401, 0, 1, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6354, 122401, 0, 4, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6355, 122401, 0, 8, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6356, 122401, 0, 1, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6357, 122401, 0, 1, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6358, 122401, 0, 1, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6360, 122401, 0, 1, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6361, 122401, 0, 1, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6362, 122401, 0, 1, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6363, 122401, 0, 1, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6365, 122401, 0, 1, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6366, 122401, 0, 1, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6368, 122401, 0, 1, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6369, 122401, 0, 1, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6370, 101501, 0, 12, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6371, 101501, 0, 12, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6372, 122601, 0, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6373, 122601, 0, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6374, 122601, 0, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6375, 122601, 0, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6376, 122601, 0, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6377, 122601, 0, 4, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6378, 122601, 0, 8, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6379, 122601, 0, 1, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6380, 122601, 0, 1, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6381, 122601, 0, 1, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6382, 122601, 0, 1, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6383, 122601, 0, 4, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6384, 122601, 0, 8, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6385, 122601, 0, 1, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6386, 122601, 0, 1, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6387, 122601, 0, 1, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6389, 122601, 0, 1, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6390, 122601, 0, 1, "かわいい～♪\n日光浴させて\nあげたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6391, 122601, 0, 1, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6392, 122601, 0, 1, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6394, 122601, 0, 1, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6395, 122601, 0, 1, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6397, 122601, 0, 1, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6398, 122601, 0, 1, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6399, 122901, 0, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6400, 122901, 0, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6401, 122901, 0, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6402, 122901, 0, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6403, 122901, 0, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6404, 122901, 0, 4, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6405, 122901, 0, 8, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6406, 122901, 0, 1, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6407, 122901, 0, 1, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6408, 122901, 0, 1, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6409, 122901, 0, 1, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6410, 122901, 0, 4, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6411, 122901, 0, 8, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6412, 122901, 0, 1, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6413, 122901, 0, 1, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6414, 122901, 0, 1, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6416, 122901, 0, 1, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6417, 122901, 0, 1, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6418, 122901, 0, 1, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6419, 122901, 0, 1, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6421, 122901, 0, 1, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6422, 122901, 0, 1, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6424, 122901, 0, 1, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6425, 122901, 0, 1, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6426, 122801, 0, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6427, 122801, 0, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6428, 122801, 0, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6429, 122801, 0, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6430, 122801, 0, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6431, 122801, 0, 4, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6432, 122801, 0, 8, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6433, 122801, 0, 1, "にいちゃん！\n早く海に行く準備\nしなよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6434, 122801, 0, 1, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6435, 122801, 0, 1, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6436, 122801, 0, 1, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6437, 122801, 0, 4, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6438, 122801, 0, 8, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6439, 122801, 0, 1, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6440, 122801, 0, 1, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6441, 122801, 0, 1, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6443, 122801, 0, 1, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6444, 122801, 0, 1, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6445, 122801, 0, 1, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6446, 122801, 0, 1, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6448, 122801, 0, 1, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6449, 122801, 0, 1, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6451, 122801, 0, 1, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6452, 122801, 0, 1, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6453, 102701, 0, 12, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6454, 102701, 0, 12, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6455, 122701, 0, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6456, 122701, 0, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6457, 122701, 0, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6458, 122701, 0, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6459, 122701, 0, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6460, 122701, 0, 4, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6461, 122701, 0, 8, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6462, 122701, 0, 1, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6463, 122701, 0, 1, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6464, 122701, 0, 1, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6465, 122701, 0, 1, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6466, 122701, 0, 4, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6467, 122701, 0, 8, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6468, 122701, 0, 1, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6469, 122701, 0, 1, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6470, 122701, 0, 1, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6472, 122701, 0, 1, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6473, 122701, 0, 1, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6474, 122701, 0, 1, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6475, 122701, 0, 1, "濡れた体を\n乾かしましょう\nタオル、タオル…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6477, 122701, 0, 1, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6478, 122701, 0, 1, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 6480, 122701, 0, 1, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 6481, 122701, 0, 1, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6482, 123001, 0, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6483, 123001, 0, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6484, 123001, 0, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6485, 123001, 0, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6486, 123001, 0, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6487, 123001, 0, 4, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6488, 123001, 0, 8, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6489, 123001, 0, 1, "おいしい店とか\n知らないの？\n食べに行きましょ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6490, 123001, 0, 1, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6491, 123001, 0, 1, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6492, 123001, 0, 1, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6493, 123001, 0, 4, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6494, 123001, 0, 8, "どこまでだって\nあんたと一緒に\nいてやるんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6495, 123001, 0, 1, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6496, 123001, 0, 1, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6497, 123001, 0, 1, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6499, 123001, 0, 1, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6500, 123001, 0, 1, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6501, 123001, 0, 1, "いい香りね\n塔にもこういうの\n置いておこうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6502, 123001, 0, 1, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6504, 123001, 0, 1, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6505, 123001, 0, 1, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6507, 123101, 0, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6508, 123101, 0, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6509, 123101, 0, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6510, 123101, 0, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6511, 123101, 0, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6512, 123101, 0, 4, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6513, 123101, 0, 8, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6514, 123101, 0, 1, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6515, 123101, 0, 1, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6516, 123101, 0, 1, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6517, 123101, 0, 1, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6518, 123101, 0, 4, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6519, 123101, 0, 8, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6520, 123101, 0, 1, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6521, 123101, 0, 1, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6522, 123101, 0, 1, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6524, 123101, 0, 1, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6525, 123101, 0, 1, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6526, 123101, 0, 1, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6527, 123101, 0, 1, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6529, 123101, 0, 1, "この料理…\n冒険するときに\n作ってみまひょか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6530, 123101, 0, 1, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6532, 103101, 0, 12, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6533, 103101, 0, 12, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6534, 104401, 0, 12, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6535, 104401, 0, 12, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6536, 123201, 0, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6537, 123201, 0, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6538, 123201, 0, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6539, 123201, 0, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6540, 123201, 0, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6541, 123201, 0, 4, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6542, 123201, 0, 8, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6543, 123201, 0, 1, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6544, 123201, 0, 1, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6545, 123201, 0, 1, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6546, 123201, 0, 1, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6547, 123201, 0, 4, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6548, 123201, 0, 8, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6549, 123201, 0, 1, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6550, 123201, 0, 1, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6551, 123201, 0, 1, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6553, 123201, 0, 1, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6554, 123201, 0, 1, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6555, 123201, 0, 1, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6556, 123201, 0, 1, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6558, 123201, 0, 1, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6559, 123201, 0, 1, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6561, 123301, 0, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6562, 123301, 0, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6563, 123301, 0, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6564, 123301, 0, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6565, 123301, 0, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6566, 123301, 0, 4, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6567, 123301, 0, 8, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6568, 123301, 0, 1, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6569, 123301, 0, 1, "んー？\n今はだるいからパース\nあとでねー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6570, 123301, 0, 1, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6571, 123301, 0, 1, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6572, 123301, 0, 4, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6573, 123301, 0, 8, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6574, 123301, 0, 1, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6575, 123301, 0, 1, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6576, 123301, 0, 1, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6578, 123301, 0, 1, "あーラクー\nいっしょーここで\nキューケーできるわー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6579, 123301, 0, 1, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6580, 123301, 0, 1, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6581, 123301, 0, 1, "あったか～♪\n溶けてスライム\n戻りそ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6583, 123301, 0, 1, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6584, 123301, 0, 1, "わ～なんかムズー\n読めなー\nウケるー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6586, 123501, 0, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6587, 123501, 0, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6588, 123501, 0, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6589, 123501, 0, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6590, 123501, 0, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6591, 123501, 0, 4, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6592, 123501, 0, 8, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6593, 123501, 0, 1, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6594, 123501, 0, 1, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6595, 123501, 0, 1, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6596, 123501, 0, 1, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6597, 123501, 0, 4, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6598, 123501, 0, 8, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6599, 123501, 0, 1, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6600, 123501, 0, 1, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6601, 123501, 0, 1, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6603, 123501, 0, 1, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6604, 123501, 0, 1, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6605, 123501, 0, 1, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6606, 123501, 0, 1, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6608, 123501, 0, 1, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6609, 123501, 0, 1, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 6611, 123501, 0, 1, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6613, 123601, 0, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6614, 123601, 0, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6615, 123601, 0, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6616, 123601, 0, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6617, 123601, 0, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6618, 123601, 0, 4, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6619, 123601, 0, 8, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6620, 123601, 0, 1, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6621, 123601, 0, 1, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6622, 123601, 0, 1, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6623, 123601, 0, 1, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6624, 123601, 0, 4, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6625, 123601, 0, 8, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6626, 123601, 0, 1, "おはよう！\n今日も朝から\nパトロールに行こう！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6627, 123601, 0, 1, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6628, 123601, 0, 1, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6630, 123601, 0, 1, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6631, 123601, 0, 1, "…かわいい\nこの子もパレードに\n連れて行きたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6632, 123601, 0, 1, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6633, 123601, 0, 1, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6635, 123601, 0, 1, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6636, 123601, 0, 1, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 6638, 123601, 0, 1, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6640, 103701, 0, 12, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6641, 103701, 0, 12, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6642, 123701, 0, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6643, 123701, 0, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6644, 123701, 0, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6645, 123701, 0, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6646, 123701, 0, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6647, 123701, 0, 4, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6648, 123701, 0, 8, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6649, 123701, 0, 1, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6650, 123701, 0, 1, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6651, 123701, 0, 1, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6652, 123701, 0, 1, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6653, 123701, 0, 4, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6654, 123701, 0, 8, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6655, 123701, 0, 1, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6656, 123701, 0, 1, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6657, 123701, 0, 1, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6659, 123701, 0, 1, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6660, 123701, 0, 1, "いや～\nかわいいものをみると\n癒されますな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6661, 123701, 0, 1, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6662, 123701, 0, 1, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6664, 123701, 0, 1, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6665, 123701, 0, 1, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 12, 3, 6667, 123701, 0, 1, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6669, 123801, 0, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6670, 123801, 0, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6671, 123801, 0, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6672, 123801, 0, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6673, 123801, 0, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6674, 123801, 0, 4, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6675, 123801, 0, 8, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6676, 123801, 0, 1, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6677, 123801, 0, 1, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6678, 123801, 0, 1, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6679, 123801, 0, 1, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6680, 123801, 0, 4, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6681, 123801, 0, 8, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6682, 123801, 0, 1, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6683, 123801, 0, 1, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6684, 123801, 0, 1, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6686, 123801, 0, 1, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6687, 123801, 0, 1, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6688, 123801, 0, 1, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6689, 123801, 0, 1, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6691, 123801, 0, 1, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6692, 123801, 0, 1, "ウサギ族は地獄耳？\nワタシとの共通点だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6694, 106401, 0, 12, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6695, 106401, 0, 12, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6696, 123901, 0, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6697, 123901, 0, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6698, 123901, 0, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6699, 123901, 0, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6700, 123901, 0, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6701, 123901, 0, 4, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6702, 123901, 0, 8, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6703, 123901, 0, 1, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6704, 123901, 0, 1, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6705, 123901, 0, 1, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6706, 123901, 0, 1, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6707, 123901, 0, 4, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6708, 123901, 0, 8, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6709, 123901, 0, 1, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6710, 123901, 0, 1, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6711, 123901, 0, 1, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6713, 123901, 0, 1, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6714, 123901, 0, 1, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6715, 123901, 0, 1, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6716, 123901, 0, 1, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6718, 123901, 0, 1, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6719, 123901, 0, 1, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6721, 106901, 0, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6722, 106901, 0, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6723, 106901, 0, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6724, 106901, 0, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6725, 106901, 0, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6726, 106901, 0, 4, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6727, 106901, 0, 8, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6728, 106901, 0, 1, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6729, 106901, 0, 1, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6730, 106901, 0, 1, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6731, 106901, 0, 1, "視力は権能に\n影響しないわよ\n当たり前じゃない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6732, 106901, 0, 4, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6733, 106901, 0, 8, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6734, 106901, 0, 1, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6735, 106901, 0, 1, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6736, 106901, 0, 1, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6738, 106901, 0, 1, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6739, 106901, 0, 1, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6740, 106901, 0, 1, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6741, 106901, 0, 1, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6743, 106901, 0, 1, "栄養価はともかく\n味のバランスは\n悪くないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6744, 106901, 0, 1, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6746, 124001, 0, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6747, 124001, 0, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6748, 124001, 0, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6749, 124001, 0, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6750, 124001, 0, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6751, 124001, 0, 4, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6752, 124001, 0, 8, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6753, 124001, 0, 1, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6754, 124001, 0, 1, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6755, 124001, 0, 1, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6756, 124001, 0, 1, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6757, 124001, 0, 4, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6758, 124001, 0, 8, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6759, 124001, 0, 1, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6760, 124001, 0, 1, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6761, 124001, 0, 1, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6763, 124001, 0, 1, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6764, 124001, 0, 1, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6765, 124001, 0, 1, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6766, 124001, 0, 1, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6768, 124001, 0, 1, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6769, 124001, 0, 1, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 6771, 124001, 0, 1, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 6772, 124001, 0, 1, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6773, 124101, 0, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6774, 124101, 0, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6775, 124101, 0, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6776, 124101, 0, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6777, 124101, 0, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6778, 124101, 0, 4, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6779, 124101, 0, 8, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6780, 124101, 0, 1, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6781, 124101, 0, 1, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6782, 124101, 0, 1, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6783, 124101, 0, 1, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6784, 124101, 0, 4, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6785, 124101, 0, 8, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6786, 124101, 0, 1, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6787, 124101, 0, 1, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6788, 124101, 0, 1, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6790, 124101, 0, 1, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6791, 124101, 0, 1, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6792, 124101, 0, 1, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6793, 124101, 0, 1, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6795, 124101, 0, 1, "お腹いっぱい\n食べられるのも\n幸せなことよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6796, 124101, 0, 1, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 6798, 124101, 0, 1, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 6799, 124101, 0, 1, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6800, 105101, 0, 12, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6801, 105101, 0, 12, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6802, 124201, 0, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6803, 124201, 0, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6804, 124201, 0, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6805, 124201, 0, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6806, 124201, 0, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6807, 124201, 0, 4, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6808, 124201, 0, 8, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6809, 124201, 0, 1, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6810, 124201, 0, 1, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6811, 124201, 0, 1, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6812, 124201, 0, 1, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6813, 124201, 0, 4, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6814, 124201, 0, 8, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6815, 124201, 0, 1, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6816, 124201, 0, 1, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6817, 124201, 0, 1, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6819, 124201, 0, 1, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6820, 124201, 0, 1, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6821, 124201, 0, 1, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6822, 124201, 0, 1, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6824, 124201, 0, 1, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6825, 124201, 0, 1, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 6827, 124201, 0, 1, "わっ！\n…………いや\n驚いてなどいない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 6828, 124201, 0, 1, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6829, 124301, 0, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6830, 124301, 0, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6831, 124301, 0, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6832, 124301, 0, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6833, 124301, 0, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6834, 124301, 0, 4, "私たちも\nあの人を見習って\nバイトとかしよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6835, 124301, 0, 8, "いつかあの人を\n私たちの学院に\n招待したいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6836, 124301, 0, 1, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6837, 124301, 0, 1, "これからなにするの？\n私たちもついて行って\nいいかな！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6838, 124301, 0, 1, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6839, 124301, 0, 1, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6840, 124301, 0, 4, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6841, 124301, 0, 8, "あなたは\nグレアと私の友達！\nこれからもずっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6842, 124301, 0, 1, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6843, 124301, 0, 1, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6844, 124301, 0, 1, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6846, 124301, 0, 1, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6847, 124301, 0, 1, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6848, 124301, 0, 1, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6849, 124301, 0, 1, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6851, 124301, 0, 1, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6852, 124301, 0, 1, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6855, 124401, 0, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6856, 124401, 0, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6857, 124401, 0, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6858, 124401, 0, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6859, 124401, 0, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6860, 124401, 0, 4, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6861, 124401, 0, 8, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6862, 124401, 0, 1, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6863, 124401, 0, 1, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6864, 124401, 0, 1, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6865, 124401, 0, 1, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6866, 124401, 0, 4, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6867, 124401, 0, 8, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6868, 124401, 0, 1, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6869, 124401, 0, 1, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6870, 124401, 0, 1, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6872, 124401, 0, 1, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6873, 124401, 0, 1, "これ…\nアンに見せたら\n喜ぶだろうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6874, 124401, 0, 1, "いい香り…\nアンの感想も\n聞いてみたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6875, 124401, 0, 1, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6877, 124401, 0, 1, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6878, 124401, 0, 1, "アンがおススメ\nしてくれた本は\nこれかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6881, 124701, 0, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6882, 124701, 0, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6883, 124701, 0, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6884, 124701, 0, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6885, 124701, 0, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6886, 124701, 0, 4, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6887, 124701, 0, 8, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6888, 124701, 0, 1, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6889, 124701, 0, 1, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6890, 124701, 0, 1, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6891, 124701, 0, 1, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6892, 124701, 0, 4, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6893, 124701, 0, 8, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6894, 124701, 0, 1, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6895, 124701, 0, 1, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6896, 124701, 0, 1, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6898, 124701, 0, 1, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6899, 124701, 0, 1, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6900, 124701, 0, 1, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6901, 124701, 0, 1, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6903, 124701, 0, 1, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6904, 124701, 0, 1, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6906, 124601, 0, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6907, 124601, 0, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6908, 124601, 0, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6909, 124601, 0, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6910, 124601, 0, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6911, 124601, 0, 4, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6912, 124601, 0, 8, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6913, 124601, 0, 1, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6914, 124601, 0, 1, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6915, 124601, 0, 1, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6916, 124601, 0, 1, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6917, 124601, 0, 4, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6918, 124601, 0, 8, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6919, 124601, 0, 1, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6920, 124601, 0, 1, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6921, 124601, 0, 1, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6923, 124601, 0, 1, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6924, 124601, 0, 1, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6925, 124601, 0, 1, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6926, 124601, 0, 1, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6928, 124601, 0, 1, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6929, 124601, 0, 1, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 6931, 104701, 0, 12, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 6932, 104701, 0, 12, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6933, 124501, 0, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6934, 124501, 0, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6935, 124501, 0, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6936, 124501, 0, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6937, 124501, 0, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6938, 124501, 0, 4, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6939, 124501, 0, 8, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6940, 124501, 0, 1, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6941, 124501, 0, 1, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6942, 124501, 0, 1, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6943, 124501, 0, 1, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6944, 124501, 0, 4, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6945, 124501, 0, 8, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6946, 124501, 0, 1, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6947, 124501, 0, 1, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6948, 124501, 0, 1, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6950, 124501, 0, 1, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6951, 124501, 0, 1, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6952, 124501, 0, 1, "いい香りだけど\n着物に移らないように\n気をつけないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6953, 124501, 0, 1, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6955, 124501, 0, 1, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6956, 124501, 0, 1, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6958, 124801, 0, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6959, 124801, 0, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6960, 124801, 0, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6961, 124801, 0, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6962, 124801, 0, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6963, 124801, 0, 4, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6964, 124801, 0, 8, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6965, 124801, 0, 1, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6966, 124801, 0, 1, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6967, 124801, 0, 1, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6968, 124801, 0, 1, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6969, 124801, 0, 4, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6970, 124801, 0, 8, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6971, 124801, 0, 1, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6972, 124801, 0, 1, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6973, 124801, 0, 1, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 6975, 124801, 0, 1, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 6976, 124801, 0, 1, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 6977, 124801, 0, 1, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 6978, 124801, 0, 1, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 6980, 124801, 0, 1, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 6981, 124801, 0, 1, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 6983, 124901, 0, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 6984, 124901, 0, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 6985, 124901, 0, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 6986, 124901, 0, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 6987, 124901, 0, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 6988, 124901, 0, 4, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 6989, 124901, 0, 8, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6990, 124901, 0, 1, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6991, 124901, 0, 1, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6992, 124901, 0, 1, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 6993, 124901, 0, 1, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 6994, 124901, 0, 4, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 6995, 124901, 0, 8, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 6996, 124901, 0, 1, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 6997, 124901, 0, 1, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 6998, 124901, 0, 1, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7000, 124901, 0, 1, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7001, 124901, 0, 1, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7002, 124901, 0, 1, "嗅いだことない\n香りだな\nなかなか気に入った！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7003, 124901, 0, 1, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7005, 124901, 0, 1, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7006, 124901, 0, 1, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7008, 105501, 0, 12, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7009, 105501, 0, 12, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7010, 125001, 0, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7011, 125001, 0, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7012, 125001, 0, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7013, 125001, 0, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7014, 125001, 0, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7015, 125001, 0, 4, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7016, 125001, 0, 8, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7017, 125001, 0, 1, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7018, 125001, 0, 1, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7019, 125001, 0, 1, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7020, 125001, 0, 1, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7021, 125001, 0, 4, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7022, 125001, 0, 8, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7023, 125001, 0, 1, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7024, 125001, 0, 1, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7025, 125001, 0, 1, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7027, 125001, 0, 1, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7028, 125001, 0, 1, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7029, 125001, 0, 1, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7030, 125001, 0, 1, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7032, 125001, 0, 1, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7033, 125001, 0, 1, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7035, 118201, 0, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7036, 118201, 0, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7037, 118201, 0, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7038, 118201, 0, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7039, 118201, 0, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7040, 118201, 0, 4, "退屈ですねー\n騎士さん、構って\nくれないかなー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7041, 118201, 0, 8, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7042, 118201, 0, 1, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7043, 118201, 0, 1, "お出かけします？\nそれともおうちで\nまったりしますー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7044, 118201, 0, 1, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7045, 118201, 0, 1, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7046, 118201, 0, 4, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7047, 118201, 0, 8, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7048, 118201, 0, 1, "おはようございます★\nあれれー？\nまだおねむですかー？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7049, 118201, 0, 1, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7050, 118201, 0, 1, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7052, 118201, 0, 1, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7053, 118201, 0, 1, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7054, 118201, 0, 1, "くんくん★\nうーん…\nたぶん素敵な香り♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7055, 118201, 0, 1, "あったかーい★\n心も体も\nぽっかぽかですねー★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7057, 118201, 0, 1, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7058, 118201, 0, 1, "へー★\nほほー★\nふんふーん★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7060, 125101, 0, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7061, 125101, 0, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7062, 125101, 0, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7063, 125101, 0, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7064, 125101, 0, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7065, 125101, 0, 4, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7066, 125101, 0, 8, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7067, 125101, 0, 1, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7068, 125101, 0, 1, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7069, 125101, 0, 1, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7070, 125101, 0, 1, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7071, 125101, 0, 4, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7072, 125101, 0, 8, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7073, 125101, 0, 1, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7074, 125101, 0, 1, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7075, 125101, 0, 1, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7077, 125101, 0, 1, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7078, 125101, 0, 1, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7079, 125101, 0, 1, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7080, 125101, 0, 1, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7082, 125101, 0, 1, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7083, 125101, 0, 1, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7085, 107101, 0, 12, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7086, 107101, 0, 12, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7087, 125201, 0, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7088, 125201, 0, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7089, 125201, 0, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7090, 125201, 0, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7091, 125201, 0, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7092, 125201, 0, 4, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7093, 125201, 0, 8, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7094, 125201, 0, 1, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7095, 125201, 0, 1, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7096, 125201, 0, 1, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7097, 125201, 0, 1, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7098, 125201, 0, 4, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7099, 125201, 0, 8, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7100, 125201, 0, 1, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7101, 125201, 0, 1, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7102, 125201, 0, 1, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7104, 125201, 0, 1, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7105, 125201, 0, 1, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7106, 125201, 0, 1, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7107, 125201, 0, 1, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7109, 125201, 0, 1, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7110, 125201, 0, 1, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7112, 120001, 0, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7113, 120001, 0, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7114, 120001, 0, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7115, 120001, 0, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7116, 120001, 0, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7117, 120001, 0, 4, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7118, 120001, 0, 8, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7119, 120001, 0, 1, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7120, 120001, 0, 1, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7121, 120001, 0, 1, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7122, 120001, 0, 1, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7123, 120001, 0, 4, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7124, 120001, 0, 8, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7125, 120001, 0, 1, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7126, 120001, 0, 1, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7127, 120001, 0, 1, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7129, 120001, 0, 1, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7130, 120001, 0, 1, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7131, 120001, 0, 1, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7132, 120001, 0, 1, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7134, 120001, 0, 1, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7135, 120001, 0, 1, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7137, 125301, 0, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7138, 125301, 0, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7139, 125301, 0, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7140, 125301, 0, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7141, 125301, 0, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7142, 125301, 0, 4, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7143, 125301, 0, 8, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7144, 125301, 0, 1, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7145, 125301, 0, 1, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7146, 125301, 0, 1, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7147, 125301, 0, 1, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7148, 125301, 0, 4, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7149, 125301, 0, 8, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7150, 125301, 0, 1, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7151, 125301, 0, 1, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7152, 125301, 0, 1, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7154, 125301, 0, 1, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7155, 125301, 0, 1, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7156, 125301, 0, 1, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7157, 125301, 0, 1, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7159, 125301, 0, 1, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7160, 125301, 0, 1, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7162, 100901, 0, 12, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7163, 100901, 0, 12, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7164, 125401, 0, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7165, 125401, 0, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7166, 125401, 0, 1, "そろそろ\nパトロールの\n時間だ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7167, 125401, 0, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7168, 125401, 0, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7169, 125401, 0, 4, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7170, 125401, 0, 8, "こんな私にも\nできることはある…\nそう信じています", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7171, 125401, 0, 1, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7172, 125401, 0, 1, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7173, 125401, 0, 1, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7174, 125401, 0, 1, "ニャットちゃん\nあなたにも\n懐いてきましたね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7175, 125401, 0, 4, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7176, 125401, 0, 8, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7177, 125401, 0, 1, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7178, 125401, 0, 1, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7179, 125401, 0, 1, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7181, 125401, 0, 1, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7182, 125401, 0, 1, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7183, 125401, 0, 1, "森の香りがします…\n今日はよく\n眠れそうです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7184, 125401, 0, 1, "ニャットちゃんと\n同じぐらい\n暖かいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7186, 125401, 0, 1, "レンジャーの仕事を\nしていると\nお腹が空きます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7187, 125401, 0, 1, "どんなに忙しくても\n読書だけは\nやめられませんね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7189, 125801, 0, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7190, 125801, 0, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7191, 125801, 0, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7192, 125801, 0, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7193, 125801, 0, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7194, 125801, 0, 4, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7195, 125801, 0, 8, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7196, 125801, 0, 1, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7197, 125801, 0, 1, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7198, 125801, 0, 1, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7199, 125801, 0, 1, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7200, 125801, 0, 4, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7201, 125801, 0, 8, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7202, 125801, 0, 1, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7203, 125801, 0, 1, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7204, 125801, 0, 1, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7206, 125801, 0, 1, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7207, 125801, 0, 1, "と、飛びかかっては\nこない…ですよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7208, 125801, 0, 1, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7209, 125801, 0, 1, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7211, 125801, 0, 1, "こんなに食べていたら\nはしたない…\nでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7212, 125801, 0, 1, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7214, 125501, 0, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7215, 125501, 0, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7216, 125501, 0, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7217, 125501, 0, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7218, 125501, 0, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7219, 125501, 0, 4, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7220, 125501, 0, 8, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7221, 125501, 0, 1, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7222, 125501, 0, 1, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7223, 125501, 0, 1, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7224, 125501, 0, 1, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7225, 125501, 0, 4, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7226, 125501, 0, 8, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7227, 125501, 0, 1, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7228, 125501, 0, 1, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7229, 125501, 0, 1, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7231, 125501, 0, 1, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7232, 125501, 0, 1, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7233, 125501, 0, 1, "いい匂い…\nそれになんだか\n集中力が増したかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7234, 125501, 0, 1, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7236, 125501, 0, 1, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7237, 125501, 0, 1, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7239, 125701, 0, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7240, 125701, 0, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7241, 125701, 0, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7242, 125701, 0, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7243, 125701, 0, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7244, 125701, 0, 4, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7245, 125701, 0, 8, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7246, 125701, 0, 1, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7247, 125701, 0, 1, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7248, 125701, 0, 1, "依頼で気になる\nところがあれば\n何でもご確認ください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7249, 125701, 0, 1, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7250, 125701, 0, 4, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7251, 125701, 0, 8, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7252, 125701, 0, 1, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7253, 125701, 0, 1, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7254, 125701, 0, 1, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7256, 125701, 0, 1, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7257, 125701, 0, 1, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7258, 125701, 0, 1, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7259, 125701, 0, 1, "この火力…\n錬金術の調合に\n使えそうですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7261, 125701, 0, 1, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7262, 125701, 0, 1, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7264, 106101, 0, 12, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7265, 106101, 0, 12, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7266, 125601, 0, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7267, 125601, 0, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7268, 125601, 0, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7269, 125601, 0, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7270, 125601, 0, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7271, 125601, 0, 4, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7272, 125601, 0, 8, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7273, 125601, 0, 1, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7274, 125601, 0, 1, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7275, 125601, 0, 1, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7276, 125601, 0, 1, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7277, 125601, 0, 4, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7278, 125601, 0, 8, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7279, 125601, 0, 1, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7280, 125601, 0, 1, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7281, 125601, 0, 1, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7283, 125601, 0, 1, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7284, 125601, 0, 1, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7285, 125601, 0, 1, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7286, 125601, 0, 1, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7288, 125601, 0, 1, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7289, 125601, 0, 1, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7291, 126201, 0, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7292, 126201, 0, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7293, 126201, 0, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7294, 126201, 0, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7295, 126201, 0, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7296, 126201, 0, 4, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7297, 126201, 0, 8, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7298, 126201, 0, 1, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7299, 126201, 0, 1, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7300, 126201, 0, 1, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7301, 126201, 0, 1, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7302, 126201, 0, 4, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7303, 126201, 0, 8, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7304, 126201, 0, 1, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7305, 126201, 0, 1, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7306, 126201, 0, 1, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7308, 126201, 0, 1, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7309, 126201, 0, 1, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7310, 126201, 0, 1, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7311, 126201, 0, 1, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7313, 126201, 0, 1, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7314, 126201, 0, 1, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7316, 100801, 0, 12, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7317, 100801, 0, 12, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7318, 126301, 0, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7319, 126301, 0, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7320, 126301, 0, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7321, 126301, 0, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7322, 126301, 0, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7323, 126301, 0, 4, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7324, 126301, 0, 8, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7325, 126301, 0, 1, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7326, 126301, 0, 1, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7327, 126301, 0, 1, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7328, 126301, 0, 1, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7329, 126301, 0, 4, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7330, 126301, 0, 8, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7331, 126301, 0, 1, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7332, 126301, 0, 1, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7333, 126301, 0, 1, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7335, 126301, 0, 1, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7336, 126301, 0, 1, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7337, 126301, 0, 1, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7338, 126301, 0, 1, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7340, 126301, 0, 1, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7341, 126301, 0, 1, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7343, 126401, 0, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7344, 126401, 0, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7345, 126401, 0, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7346, 126401, 0, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7347, 126401, 0, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7348, 126401, 0, 4, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7349, 126401, 0, 8, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7350, 126401, 0, 1, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7351, 126401, 0, 1, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7352, 126401, 0, 1, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7353, 126401, 0, 1, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7354, 126401, 0, 4, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7355, 126401, 0, 8, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7356, 126401, 0, 1, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7357, 126401, 0, 1, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7358, 126401, 0, 1, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7360, 126401, 0, 1, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7361, 126401, 0, 1, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7362, 126401, 0, 1, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7363, 126401, 0, 1, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7365, 126401, 0, 1, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7366, 126401, 0, 1, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7368, 126701, 0, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7369, 126701, 0, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7370, 126701, 0, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7371, 126701, 0, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7372, 126701, 0, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7373, 126701, 0, 4, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7374, 126701, 0, 8, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7375, 126701, 0, 1, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7376, 126701, 0, 1, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7377, 126701, 0, 1, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7378, 126701, 0, 1, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7379, 126701, 0, 4, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7380, 126701, 0, 8, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7381, 126701, 0, 1, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7382, 126701, 0, 1, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7383, 126701, 0, 1, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7385, 126701, 0, 1, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7386, 126701, 0, 1, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7387, 126701, 0, 1, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7388, 126701, 0, 1, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7390, 126701, 0, 1, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7391, 126701, 0, 1, "夏のトレンドを\n押さえておくのも\n大事ですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7393, 126701, 0, 1, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7394, 126701, 0, 1, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7395, 126601, 0, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7396, 126601, 0, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7397, 126601, 0, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7398, 126601, 0, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7399, 126601, 0, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7400, 126601, 0, 4, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7401, 126601, 0, 8, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7402, 126601, 0, 1, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7403, 126601, 0, 1, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7404, 126601, 0, 1, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7405, 126601, 0, 1, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7406, 126601, 0, 4, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7407, 126601, 0, 8, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7408, 126601, 0, 1, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7409, 126601, 0, 1, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7410, 126601, 0, 1, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7412, 126601, 0, 1, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7413, 126601, 0, 1, "なかなか気に入った…\nというのは\n誰にも秘密です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7414, 126601, 0, 1, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7415, 126601, 0, 1, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7417, 126601, 0, 1, "私がどのような味が\n好きなのかは…\n当然秘密です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7418, 126601, 0, 1, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7420, 126601, 0, 1, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7421, 126601, 0, 1, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7422, 105401, 0, 12, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7423, 105401, 0, 12, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7424, 126801, 0, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7425, 126801, 0, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7426, 126801, 0, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7427, 126801, 0, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7428, 126801, 0, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7429, 126801, 0, 4, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7430, 126801, 0, 8, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7431, 126801, 0, 1, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7432, 126801, 0, 1, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7433, 126801, 0, 1, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7434, 126801, 0, 1, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7435, 126801, 0, 4, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7436, 126801, 0, 8, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7437, 126801, 0, 1, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7438, 126801, 0, 1, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7439, 126801, 0, 1, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7441, 126801, 0, 1, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7442, 126801, 0, 1, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7443, 126801, 0, 1, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7444, 126801, 0, 1, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7446, 126801, 0, 1, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7447, 126801, 0, 1, "試験に使った参考書、\nたまには読み返して\nおかないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7449, 126801, 0, 1, "ふふ♪\nいい手触りね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7450, 126801, 0, 1, "…はふう～…\nゴロゴロするのって\n最高ね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7451, 126901, 0, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7452, 126901, 0, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7453, 126901, 0, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7454, 126901, 0, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7455, 126901, 0, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7456, 126901, 0, 4, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7457, 126901, 0, 8, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7458, 126901, 0, 1, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7459, 126901, 0, 1, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7460, 126901, 0, 1, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7461, 126901, 0, 1, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7462, 126901, 0, 4, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7463, 126901, 0, 8, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7464, 126901, 0, 1, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7465, 126901, 0, 1, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7466, 126901, 0, 1, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7468, 126901, 0, 1, "素肌にふわふわが…\n気持ちいい…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7469, 126901, 0, 1, "うふふ…\n一緒に海…行く？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7470, 126901, 0, 1, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7471, 126901, 0, 1, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7473, 126901, 0, 1, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7474, 126901, 0, 1, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7476, 126901, 0, 1, "これで合ってる…？\n遊び方わからなくて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7477, 126901, 0, 1, "ふわふわ…♪\n海の底にいるみたい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7478, 127001, 0, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7479, 127001, 0, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7480, 127001, 0, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7481, 127001, 0, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7482, 127001, 0, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7483, 127001, 0, 4, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7484, 127001, 0, 8, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7485, 127001, 0, 1, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7486, 127001, 0, 1, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7487, 127001, 0, 1, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7488, 127001, 0, 1, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7489, 127001, 0, 4, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7490, 127001, 0, 8, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7491, 127001, 0, 1, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7492, 127001, 0, 1, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7493, 127001, 0, 1, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7495, 127001, 0, 1, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7496, 127001, 0, 1, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7497, 127001, 0, 1, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7498, 127001, 0, 1, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7500, 127001, 0, 1, "クウカの口の中に\nたくさん…♪\n止まりません…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7501, 127001, 0, 1, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7503, 127001, 0, 1, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7504, 127001, 0, 1, "これは…\n妄想に役立ちそうな\n感触です…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7505, 100501, 0, 12, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7506, 100501, 0, 12, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7507, 127101, 0, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7508, 127101, 0, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7509, 127101, 0, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7510, 127101, 0, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7511, 127101, 0, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7512, 127101, 0, 4, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7513, 127101, 0, 8, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7514, 127101, 0, 1, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7515, 127101, 0, 1, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7516, 127101, 0, 1, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7517, 127101, 0, 1, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7518, 127101, 0, 4, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7519, 127101, 0, 8, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7520, 127101, 0, 1, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7521, 127101, 0, 1, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7522, 127101, 0, 1, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7524, 127101, 0, 1, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7525, 127101, 0, 1, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7526, 127101, 0, 1, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7527, 127101, 0, 1, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7529, 127101, 0, 1, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7530, 127101, 0, 1, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 7532, 127101, 0, 1, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 7533, 127101, 0, 1, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7534, 126501, 0, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7535, 126501, 0, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7536, 126501, 0, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7537, 126501, 0, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7538, 126501, 0, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7539, 126501, 0, 4, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7540, 126501, 0, 8, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7541, 126501, 0, 1, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7542, 126501, 0, 1, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7543, 126501, 0, 1, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7544, 126501, 0, 1, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7545, 126501, 0, 4, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7546, 126501, 0, 8, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7547, 126501, 0, 1, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7548, 126501, 0, 1, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7549, 126501, 0, 1, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7551, 126501, 0, 1, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7552, 126501, 0, 1, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7553, 126501, 0, 1, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7554, 126501, 0, 1, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7556, 126501, 0, 1, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7557, 126501, 0, 1, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7559, 127201, 0, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7560, 127201, 0, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7561, 127201, 0, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7562, 127201, 0, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7563, 127201, 0, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7564, 127201, 0, 4, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7565, 127201, 0, 8, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7566, 127201, 0, 1, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7567, 127201, 0, 1, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7568, 127201, 0, 1, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7569, 127201, 0, 1, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7570, 127201, 0, 4, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7571, 127201, 0, 8, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7572, 127201, 0, 1, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7573, 127201, 0, 1, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7574, 127201, 0, 1, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7576, 127201, 0, 1, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7577, 127201, 0, 1, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7578, 127201, 0, 1, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7579, 127201, 0, 1, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7581, 127201, 0, 1, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7582, 127201, 0, 1, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7584, 104301, 0, 12, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7585, 104301, 0, 12, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7586, 127301, 0, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7587, 127301, 0, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7588, 127301, 0, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7589, 127301, 0, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7590, 127301, 0, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7591, 127301, 0, 4, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7592, 127301, 0, 8, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7593, 127301, 0, 1, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7594, 127301, 0, 1, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7595, 127301, 0, 1, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7596, 127301, 0, 1, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7597, 127301, 0, 4, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7598, 127301, 0, 8, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7599, 127301, 0, 1, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7600, 127301, 0, 1, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7601, 127301, 0, 1, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7603, 127301, 0, 1, "充電ちう～\n…ガッコーのイスも\nこれにして～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7604, 127301, 0, 1, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7605, 127301, 0, 1, "リフレッシュの\n魔法発見！\nいい香り～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7606, 127301, 0, 1, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7608, 127301, 0, 1, "超おいし～♪\n学食にあったら\n毎日リピする～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7609, 127301, 0, 1, "ふむふむ…\nねむみパない…\n続きはまた今度～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7611, 127501, 0, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7612, 127501, 0, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7613, 127501, 0, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7614, 127501, 0, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7615, 127501, 0, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7616, 127501, 0, 4, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7617, 127501, 0, 8, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7618, 127501, 0, 1, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7619, 127501, 0, 1, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7620, 127501, 0, 1, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7621, 127501, 0, 1, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7622, 127501, 0, 4, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7623, 127501, 0, 8, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7624, 127501, 0, 1, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7625, 127501, 0, 1, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7626, 127501, 0, 1, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7628, 127501, 0, 1, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7629, 127501, 0, 1, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7630, 127501, 0, 1, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7631, 127501, 0, 1, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7633, 127501, 0, 1, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7634, 127501, 0, 1, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7636, 101401, 0, 12, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7637, 101401, 0, 12, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7638, 127601, 0, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7639, 127601, 0, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7640, 127601, 0, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7641, 127601, 0, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7642, 127601, 0, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7643, 127601, 0, 4, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7644, 127601, 0, 8, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7645, 127601, 0, 1, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7646, 127601, 0, 1, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7647, 127601, 0, 1, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7648, 127601, 0, 1, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7649, 127601, 0, 4, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7650, 127601, 0, 8, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7651, 127601, 0, 1, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7652, 127601, 0, 1, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7653, 127601, 0, 1, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7655, 127601, 0, 1, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7656, 127601, 0, 1, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7657, 127601, 0, 1, "この香り…\nサラサリア土産の\n香木かい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7658, 127601, 0, 1, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7660, 127601, 0, 1, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7661, 127601, 0, 1, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7663, 126001, 0, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7664, 126001, 0, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7665, 126001, 0, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7666, 126001, 0, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7667, 126001, 0, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7668, 126001, 0, 4, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7669, 126001, 0, 8, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7670, 126001, 0, 1, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7671, 126001, 0, 1, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7672, 126001, 0, 1, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7673, 126001, 0, 1, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7674, 126001, 0, 4, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7675, 126001, 0, 8, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7676, 126001, 0, 1, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7677, 126001, 0, 1, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7678, 126001, 0, 1, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7680, 126001, 0, 1, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7681, 126001, 0, 1, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7682, 126001, 0, 1, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7683, 126001, 0, 1, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7685, 126001, 0, 1, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7686, 126001, 0, 1, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7688, 103801, 0, 12, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7689, 103801, 0, 12, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7690, 126101, 0, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7691, 126101, 0, 1, "きょろきょろ\nここも\nぱとろる、するよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7692, 126101, 0, 1, "おうち\nにぎやか\nさみしく、ない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7693, 126101, 0, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7694, 126101, 0, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7695, 126101, 0, 4, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7696, 126101, 0, 8, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7697, 126101, 0, 1, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7698, 126101, 0, 1, "くろぷれしあ\nかっこいい？\nん…あざます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7699, 126101, 0, 1, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7700, 126101, 0, 1, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7701, 126101, 0, 4, "かみ、りぼん\nおてつだい\nよろ\nです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7702, 126101, 0, 8, "ここ\nすむ\nね？\nまいにち、いっしょ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7703, 126101, 0, 1, "ぐっもにん\nあさごはん\nあさごはん", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7704, 126101, 0, 1, "お兄\nおつかれ？\nれっつ、まいぺーす", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7705, 126101, 0, 1, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7707, 126101, 0, 1, "ふかふか\nパパの、おなか\nみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7708, 126101, 0, 1, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7709, 126101, 0, 1, "いい、におい\nでも、お兄\nもっと、いい、におい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7710, 126101, 0, 1, "とりま\nお肉、やこ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7712, 126101, 0, 1, "もぐもぐ…\nむげん肉まん、も\nきぼう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7713, 126101, 0, 1, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7715, 127901, 0, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7716, 127901, 0, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7717, 127901, 0, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7718, 127901, 0, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7719, 127901, 0, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7720, 127901, 0, 4, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7721, 127901, 0, 8, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7722, 127901, 0, 1, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7723, 127901, 0, 1, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7724, 127901, 0, 1, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7725, 127901, 0, 1, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7726, 127901, 0, 4, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7727, 127901, 0, 8, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7728, 127901, 0, 1, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7729, 127901, 0, 1, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7730, 127901, 0, 1, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7732, 127901, 0, 1, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7733, 127901, 0, 1, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7734, 127901, 0, 1, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7735, 127901, 0, 1, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7737, 127901, 0, 1, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7738, 127901, 0, 1, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 7740, 127901, 0, 1, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 7741, 127901, 0, 1, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7742, 106601, 0, 12, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7743, 106601, 0, 12, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7744, 128001, 0, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7745, 128001, 0, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7746, 128001, 0, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7747, 128001, 0, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7748, 128001, 0, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7749, 128001, 0, 4, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7750, 128001, 0, 8, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7751, 128001, 0, 1, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7752, 128001, 0, 1, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7753, 128001, 0, 1, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7754, 128001, 0, 1, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7755, 128001, 0, 4, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7756, 128001, 0, 8, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7757, 128001, 0, 1, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7758, 128001, 0, 1, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7759, 128001, 0, 1, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7761, 128001, 0, 1, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7762, 128001, 0, 1, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7763, 128001, 0, 1, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7764, 128001, 0, 1, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7766, 128001, 0, 1, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7767, 128001, 0, 1, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 14, 3, 7769, 128001, 0, 1, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 15, 3, 7770, 128001, 0, 1, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7771, 128101, 0, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7772, 128101, 0, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7773, 128101, 0, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7774, 128101, 0, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7775, 128101, 0, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7776, 128101, 0, 4, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7777, 128101, 0, 8, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7778, 128101, 0, 1, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7779, 128101, 0, 1, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7780, 128101, 0, 1, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7781, 128101, 0, 1, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7782, 128101, 0, 4, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7783, 128101, 0, 8, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7784, 128101, 0, 1, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7785, 128101, 0, 1, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7786, 128101, 0, 1, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7788, 128101, 0, 1, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7789, 128101, 0, 1, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7790, 128101, 0, 1, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7791, 128101, 0, 1, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7793, 128101, 0, 1, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7794, 128101, 0, 1, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7797, 128201, 0, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7798, 128201, 0, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7799, 128201, 0, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7800, 128201, 0, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7801, 128201, 0, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7802, 128201, 0, 4, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7803, 128201, 0, 8, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7804, 128201, 0, 1, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7805, 128201, 0, 1, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7806, 128201, 0, 1, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7807, 128201, 0, 1, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7808, 128201, 0, 4, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7809, 128201, 0, 8, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7810, 128201, 0, 1, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7811, 128201, 0, 1, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7812, 128201, 0, 1, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7814, 128201, 0, 1, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7815, 128201, 0, 1, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7816, 128201, 0, 1, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7817, 128201, 0, 1, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7819, 128201, 0, 1, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7820, 128201, 0, 1, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7823, 128301, 0, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7824, 128301, 0, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7825, 128301, 0, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7826, 128301, 0, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7827, 128301, 0, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7828, 128301, 0, 4, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7829, 128301, 0, 8, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7830, 128301, 0, 1, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7831, 128301, 0, 1, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7832, 128301, 0, 1, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7833, 128301, 0, 1, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7834, 128301, 0, 4, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7835, 128301, 0, 8, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7836, 128301, 0, 1, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7837, 128301, 0, 1, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7838, 128301, 0, 1, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7840, 128301, 0, 1, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7841, 128301, 0, 1, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7842, 128301, 0, 1, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7843, 128301, 0, 1, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7845, 128301, 0, 1, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7846, 128301, 0, 1, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7848, 128401, 0, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7849, 128401, 0, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7850, 128401, 0, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7851, 128401, 0, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7852, 128401, 0, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7853, 128401, 0, 4, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7854, 128401, 0, 8, "来年も彼の隣で\n初日の出が\n見られるといいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7855, 128401, 0, 1, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7856, 128401, 0, 1, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7857, 128401, 0, 1, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7858, 128401, 0, 1, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7859, 128401, 0, 4, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7860, 128401, 0, 8, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7861, 128401, 0, 1, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7862, 128401, 0, 1, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7863, 128401, 0, 1, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7865, 128401, 0, 1, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7866, 128401, 0, 1, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7867, 128401, 0, 1, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7868, 128401, 0, 1, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7870, 128401, 0, 1, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7871, 128401, 0, 1, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 7873, 106501, 0, 12, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 7874, 106501, 0, 12, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7875, 128501, 0, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7876, 128501, 0, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7877, 128501, 0, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7878, 128501, 0, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7879, 128501, 0, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7880, 128501, 0, 4, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7881, 128501, 0, 8, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7882, 128501, 0, 1, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7883, 128501, 0, 1, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7884, 128501, 0, 1, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7885, 128501, 0, 1, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7886, 128501, 0, 4, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7887, 128501, 0, 8, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7888, 128501, 0, 1, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7889, 128501, 0, 1, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7890, 128501, 0, 1, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7892, 128501, 0, 1, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7893, 128501, 0, 1, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7894, 128501, 0, 1, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7895, 128501, 0, 1, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7897, 128501, 0, 1, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7898, 128501, 0, 1, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7900, 128801, 0, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7901, 128801, 0, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7902, 128801, 0, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7903, 128801, 0, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7904, 128801, 0, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7905, 128801, 0, 4, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7906, 128801, 0, 8, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7907, 128801, 0, 1, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7908, 128801, 0, 1, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7909, 128801, 0, 1, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7910, 128801, 0, 1, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7911, 128801, 0, 4, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7912, 128801, 0, 8, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7913, 128801, 0, 1, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7914, 128801, 0, 1, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7915, 128801, 0, 1, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7917, 128801, 0, 1, "休める時は休む…\n戦い続けるには\n必要なことだからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7918, 128801, 0, 1, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7919, 128801, 0, 1, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7920, 128801, 0, 1, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7922, 128801, 0, 1, "この味付け…\n今度試してみようか\n味見は任せたからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7923, 128801, 0, 1, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7925, 128901, 0, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7926, 128901, 0, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7927, 128901, 0, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7928, 128901, 0, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7929, 128901, 0, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7930, 128901, 0, 4, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7931, 128901, 0, 8, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7932, 128901, 0, 1, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7933, 128901, 0, 1, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7934, 128901, 0, 1, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7935, 128901, 0, 1, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7936, 128901, 0, 4, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7937, 128901, 0, 8, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7938, 128901, 0, 1, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7939, 128901, 0, 1, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7940, 128901, 0, 1, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7942, 128901, 0, 1, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7943, 128901, 0, 1, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7944, 128901, 0, 1, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7945, 128901, 0, 1, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7947, 128901, 0, 1, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7948, 128901, 0, 1, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7950, 128701, 0, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7951, 128701, 0, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7952, 128701, 0, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7953, 128701, 0, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7954, 128701, 0, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7955, 128701, 0, 4, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7956, 128701, 0, 8, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7957, 128701, 0, 1, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7958, 128701, 0, 1, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7959, 128701, 0, 1, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7960, 128701, 0, 1, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7961, 128701, 0, 4, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7962, 128701, 0, 8, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7963, 128701, 0, 1, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7964, 128701, 0, 1, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7965, 128701, 0, 1, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7967, 128701, 0, 1, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7968, 128701, 0, 1, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7969, 128701, 0, 1, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7970, 128701, 0, 1, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7972, 128701, 0, 1, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7973, 128701, 0, 1, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 7975, 129001, 0, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 7976, 129001, 0, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 7977, 129001, 0, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 7978, 129001, 0, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 7979, 129001, 0, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 7980, 129001, 0, 4, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 7981, 129001, 0, 8, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7982, 129001, 0, 1, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7983, 129001, 0, 1, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7984, 129001, 0, 1, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 7985, 129001, 0, 1, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 7986, 129001, 0, 4, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 7987, 129001, 0, 8, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 7988, 129001, 0, 1, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 7989, 129001, 0, 1, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 7990, 129001, 0, 1, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 7992, 129001, 0, 1, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 7993, 129001, 0, 1, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 7994, 129001, 0, 1, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 7995, 129001, 0, 1, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 7997, 129001, 0, 1, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 7998, 129001, 0, 1, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8000, 129301, 0, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8001, 129301, 0, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8002, 129301, 0, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8003, 129301, 0, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8004, 129301, 0, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8005, 129301, 0, 4, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8006, 129301, 0, 8, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8007, 129301, 0, 1, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8008, 129301, 0, 1, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8009, 129301, 0, 1, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8010, 129301, 0, 1, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8011, 129301, 0, 4, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8012, 129301, 0, 8, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8013, 129301, 0, 1, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8014, 129301, 0, 1, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8015, 129301, 0, 1, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8017, 129301, 0, 1, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8018, 129301, 0, 1, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8019, 129301, 0, 1, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8020, 129301, 0, 1, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8022, 129301, 0, 1, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8023, 129301, 0, 1, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8025, 129301, 0, 1, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8026, 129301, 0, 1, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8027, 108901, 0, 12, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8028, 108901, 0, 12, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8029, 108801, 0, 12, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8030, 108801, 0, 12, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8031, 108701, 0, 12, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8032, 108701, 0, 12, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8033, 129401, 0, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8034, 129401, 0, 1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8035, 129401, 0, 1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8036, 129401, 0, 1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8037, 129401, 0, 1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8038, 129401, 0, 4, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8039, 129401, 0, 8, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8040, 129401, 0, 1, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8041, 129401, 0, 1, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8042, 129401, 0, 1, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8043, 129401, 0, 1, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8044, 129401, 0, 4, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8045, 129401, 0, 8, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8046, 129401, 0, 1, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8047, 129401, 0, 1, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8048, 129401, 0, 1, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8050, 129401, 0, 1, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8051, 129401, 0, 1, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8052, 129401, 0, 1, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8053, 129401, 0, 1, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8055, 129401, 0, 1, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8056, 129401, 0, 1, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8058, 129401, 0, 1, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8059, 129401, 0, 1, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8060, 129801, 0, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8061, 129801, 0, 1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8062, 129801, 0, 1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8063, 129801, 0, 1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8064, 129801, 0, 1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8065, 129801, 0, 4, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8066, 129801, 0, 8, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8067, 129801, 0, 1, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8068, 129801, 0, 1, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8069, 129801, 0, 1, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8070, 129801, 0, 1, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8071, 129801, 0, 4, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8072, 129801, 0, 8, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8073, 129801, 0, 1, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8074, 129801, 0, 1, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8075, 129801, 0, 1, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8077, 129801, 0, 1, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8078, 129801, 0, 1, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8079, 129801, 0, 1, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8080, 129801, 0, 1, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8082, 129801, 0, 1, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8083, 129801, 0, 1, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8085, 129901, 0, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8086, 129901, 0, 1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8087, 129901, 0, 1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8088, 129901, 0, 1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8089, 129901, 0, 1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8090, 129901, 0, 4, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8091, 129901, 0, 8, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8092, 129901, 0, 1, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8093, 129901, 0, 1, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8094, 129901, 0, 1, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8095, 129901, 0, 1, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8096, 129901, 0, 4, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8097, 129901, 0, 8, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8098, 129901, 0, 1, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8099, 129901, 0, 1, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8100, 129901, 0, 1, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8102, 129901, 0, 1, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8103, 129901, 0, 1, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8104, 129901, 0, 1, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8105, 129901, 0, 1, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8107, 129901, 0, 1, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8108, 129901, 0, 1, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8110, 129601, 0, 1, "……（そわそわ）", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8111, 129601, 0, 1, "ここに寄らずの花陣…\n作ったらダメですよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8112, 129601, 0, 1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8113, 129601, 0, 1, "身を隠せる場所が\nあると…\n助かるんですけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8114, 129601, 0, 1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8115, 129601, 0, 4, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8116, 129601, 0, 8, "この家なら安全─\nかもしれません\n魔物が出ないので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8117, 129601, 0, 1, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8118, 129601, 0, 1, "長話─苦手です\n疲れるので…\nふぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8119, 129601, 0, 1, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8120, 129601, 0, 1, "世話をするのは\n得意です\n…花の、ですよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8121, 129601, 0, 4, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8122, 129601, 0, 8, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8123, 129601, 0, 1, "花に、お水─？\n…いりません", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8124, 129601, 0, 1, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8125, 129601, 0, 1, "夜番。私がします\nあまり眠らない\n性質なので…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8127, 129601, 0, 1, "ここなら根を張れる\nかもしれません\n…張りませんけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8128, 129601, 0, 1, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8129, 129601, 0, 1, "花みたいに─\nいい匂い、です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8130, 129601, 0, 1, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8132, 129601, 0, 1, "食事も立派な戦支度\n媛が言ってました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8133, 129601, 0, 1, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8135, 130701, 0, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8136, 130701, 0, 1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8137, 130701, 0, 1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8138, 130701, 0, 1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8139, 130701, 0, 1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8140, 130701, 0, 4, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8141, 130701, 0, 8, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8142, 130701, 0, 1, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8143, 130701, 0, 1, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8144, 130701, 0, 1, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8145, 130701, 0, 1, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8146, 130701, 0, 4, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8147, 130701, 0, 8, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8148, 130701, 0, 1, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8149, 130701, 0, 1, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8150, 130701, 0, 1, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8152, 130701, 0, 1, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8153, 130701, 0, 1, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8154, 130701, 0, 1, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8155, 130701, 0, 1, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8157, 130701, 0, 1, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8158, 130701, 0, 1, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8160, 130801, 0, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8161, 130801, 0, 1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8162, 130801, 0, 1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8163, 130801, 0, 1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8164, 130801, 0, 1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8165, 130801, 0, 4, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8166, 130801, 0, 8, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8167, 130801, 0, 1, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8168, 130801, 0, 1, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8169, 130801, 0, 1, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8170, 130801, 0, 1, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8171, 130801, 0, 4, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8172, 130801, 0, 8, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8173, 130801, 0, 1, "はう、そうでした\nお花にお水を\nあげる時間です！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8174, 130801, 0, 1, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8175, 130801, 0, 1, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8177, 130801, 0, 1, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8178, 130801, 0, 1, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8179, 130801, 0, 1, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8180, 130801, 0, 1, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8182, 130801, 0, 1, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8183, 130801, 0, 1, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8185, 131001, 0, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8186, 131001, 0, 1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8187, 131001, 0, 1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8188, 131001, 0, 1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8189, 131001, 0, 1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8190, 131001, 0, 4, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8191, 131001, 0, 8, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8192, 131001, 0, 1, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8193, 131001, 0, 1, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8194, 131001, 0, 1, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8195, 131001, 0, 1, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8196, 131001, 0, 4, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8197, 131001, 0, 8, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8198, 131001, 0, 1, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8199, 131001, 0, 1, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8200, 131001, 0, 1, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8202, 131001, 0, 1, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8203, 131001, 0, 1, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8204, 131001, 0, 1, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8205, 131001, 0, 1, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8207, 131001, 0, 1, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8208, 131001, 0, 1, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8210, 105601, 0, 12, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8211, 105601, 0, 12, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8212, 130901, 0, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8213, 130901, 0, 1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8214, 130901, 0, 1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8215, 130901, 0, 1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8216, 130901, 0, 1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8217, 130901, 0, 4, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8218, 130901, 0, 8, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8219, 130901, 0, 1, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8220, 130901, 0, 1, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8221, 130901, 0, 1, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8222, 130901, 0, 1, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8223, 130901, 0, 4, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8224, 130901, 0, 8, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8225, 130901, 0, 1, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8226, 130901, 0, 1, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8227, 130901, 0, 1, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8229, 130901, 0, 1, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8230, 130901, 0, 1, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8231, 130901, 0, 1, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8232, 130901, 0, 1, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8234, 130901, 0, 1, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8235, 130901, 0, 1, "パークの\nガイドブックとか\nあったらいいですね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8237, 131101, 0, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8238, 131101, 0, 1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8239, 131101, 0, 1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8240, 131101, 0, 1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8241, 131101, 0, 1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8242, 131101, 0, 4, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8243, 131101, 0, 8, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8244, 131101, 0, 1, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8245, 131101, 0, 1, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8246, 131101, 0, 1, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8247, 131101, 0, 1, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8248, 131101, 0, 4, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8249, 131101, 0, 8, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8250, 131101, 0, 1, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8251, 131101, 0, 1, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8252, 131101, 0, 1, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8254, 131101, 0, 1, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8255, 131101, 0, 1, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8256, 131101, 0, 1, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8257, 131101, 0, 1, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8259, 131101, 0, 1, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8260, 131101, 0, 1, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8262, 131101, 0, 1, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8263, 131101, 0, 1, "あーやべ\n一瞬で寝そー\nヤバだわ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8264, 129701, 0, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8265, 129701, 0, 1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8266, 129701, 0, 1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8267, 129701, 0, 1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8268, 129701, 0, 1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8269, 129701, 0, 4, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8270, 129701, 0, 8, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8271, 129701, 0, 1, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8272, 129701, 0, 1, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8273, 129701, 0, 1, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8274, 129701, 0, 1, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8275, 129701, 0, 4, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8276, 129701, 0, 8, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8277, 129701, 0, 1, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8278, 129701, 0, 1, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8279, 129701, 0, 1, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8281, 129701, 0, 1, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8282, 129701, 0, 1, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8283, 129701, 0, 1, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8284, 129701, 0, 1, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8286, 129701, 0, 1, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8287, 129701, 0, 1, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8289, 131201, 0, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8290, 131201, 0, 1, "海への行き来は\nテレポーテーションに\nお任せです★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8291, 131201, 0, 1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8292, 131201, 0, 1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8293, 131201, 0, 1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8294, 131201, 0, 4, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8295, 131201, 0, 8, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8296, 131201, 0, 1, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8297, 131201, 0, 1, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8298, 131201, 0, 1, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8299, 131201, 0, 1, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8300, 131201, 0, 4, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8301, 131201, 0, 8, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8302, 131201, 0, 1, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8303, 131201, 0, 1, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8304, 131201, 0, 1, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8306, 131201, 0, 1, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8307, 131201, 0, 1, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8308, 131201, 0, 1, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8309, 131201, 0, 1, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8311, 131201, 0, 1, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8312, 131201, 0, 1, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8314, 131201, 0, 1, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8315, 131201, 0, 1, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8316, 131601, 0, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8317, 131601, 0, 1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8318, 131601, 0, 1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8319, 131601, 0, 1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8320, 131601, 0, 1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8321, 131601, 0, 4, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8322, 131601, 0, 8, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8323, 131601, 0, 1, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8324, 131601, 0, 1, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8325, 131601, 0, 1, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8326, 131601, 0, 1, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8327, 131601, 0, 4, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8328, 131601, 0, 8, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8329, 131601, 0, 1, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8330, 131601, 0, 1, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8331, 131601, 0, 1, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8333, 131601, 0, 1, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8334, 131601, 0, 1, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8335, 131601, 0, 1, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8336, 131601, 0, 1, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8338, 131601, 0, 1, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8339, 131601, 0, 1, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8341, 131601, 0, 1, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8342, 131601, 0, 1, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8343, 131701, 0, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8344, 131701, 0, 1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8345, 131701, 0, 1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8346, 131701, 0, 1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8347, 131701, 0, 1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8348, 131701, 0, 4, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8349, 131701, 0, 8, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8350, 131701, 0, 1, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8351, 131701, 0, 1, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8352, 131701, 0, 1, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8353, 131701, 0, 1, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8354, 131701, 0, 4, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8355, 131701, 0, 8, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8356, 131701, 0, 1, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8357, 131701, 0, 1, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8358, 131701, 0, 1, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8360, 131701, 0, 1, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8361, 131701, 0, 1, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8362, 131701, 0, 1, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8363, 131701, 0, 1, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8365, 131701, 0, 1, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8366, 131701, 0, 1, "文字を読むのと\nデータを読むのは\nまったく違うわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8368, 131701, 0, 1, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8369, 131701, 0, 1, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8370, 118601, 0, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8371, 118601, 0, 1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8372, 118601, 0, 1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8373, 118601, 0, 1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8374, 118601, 0, 1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8375, 118601, 0, 4, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8376, 118601, 0, 8, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8377, 118601, 0, 1, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8378, 118601, 0, 1, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8379, 118601, 0, 1, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8380, 118601, 0, 1, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8381, 118601, 0, 4, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8382, 118601, 0, 8, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8383, 118601, 0, 1, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8384, 118601, 0, 1, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8385, 118601, 0, 1, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8387, 118601, 0, 1, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8388, 118601, 0, 1, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8389, 118601, 0, 1, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8390, 118601, 0, 1, "なんていうか…\n心があたたかいわ\n…変な感じ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8392, 118601, 0, 1, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8393, 118601, 0, 1, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8395, 131801, 0, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8396, 131801, 0, 1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8397, 131801, 0, 1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8398, 131801, 0, 1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8399, 131801, 0, 1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8400, 131801, 0, 4, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8401, 131801, 0, 8, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8402, 131801, 0, 1, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8403, 131801, 0, 1, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8404, 131801, 0, 1, "熱中症には\n気を付けないと\nだめだよ～？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8405, 131801, 0, 1, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8406, 131801, 0, 4, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8407, 131801, 0, 8, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8408, 131801, 0, 1, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8409, 131801, 0, 1, "ふふ♪\n波音が心地良い\n時間だね～☆", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8410, 131801, 0, 1, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8412, 131801, 0, 1, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8413, 131801, 0, 1, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8414, 131801, 0, 1, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8415, 131801, 0, 1, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8417, 131801, 0, 1, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8418, 131801, 0, 1, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8420, 131801, 0, 1, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8421, 131801, 0, 1, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8422, 181201, 0, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8423, 181201, 0, 1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8424, 181201, 0, 1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8425, 181201, 0, 1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8426, 181201, 0, 1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8427, 181201, 0, 4, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8428, 181201, 0, 8, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8429, 181201, 0, 1, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8430, 181201, 0, 1, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8431, 181201, 0, 1, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8432, 181201, 0, 1, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8433, 181201, 0, 4, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8434, 181201, 0, 8, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8435, 181201, 0, 1, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8436, 181201, 0, 1, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8437, 181201, 0, 1, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8439, 181201, 0, 1, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8440, 181201, 0, 1, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8441, 181201, 0, 1, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8442, 181201, 0, 1, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8444, 181201, 0, 1, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8445, 181201, 0, 1, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8447, 131901, 0, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8448, 131901, 0, 1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8449, 131901, 0, 1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8450, 131901, 0, 1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8451, 131901, 0, 1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8452, 131901, 0, 4, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8453, 131901, 0, 8, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8454, 131901, 0, 1, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8455, 131901, 0, 1, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8456, 131901, 0, 1, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8457, 131901, 0, 1, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8458, 131901, 0, 4, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8459, 131901, 0, 8, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8460, 131901, 0, 1, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8461, 131901, 0, 1, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8462, 131901, 0, 1, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8464, 131901, 0, 1, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8465, 131901, 0, 1, "細かいところまで\nすごーく丁寧に\n作られてる…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8466, 131901, 0, 1, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8467, 131901, 0, 1, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8469, 131901, 0, 1, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8470, 131901, 0, 1, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8472, 131901, 0, 1, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8473, 131901, 0, 1, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8474, 132101, 0, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8475, 132101, 0, 1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8476, 132101, 0, 1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8477, 132101, 0, 1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8478, 132101, 0, 1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8479, 132101, 0, 4, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8480, 132101, 0, 8, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8481, 132101, 0, 1, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8482, 132101, 0, 1, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8483, 132101, 0, 1, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8484, 132101, 0, 1, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8485, 132101, 0, 4, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8486, 132101, 0, 8, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8487, 132101, 0, 1, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8488, 132101, 0, 1, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8489, 132101, 0, 1, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8491, 132101, 0, 1, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8492, 132101, 0, 1, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8493, 132101, 0, 1, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8494, 132101, 0, 1, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8496, 132101, 0, 1, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8497, 132101, 0, 1, "海に関する詩文なども\nあるのでしょうか…\n気になります", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8499, 132101, 0, 1, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8500, 132101, 0, 1, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8501, 132001, 0, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8502, 132001, 0, 1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8503, 132001, 0, 1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8504, 132001, 0, 1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8505, 132001, 0, 1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8506, 132001, 0, 4, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8507, 132001, 0, 8, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8508, 132001, 0, 1, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8509, 132001, 0, 1, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8510, 132001, 0, 1, "この世界においては\nボクは異物だからね\n大人しくしているさ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8511, 132001, 0, 1, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8512, 132001, 0, 4, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8513, 132001, 0, 8, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8514, 132001, 0, 1, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8515, 132001, 0, 1, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8516, 132001, 0, 1, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8518, 132001, 0, 1, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8519, 132001, 0, 1, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8520, 132001, 0, 1, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8521, 132001, 0, 1, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8523, 132001, 0, 1, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8524, 132001, 0, 1, "素晴らしいね\nここには叡智が\n詰まっている", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 10, 3, 8526, 132001, 0, 1, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 11, 3, 8527, 132001, 0, 1, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8528, 109701, 0, 12, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8529, 109701, 0, 12, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8530, 109801, 0, 12, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8531, 109801, 0, 12, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 1, 8532, 109901, 0, 12, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 103, 2, 8533, 109901, 0, 12, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8534, 132201, 0, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8535, 132201, 0, 1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8536, 132201, 0, 1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8537, 132201, 0, 1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8538, 132201, 0, 1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8539, 132201, 0, 4, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8540, 132201, 0, 8, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8541, 132201, 0, 1, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8542, 132201, 0, 1, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8543, 132201, 0, 1, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8544, 132201, 0, 1, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8545, 132201, 0, 4, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8546, 132201, 0, 8, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8547, 132201, 0, 1, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8548, 132201, 0, 1, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8549, 132201, 0, 1, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8551, 132201, 0, 1, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8552, 132201, 0, 1, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8553, 132201, 0, 1, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8554, 132201, 0, 1, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8556, 132201, 0, 1, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8557, 132201, 0, 1, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 1, 8559, 127701, 0, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 1, 8560, 127701, 0, 1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 1, 8561, 127701, 0, 1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 1, 8562, 127701, 0, 1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 1, 8563, 127701, 0, 1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 1, 8564, 127701, 0, 4, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 1, 8565, 127701, 0, 8, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8566, 127701, 0, 1, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8567, 127701, 0, 1, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8568, 127701, 0, 1, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 2, 8569, 127701, 0, 1, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 101, 2, 8570, 127701, 0, 4, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 102, 2, 8571, 127701, 0, 8, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 1, 2, 8572, 127701, 0, 1, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 2, 8573, 127701, 0, 1, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 2, 8574, 127701, 0, 1, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 3);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 2, 3, 8576, 127701, 0, 1, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 3, 3, 8577, 127701, 0, 1, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 4, 3, 8578, 127701, 0, 1, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 5, 3, 8579, 127701, 0, 1, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 7, 3, 8581, 127701, 0, 1, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 0);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (1, 8, 3, 8582, 127701, 0, 1, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 0);
