INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 1, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 2, 2, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 3, 3, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 4, 4, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 5, 5, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 101, 6, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 102, 7, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 1, 8, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 2, 9, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 3, 10, "えっとね…しっぽは\n触っちゃダメだよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 4, 11, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 101, 12, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 102, 13, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 1, 1, 14, "おはよー！\n今日も元気に\nいっちゃうよー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 2, 2, 15, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 3, 3, 16, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 1, 17, "ちゃんとケアして\n傷が残らないように\nしないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 2, 18, "一回休むと\nこのままずるずる\nいきそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 3, 19, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 4, 20, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 5, 21, "あったかい所に\nいると体が丸く\nなっちゃう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 6, 22, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 7, 23, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100101, 0, 0, 8, 24, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 1, 26, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 2, 27, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 3, 28, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 4, 29, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 5, 30, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 101, 31, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 102, 32, "…なんであたし\n騎士くんばっかり\n見てるんだろ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 1, 33, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 2, 34, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 3, 35, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 4, 36, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 101, 37, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 102, 38, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 1, 1, 39, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 2, 2, 40, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 3, 3, 41, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 1, 42, "これでよし♪\n騎士くん\n気づくかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 2, 43, "一回休むと\nこのままずるずる\nいきそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 3, 44, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 4, 45, "こういうの\nなんか気になって\nじーって見ちゃうんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 5, 46, "あったかい所に\nいると体が丸く\nなっちゃう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 6, 47, "いつ何が起きるか\n分からないし\nもっと力をつけないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 7, 48, "お腹が減ると\nなんか力が\n出ないんだよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100102, 0, 0, 8, 49, "色んなことを\n知っておくのは\n大事だよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 1, 51, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 2, 52, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 3, 53, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 4, 54, "戦いは\n得意じゃないけど\nお料理なら…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 5, 55, "こういう平和な\n時間が、もっと\n続くといいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 101, 56, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 102, 57, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 1, 58, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 2, 59, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 3, 60, "困ったことが\nあったら\nいつでも話してね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 4, 61, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 101, 62, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 102, 63, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 1, 1, 64, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 2, 2, 65, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 3, 3, 66, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 1, 67, "よし…\nこれなら\n褒めてもらえるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 2, 68, "がんばることも\n必要だけど、\nお休みも大切だよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 3, 69, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 4, 70, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 5, 71, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 6, 72, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 7, 73, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100201, 0, 0, 8, 74, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 1, 76, "洗濯と掃除\nどっちから\nはじめよう？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 2, 77, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 3, 78, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 4, 79, "今日はどんな料理に\n挑戦しよう…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 5, 80, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 101, 81, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 102, 82, "騎士クンのそばに\nいられるだけで\n幸せだよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 1, 83, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 2, 84, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 3, 85, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 4, 86, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 101, 87, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 102, 88, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 1, 1, 89, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 2, 2, 90, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 3, 3, 91, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 1, 92, "よし…\nこれなら\n褒めてもらえるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 2, 93, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 3, 94, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 4, 95, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 5, 96, "あの…！\nよ、良かったら\n一緒に…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 6, 97, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 7, 98, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100202, 0, 0, 8, 99, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 1, 101, "装備の手入れでも\nしようかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 2, 102, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 3, 103, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 4, 104, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 5, 105, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 101, 106, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 102, 107, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 1, 108, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 2, 109, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 3, 110, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 4, 111, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 101, 112, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 102, 113, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 1, 1, 114, "おはよう\n今日も充実した\n一日にしていこう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 2, 2, 115, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 3, 3, 116, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 1, 117, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 2, 118, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 3, 119, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 4, 120, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 5, 121, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 6, 122, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 7, 123, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100301, 0, 0, 8, 124, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 1, 126, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 2, 127, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 3, 128, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 4, 129, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 5, 130, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 101, 131, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 102, 132, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 1, 133, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 2, 134, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 3, 135, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 4, 136, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 101, 137, "ひょっとして\n今のも一本だなんて\n言わないよね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 102, 138, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 1, 1, 139, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 2, 2, 140, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 3, 3, 141, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 1, 142, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 2, 143, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 3, 144, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 4, 145, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 5, 146, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 6, 147, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 7, 148, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100302, 0, 0, 8, 149, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 1, 151, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 2, 152, "落とし穴…どこに\n作ったんだっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 3, 153, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 4, 154, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 5, 155, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 101, 156, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 102, 157, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 1, 158, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 2, 159, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 3, 160, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 4, 161, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 101, 162, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 102, 163, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 1, 1, 164, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 2, 2, 165, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 3, 3, 166, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 1, 167, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 2, 168, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 3, 169, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 4, 170, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 5, 171, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 6, 172, "探検家は\n体力勝負だよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 7, 173, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100401, 0, 0, 8, 174, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 1, 176, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 2, 177, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 3, 178, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 4, 179, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 5, 180, "かくれんぼする人\nこの指、と～まれ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 101, 181, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 102, 182, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 1, 183, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 2, 184, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 3, 185, "将来の夢は探検家！\n探したい物はないけど", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 4, 186, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 101, 187, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 102, 188, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 1, 1, 189, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 2, 2, 190, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 3, 3, 191, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 1, 192, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 2, 193, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 3, 194, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 4, 195, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 5, 196, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 6, 197, "探検家は\n体力勝負だよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 7, 198, "探検のおやつに\nぴったりかも～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100402, 0, 0, 8, 199, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 1, 201, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 2, 202, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 3, 203, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 4, 204, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 5, 205, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 101, 206, "お兄ちゃんと私は\n相性抜群です♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 102, 207, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 1, 208, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 2, 209, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 3, 210, "そんなところ\n触っちゃダメ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 4, 211, "お兄ちゃん、イジワル\nしないでください…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 101, 212, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 102, 213, "コーチ、\n今日も特訓\nお願いします！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 1, 1, 214, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 2, 2, 215, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 3, 3, 216, "夜はこれからですよ\nお兄ちゃん♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 1, 217, "世界一、かわいく\nなるんだから♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 2, 218, "気持ちいい♪\nもう立てないよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 3, 219, "かわいい♪\nアカリのライバル\nだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 4, 220, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 5, 221, "はぁ…アカリ、\nとろけちゃう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 6, 222, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 7, 223, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100601, 0, 0, 8, 224, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 1, 226, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 2, 227, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 3, 228, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 4, 229, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 5, 230, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 101, 231, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 102, 232, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 1, 233, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 2, 234, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 3, 235, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 4, 236, "お兄ちゃん、イジワル\nしないでください…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 101, 237, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 102, 238, "コーチ、\n今日も特訓\nお願いします！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 1, 1, 239, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 2, 2, 240, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 3, 3, 241, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 1, 242, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 2, 243, "気持ちいい♪\nもう立てないよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 3, 244, "かわいい♪\nアカリのライバル\nだね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 4, 245, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 5, 246, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 6, 247, "汗をかくと\n気持ちいいですよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 7, 248, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100602, 0, 0, 8, 249, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 1, 251, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 2, 252, "う～ら～め～し～や～\nなの～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 3, 253, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 4, 254, "幽霊の正体見たり、\nミヤコなの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 5, 255, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 101, 256, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 102, 257, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 1, 258, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 2, 259, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 3, 260, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 4, 261, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 101, 262, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 102, 263, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 1, 1, 264, "う～　眠いの…\n二度目の二度寝を\nするの…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 2, 2, 265, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 3, 3, 266, "ディナーは豪華に、\nプリンのフルコース\nなの～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 1, 267, "体が透けちゃうから\nうまく使えないの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 2, 268, "まったりなの～\nあとはプリンがあれば\n完璧なの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 3, 269, "む～…かわいいけど\nプリンは分けて\nあげないの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 4, 270, "は～　いい気分なの…\n成仏しそうなの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 5, 271, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 6, 272, "幽霊には、体力も\n筋力も関係ないの～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 7, 273, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100701, 0, 0, 8, 274, "プリンの写真集が\nあればいいのに\nなの～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 1, 276, "うらめしや～…\n裏の飯より\nプリンがいいの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 2, 277, "プリン♪　プリン♪\nプリン♪　なの～♪　", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 3, 278, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 4, 279, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 5, 280, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 101, 281, "アイツがいて\nプリンもある\nきっとここが天国なの", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 102, 282, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 1, 283, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 2, 284, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 3, 285, "今日はオマエと\nプリンを買いに行くの\n強制なの～　", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 4, 286, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 101, 287, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 102, 288, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 1, 1, 289, "う～　眠いの…\n二度目の二度寝を\nするの… ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 2, 2, 290, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 3, 3, 291, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 1, 292, "体が透けちゃうから\nうまく使えないの～ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 2, 293, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 3, 294, "む～…かわいいけど\nプリンは分けて\nあげないの～　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 4, 295, "は～　いい気分なの…\n成仏しそうなの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 5, 296, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 6, 297, "幽霊には、体力も\n筋力も関係ないの～！　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 7, 298, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100702, 0, 0, 8, 299, "プリンの写真集が\nあればいいのに\nなの～ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 1, 301, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 2, 302, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 3, 303, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 4, 304, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 5, 305, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 101, 306, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 102, 307, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 1, 308, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 2, 309, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 3, 310, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 4, 311, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 101, 312, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 102, 313, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 1, 1, 314, "美しいボクの\n美しい一日が\n始まるよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 2, 2, 315, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 3, 3, 316, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 1, 317, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 2, 318, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 3, 319, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 4, 320, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 5, 321, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 6, 322, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 7, 323, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100801, 0, 0, 8, 324, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 1, 326, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 2, 327, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 3, 328, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 4, 329, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 5, 330, "ボクの可愛さについて\nみんな話し合わない\nのかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 101, 331, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 102, 332, "あの人も今\n可愛いボクのこと\n考えてるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 1, 333, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 2, 334, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 3, 335, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 4, 336, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 101, 337, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 102, 338, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 1, 1, 339, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 2, 2, 340, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 3, 3, 341, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 1, 342, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 2, 343, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 3, 344, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 4, 345, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 5, 346, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 6, 347, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 7, 348, "ボクが食べると\n食べ物まで\n可愛いなあ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100802, 0, 0, 8, 349, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 1, 351, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 2, 352, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 3, 353, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 4, 354, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 5, 355, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 101, 356, "シグルド…\n今頃何処で何を\nしているのだろう…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 102, 357, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 1, 358, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 2, 359, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 3, 360, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 4, 361, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 101, 362, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 102, 363, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 1, 1, 364, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 2, 2, 365, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 3, 3, 366, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 1, 367, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 2, 368, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 3, 369, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 4, 370, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 5, 371, "これが我らの求めた\n安寧か…\n悪くない…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 6, 372, "この程度\n前世での激闘に\n比べれば…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 7, 373, "最近金欠でな…\nいやぁありがたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100901, 0, 0, 8, 374, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 1, 376, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 2, 377, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 3, 378, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 4, 379, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 5, 380, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 101, 381, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 102, 382, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 1, 383, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 2, 384, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 3, 385, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 4, 386, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 101, 387, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 102, 388, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 1, 1, 389, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 2, 2, 390, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 3, 3, 391, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 1, 392, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 2, 393, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 3, 394, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 4, 395, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 5, 396, "これが我らの求めた\n安寧か…\n悪くない…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 6, 397, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 7, 398, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (100902, 0, 0, 8, 399, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 1, 401, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 2, 402, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 3, 403, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 4, 404, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 5, 405, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 101, 406, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 102, 407, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 1, 408, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 2, 409, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 3, 410, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 4, 411, "うちの話し方\nのんびりしてます\nやろか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 101, 412, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 102, 413, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 1, 1, 414, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 2, 2, 415, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 3, 3, 416, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 1, 417, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 2, 418, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 3, 419, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 4, 420, "心地ええどす。まるで\nお花畑にいるようやね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 5, 421, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 6, 422, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 7, 423, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101001, 0, 0, 8, 424, "絵本、絵本～\n幸せやわぁ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 1, 426, "王子はんにとって\n自慢の姫になるえ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 2, 427, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 3, 428, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 4, 429, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 5, 430, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 101, 431, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 102, 432, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 1, 433, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 2, 434, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 3, 435, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 4, 436, "絵本読むんも\nええなぁ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 101, 437, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 102, 438, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 1, 1, 439, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 2, 2, 440, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 3, 3, 441, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 1, 442, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 2, 443, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 3, 444, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 4, 445, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 5, 446, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 6, 447, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 7, 448, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101002, 0, 0, 8, 449, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 1, 451, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 2, 452, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 3, 453, "い、いけない！\n縫い針落としちゃった", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 4, 454, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 5, 455, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 101, 456, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 102, 457, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 1, 458, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 2, 459, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 3, 460, "勉強、見てくれるん\nですか？　やった～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 4, 461, "服がほつれてますよ？\n直してあげます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 101, 462, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 102, 463, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 1, 1, 464, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 2, 2, 465, "お昼休憩に\n甘いものが\n食べたいです～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 3, 3, 466, "ねむいです～\nでももうちょっと\n勉強しないと～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 1, 467, "この服、かわいい♪\n今度作って\nみよっと～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 2, 468, "勉強で疲れたから\n休憩しよ～っと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 3, 469, "かわいい♪\n私が服を作って\nあげよっか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 4, 470, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 5, 471, "のんびりしたら、\nまた勉強しよっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 6, 472, "運動すると勉強も\nはかどるよね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 7, 473, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101101, 0, 0, 8, 474, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 1, 476, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 2, 477, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 3, 478, "い、いけない！\n縫い針落としちゃった", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 4, 479, "笑う時には服着ます！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 5, 480, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 101, 481, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 102, 482, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 1, 483, "勉強、見てくれるん\nですか？　やった～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 2, 484, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 3, 485, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 4, 486, "勉強は…\n明日にしようっと", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 101, 487, "妹だけど、大人な\nレディですからね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 102, 488, "な、ナデナデとか\n恥ずかしいな～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 1, 1, 489, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 2, 2, 490, "お昼休憩に\n甘いものが\n食べたいです～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 3, 3, 491, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 1, 492, "服装や髪形が変わると\n気分も変わるよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 2, 493, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 3, 494, "かわいい♪\n私が服を作って\nあげよっか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 4, 495, "いまならどんな試験も\n合格できそうです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 5, 496, "のんびりしたら\nまた勉強しよっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 6, 497, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 7, 498, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101102, 0, 0, 8, 499, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 1, 501, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 2, 502, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 3, 503, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 4, 504, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 5, 505, "もし、超能力のことが\nバレたら…ブルブル", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 101, 506, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 102, 507, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 1, 508, "ん～、なになに？\n一緒にお昼寝する？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 2, 509, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 3, 510, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 4, 511, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 101, 512, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 102, 513, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 1, 1, 514, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 2, 2, 515, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 3, 3, 516, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 1, 517, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 2, 518, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 3, 519, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 4, 520, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 5, 521, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 6, 522, "眠くならないように、\n運動しよっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 7, 523, "お腹いっぱいになると\n眠くなるよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101201, 0, 0, 8, 524, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 1, 526, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 2, 527, "もっといっぱい\n超能力使えたら\nいいのにな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 3, 528, "ねえねえ、\n今から人助け、\n付き合ってほしいなー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 4, 529, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 5, 530, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 101, 531, "キミと一緒なら、\nなんだって出来る！\n間違いない！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 102, 532, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 1, 533, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 2, 534, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 3, 535, "一緒に\nお出かけしたいなー\nデートしよっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 4, 536, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 101, 537, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 102, 538, "う～だんだん眠く…\nねぇ一緒に寝よ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 1, 1, 539, "キミって早起きだね！\n私は寝ようかな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 2, 2, 540, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 3, 3, 541, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 1, 542, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 2, 543, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 3, 544, "これ…可愛い！\nシオリンに\n届けてあげたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 4, 545, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 5, 546, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 6, 547, "よーし\n困ってる人のために\nパワーアップ☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 7, 548, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101202, 0, 0, 8, 549, "睡眠学習って\n本を読みながら\n寝るんだっけ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 1, 551, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 2, 552, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 3, 553, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 4, 554, "ここで撮影会、\nしようかな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 5, 555, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 101, 556, "やば…\nこのままじゃ、うち\nキュン死にかも…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 102, 557, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 1, 558, "うちの写真撮るの？\nどんなポーズ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 2, 559, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 3, 560, "宿題見てくんない？\nひとりじゃムリ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 4, 561, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 101, 562, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 102, 563, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 1, 1, 564, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 2, 2, 565, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 3, 3, 566, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 1, 567, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 2, 568, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 3, 569, "きゃわたーん☆\n君もおしゃれしよっか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 4, 570, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？", 1, 3, 1);
