INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 6, 572, "あはは、なんか\nウォーキングの練習\nしてるみたーい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 7, 573, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101601, 0, 0, 8, 574, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 1, 576, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 2, 577, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 3, 578, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　", 1, 1, 1);
