INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 1, 592, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 2, 593, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 3, 594, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 4, 595, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 5, 596, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 6, 597, "食べた分は\n消費しないとね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 7, 598, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101602, 0, 0, 8, 599, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 1, 601, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 2, 602, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 3, 603, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 4, 604, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 5, 605, "さぁ、みんなで歌って\n踊るさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 101, 606, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 102, 607, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 1, 608, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 2, 609, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 3, 610, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 4, 611, "空手の型の\n練習するさー\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 101, 612, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 102, 613, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 1, 1, 614, "ハイターイ！\n今日も一日、\nがんばるさー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 2, 2, 615, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 3, 3, 616, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 1, 617, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 2, 618, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 3, 619, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 4, 620, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 5, 621, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 6, 622, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 7, 623, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101701, 0, 0, 8, 624, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 1, 626, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 2, 627, "さぁ、みんなで歌って\n踊るさ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 3, 628, "元気がないときは\n私のところに来るさー", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 4, 629, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 5, 630, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 101, 631, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 102, 632, "幸せすぎて踊らずには\nいられないさ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 1, 633, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 2, 634, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 3, 635, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 4, 636, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 101, 637, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 102, 638, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 1, 1, 639, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 2, 2, 640, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 3, 3, 641, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 1, 642, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 2, 643, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 3, 644, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 4, 645, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 5, 646, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 6, 647, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 7, 648, "もぐもぐ…もっと\nおかわりがほしいさー", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101702, 0, 0, 8, 649, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 1, 651, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 2, 652, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 3, 653, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 4, 654, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 5, 655, "はー…\nちょっと肩が\nこっちゃった…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 101, 656, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 102, 657, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 1, 658, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 2, 659, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 3, 660, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 4, 661, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 101, 662, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 102, 663, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 1, 1, 664, "学校に\n遅刻しないように\n早く準備しないとね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 2, 2, 665, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 3, 3, 666, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 1, 667, "もしかして…\n少し露出多いかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 2, 668, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 3, 669, "かわいい♪　あなたも\n私の生徒になる？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 4, 670, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 5, 671, "まだ寒いわぁ\n誰か隣に\n来てくれたら…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 6, 672, "運動部の顧問も\nできるかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 7, 673, "学校の給食でも\n食べたいわぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101801, 0, 0, 8, 674, "いい参考書を\n見つけちゃった♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 1, 676, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 2, 677, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 3, 678, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 4, 679, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 5, 680, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 101, 681, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 102, 682, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 1, 683, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 2, 684, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 3, 685, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 4, 686, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 101, 687, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 102, 688, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 1, 1, 689, "朝の特別授業\nしてあげましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 2, 2, 690, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 3, 3, 691, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 1, 692, "もしかして…\n少し露出多いかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 2, 693, "私の膝枕と、どっちが\nフカフカかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 3, 694, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 4, 695, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 5, 696, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 6, 697, "運動部の顧問も\nできるかしら？　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 7, 698, "あん！\nお胸にこぼしちゃった", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (101802, 0, 0, 8, 699, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 1, 701, "おにいちゃんは、\nにんじん食べられる？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 2, 702, "えへへ、\nお絵かきしよ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 3, 703, "あのね、ミミと\nあそんでほしいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 4, 704, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 5, 705, "うさぎさん、\nぴょんぴょん\nぴょーん♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 101, 706, "おにいちゃんの絵、\nかいてあげるね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 102, 707, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 1, 708, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 2, 709, "やった～！\nおにいちゃんが\nほめてくれた～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 3, 710, "ねえねえ\nおにいちゃん、\n一緒にあそぼっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 4, 711, "おにいちゃん、\n遊んでくれるの？\n　わぁい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 101, 712, "わぁい！\nおにいちゃんだ～♪\nぎゅ～っ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 102, 713, "あのねあのね、\nミミの頭ポンポンって\nしてほしいな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 1, 1, 714, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 2, 2, 715, "うわぁ！\nお日さまがすごーく\n高いところにある～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 3, 3, 716, "おにいちゃん\nおやすみのご本\nよんで～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 1, 717, "ミミも、\nうさぎさんみたいに、\nかわいくなりたいな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 2, 718, "のんびり～♪\nゆったり～♪\nまったり～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 3, 719, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 4, 720, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 5, 721, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 6, 722, "ふ、ふぇ～　ミミ、\n運動苦手だよ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 7, 723, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102001, 0, 0, 8, 724, "ミミね！\nこのお話だーい好き！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 1, 726, "ぴょんぴょん\n跳ねるよ\nうっさぎっさ～ん♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 2, 727, "うさぎさんの\nマネだよ～\n似てるかな～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 3, 728, "モフモフすると\nきもちい～よ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 4, 729, "ミソギちゃん\nキョウカちゃん\nあ～そ～ぼっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 5, 730, "おにいちゃん\nおやすみのご本\nよんで～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 101, 731, "ねえ、おにいちゃん\nミミのこと、すき？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 102, 732, "おっきくなったら\nおにいちゃんの\nお嫁さんになるんだ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 1, 733, "やった～！\nおにいちゃんが\nほめてくれた～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 2, 734, "おにいちゃん\n遊んでくれるの？\nわぁい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 3, 735, "おにいちゃん\nあのね…\nいいこいいこして～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 4, 736, "あはは～\nくすぐったいよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 101, 737, "おにいちゃんの\nおひざの上、とっても\n安心するんだ～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 102, 738, "わぁい♪\nおにいちゃんの\nなでなで\nうれし～な♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 1, 1, 739, "おひさまさん\nおはよ～！\n毎朝、早おきだね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 2, 2, 740, "お昼寝お昼寝～♪\nおにいちゃんと一緒に\nお昼寝～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 3, 3, 741, "眠くなっちゃった～\nおにいちゃん！\n今日は一緒に寝よ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 1, 742, "ミミも\nうさぎさんみたいに\nかわいくなりたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 2, 743, "ふわふわ～モコモコ～\n気持ちいい～♪\nミミもゴロゴロ～ン♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 3, 744, "わぁ～かわいい～♪\nミミ、幸せ～♪\n抱きついていいかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 4, 745, "いいにおい～♪\nお鼻ピクピク♪\nうさぎさんも喜ぶよ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 5, 746, "あったか～♪\nぬくぬく～♪\nポカポカ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 6, 747, "はぁはぁ…\nふえ～ミミ\n疲れちゃうよ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 7, 748, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102002, 0, 0, 8, 749, "ミミね！\nこのお話だーい好き！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 1, 751, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 2, 752, "お外より\nお部屋の中の方が\n落ち着くな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 3, 753, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 4, 754, "あ、あめんぼあかいな\nあいうえお！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 5, 755, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 101, 756, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 102, 757, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 1, 758, "こ、この本、とっても\nおもしろいんですよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 2, 759, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 3, 760, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 4, 761, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 101, 762, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 102, 763, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 1, 1, 764, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 2, 2, 765, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 3, 3, 766, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 1, 767, "可愛い女の子に変身！\n出来ますように…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 2, 768, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 3, 769, "この子を相手に、\n演技の練習～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 4, 770, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 5, 771, "えへへ。\nぽっかぽかです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 6, 772, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 7, 773, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102101, 0, 0, 8, 774, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 1, 776, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 2, 777, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 3, 778, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 4, 779, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 5, 780, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 101, 781, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 102, 782, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 1, 783, "こ、この本、とっても\nおもしろいんですよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 2, 784, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 3, 785, "何か私にできる事\n…そうだ！\nお掃除しますね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 4, 786, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 101, 787, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 102, 788, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 1, 1, 789, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 2, 2, 790, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 3, 3, 791, "寝る前に\n台本を読むと、\n覚えやすいんです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 1, 792, "可愛い女の子に変身！\n出来ますように…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 2, 793, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 3, 794, "この子を相手に\n演技の練習～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 4, 795, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 5, 796, "えへへ\nぽっかぽかです…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 6, 797, "え、演劇には\nた、体力も重要\nだから～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 7, 798, "残さず\n食べますね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102102, 0, 0, 8, 799, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 1, 801, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 2, 802, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 3, 803, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 4, 804, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 5, 805, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 101, 806, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 102, 807, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 1, 808, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 2, 809, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 3, 810, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 4, 811, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 101, 812, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 102, 813, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 1, 1, 814, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 2, 2, 815, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 3, 3, 816, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 1, 817, "アカリみたいに\n可愛くなるのは\n難しいかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 2, 818, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 3, 819, "可愛い…けど、\n私には似合わないかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 4, 820, "はあ、リラックスして\nゲームができるわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 5, 821, "ん…暖かくて、\n寝落ちしちゃいそう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 6, 822, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 7, 823, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102201, 0, 0, 8, 824, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 1, 826, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 2, 827, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 3, 828, "レアカードをゲット！\nすごいでしょ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 4, 829, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 5, 830, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 101, 831, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 102, 832, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 1, 833, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 2, 834, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 3, 835, "ねえ、見て！\n新しいゲームを\n買って来たの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 4, 836, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 101, 837, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 102, 838, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 1, 1, 839, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 2, 2, 840, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 3, 3, 841, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 1, 842, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 2, 843, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 3, 844, "可愛い…けど、\n私には似合わないかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 4, 845, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 5, 846, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 6, 847, "最近ゲームばかりだし\nたまにはいいかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 7, 848, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102202, 0, 0, 8, 849, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 1, 851, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 2, 852, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 3, 853, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 4, 854, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 5, 855, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 101, 856, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 102, 857, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 1, 858, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 2, 859, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 3, 860, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 4, 861, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 101, 862, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 102, 863, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 1, 1, 864, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 2, 2, 865, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 3, 3, 866, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 2, 868, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 3, 869, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 4, 870, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 5, 871, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 7, 873, "おいしい～\nぷうきちも\n食べてみる？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102301, 0, 0, 8, 874, "ぷうきち！\nこの本、読んで～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 1, 901, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 2, 902, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 3, 903, "どうしたら転ばなく\nなるんでしょうか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 4, 904, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 5, 905, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 101, 906, "おいしい料理で\nお出迎えしなくちゃ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 102, 907, "一生懸命\nご奉仕しますよ～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 1, 908, "耳かきしましょうか？\nえ？　こわい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 2, 909, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 3, 910, "おかえりなさいませ\nご主人さま♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 4, 911, "すみません\nすみません\nすみません～～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 101, 912, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 102, 913, "私のこと、\n専属メイドと思って\n頼ってくださいね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 1, 1, 914, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 2, 2, 915, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 3, 3, 916, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 1, 917, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 2, 918, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 3, 919, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 4, 920, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 5, 921, "ここでお洗濯ものを\n乾かしましょうか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 6, 922, "あわわわっ…\nこ、転びそうです～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 7, 923, "これくらい\nおいしいものを\n作ってみたいです～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102501, 0, 0, 8, 924, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 1, 926, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 2, 927, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 3, 928, "どうやったら、立派な\nメイドになれるのかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 4, 929, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 5, 930, "ご用があれば\nなんでも言って\nくださいね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 101, 931, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 102, 932, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 1, 933, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 2, 934, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 3, 935, "私、またなにか\nやっちゃいましたか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 4, 936, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 101, 937, "転んだふりであの人に\n…って！\nなんでもないですよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 102, 938, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 1, 1, 939, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 2, 2, 940, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 3, 3, 941, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 1, 942, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 2, 943, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 3, 944, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 4, 945, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 5, 946, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 6, 947, "運動しようとすると\nいつも怪我するん\nですよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 7, 948, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102502, 0, 0, 8, 949, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 1, 951, "あの方と私だけの\n部屋にしたいですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 2, 952, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 3, 953, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 4, 954, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 5, 955, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 101, 956, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 102, 957, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 1, 958, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 2, 959, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 3, 960, "お願いがあるのです。\n新しい研究の実験台に\n…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 4, 961, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 101, 962, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 102, 963, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 1, 1, 964, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 2, 2, 965, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 3, 3, 966, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 1, 967, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 2, 968, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 3, 969, "あら、キメラの素体に\nぴったりですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 4, 970, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 5, 971, "あたたかい…\n加熱調合にも\n使えそうですわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 6, 972, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 7, 973, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102701, 0, 0, 8, 974, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 1, 976, "あの方と私だけの\n部屋にしたいですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 2, 977, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 3, 978, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 4, 979, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 5, 980, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 101, 981, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 102, 982, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 1, 983, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 2, 984, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 3, 985, "お願いがあるのです\n新しい研究の\n実験台に…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 4, 986, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 101, 987, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 102, 988, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 1, 1, 989, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 2, 2, 990, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 3, 3, 991, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 1, 992, "私ももう少し\n外見に気を配る\nべきでしょうか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 2, 993, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 3, 994, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 4, 995, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 5, 996, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 6, 997, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 7, 998, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102702, 0, 0, 8, 999, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 1, 1001, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 2, 1002, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 3, 1003, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 4, 1004, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 5, 1005, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 101, 1006, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 102, 1007, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 1, 1008, "ねぇあんた、どこか\n安いお店知らない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 2, 1009, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 3, 1010, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 4, 1011, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 101, 1012, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 102, 1013, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 1, 1, 1014, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 2, 2, 1015, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 3, 3, 1016, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 1, 1017, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 2, 1018, "これで仕事は一段落ね\nちょっと休憩…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 3, 1019, "うふふ、\nかわいいものを\n見るのは幸せね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 4, 1020, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 5, 1021, "風邪を引かないように\n身体を温めなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 6, 1022, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 7, 1023, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102801, 0, 0, 8, 1024, "知識から過去を学んで\n未来に活かすわよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 1, 1026, "安売りのお店の情報\n整理しないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 2, 1027, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 3, 1028, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 4, 1029, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 5, 1030, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 101, 1031, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 102, 1032, "あいつと一緒に…\n色々なところに\nいきたいな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 1, 1033, "今度買い物に\n付き合いなさい\nセールがあるのよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 2, 1034, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 3, 1035, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 4, 1036, "あんたとこうして\nのんびりするのも\nいいものね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 101, 1037, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 102, 1038, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 1, 1, 1039, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 2, 2, 1040, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 3, 3, 1041, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 1, 1042, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 2, 1043, "これで仕事は一段落ね\nちょっと休憩…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 3, 1044, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 4, 1045, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 5, 1046, "風邪を引かないように\n身体を温めなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 6, 1047, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 7, 1048, "しっかり食べて\n栄養補給、栄養補給！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102802, 0, 0, 8, 1049, "知識から過去を学んで\n未来に活かすわよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 1, 1051, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 2, 1052, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 3, 1053, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 4, 1054, "彼とデュエット\nしてみたいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 5, 1055, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 101, 1056, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 102, 1057, "写真集見なくても、\n本物がここに\nいるのにな～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 1, 1058, "握手会の練習、\n付き合ってくれない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 2, 1059, "写真集のポーズ\n練習中～！\nど、どうかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 3, 1060, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 4, 1061, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 101, 1062, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 102, 1063, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 1, 1, 1064, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 2, 2, 1065, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 3, 3, 1066, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 1, 1067, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 2, 1068, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 3, 1069, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 4, 1070, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 5, 1071, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 6, 1072, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 7, 1073, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102901, 0, 0, 8, 1074, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 1, 1076, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 2, 1077, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 3, 1078, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 4, 1079, "彼とデュエット\nしてみたいな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 5, 1080, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 101, 1081, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 102, 1082, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 1, 1083, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 2, 1084, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 3, 1085, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 4, 1086, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 101, 1087, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 102, 1088, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 1, 1, 1089, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 2, 2, 1090, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 3, 3, 1091, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 1, 1092, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 2, 1093, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 3, 1094, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 4, 1095, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 5, 1096, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 6, 1097, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 7, 1098, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (102902, 0, 0, 8, 1099, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 1, 1101, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 2, 1102, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 3, 1103, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 4, 1104, "むむ、アクダイカンの\n気配がするデース", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 5, 1105, "クニトリ合戦、\n楽しみデース！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 101, 1106, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 102, 1107, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 1, 1108, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 2, 1109, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 3, 1110, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 4, 1111, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 101, 1112, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 102, 1113, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 1, 1, 1114, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 2, 2, 1115, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 3, 3, 1116, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 1, 1117, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 2, 1118, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 3, 1119, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 4, 1120, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 5, 1121, "身体を暖めて\nイクサに向かうデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 6, 1122, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 7, 1123, "うーん、\n美味しいデス…\n幸せデス～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103001, 0, 0, 8, 1124, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 1, 1126, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 2, 1127, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 3, 1128, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 4, 1129, "むむ、アクダイカンの\n気配がするデース", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 5, 1130, "クニトリ合戦\n楽しみデース！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 101, 1131, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 102, 1132, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 1, 1133, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 2, 1134, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 3, 1135, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 4, 1136, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 101, 1137, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 102, 1138, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 1, 1, 1139, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 2, 2, 1140, "もうお昼デス\nお昼寝するデスか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 3, 3, 1141, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 1, 1142, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 2, 1143, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 3, 1144, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 4, 1145, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 5, 1146, "身体を暖めてイクサに\n向かうデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 6, 1147, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 7, 1148, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103002, 0, 0, 8, 1149, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 1, 1151, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 2, 1152, "父は、いつも男性に\nきびしいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 3, 1153, "占ってほしい人は、\nいつでも言って\nください", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 4, 1154, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 5, 1155, "この部屋、風水的にも\n運気が上がりそうです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 101, 1156, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 102, 1157, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 1, 1158, "女難の相が\n出ていますね…\n気をつけてください", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 2, 1159, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 3, 1160, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 4, 1161, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 101, 1162, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 102, 1163, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 1, 1, 1164, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 2, 2, 1165, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 3, 3, 1166, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 1, 1167, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 2, 1168, "一休みしましょう。\n憑かれて疲れました", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 3, 1169, "かわいい…\n付喪神が宿らないかな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 4, 1170, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 5, 1171, "気持ちいいです…\n極楽浄土みたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 6, 1172, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 7, 1173, "お父さんも\n食べたいの？\n死んでるのに？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103101, 0, 0, 8, 1174, "この本…\nもしかして予言書？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 1, 1176, "ここにいる幽霊は\nみんな幸せそうです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 2, 1177, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 3, 1178, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 4, 1179, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 5, 1180, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 101, 1181, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 102, 1182, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 1, 1183, "この部屋、風水的にも\n運気が上がりそうです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 2, 1184, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 3, 1185, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 4, 1186, "ラップ音？\nいえ、父のいびきです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 101, 1187, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 102, 1188, "前世って信じますか？\n私とあなたは\nもしかすると…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 1, 1, 1189, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 2, 2, 1190, "明るい時間は\nあまり得意では\nないんです…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 3, 3, 1191, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 1, 1192, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 2, 1193, "うとうと…\nあれ…\n走馬灯が見える…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 3, 1194, "かわいい…\nお父さんみたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 4, 1195, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 5, 1196, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 6, 1197, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 7, 1198, "お父さんの分も、\n私が食べるね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103102, 0, 0, 8, 1199, "この本の占い\n私の占いと違う…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 1, 1201, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 2, 1202, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 3, 1203, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 4, 1204, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 5, 1205, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 101, 1206, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 102, 1207, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 1, 1208, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 2, 1209, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 3, 1210, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 4, 1211, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 101, 1212, "その、よろしければ\n私とお散歩など\nいかがです？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 102, 1213, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 1, 1, 1214, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 2, 2, 1215, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 3, 3, 1216, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 1, 1217, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 2, 1218, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 3, 1219, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 4, 1220, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 5, 1221, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 6, 1222, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 7, 1223, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103201, 0, 0, 8, 1224, "経済の仕組みを\nしっかりと\n学ばなくては…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 1, 1226, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 2, 1227, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 3, 1228, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 4, 1229, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 5, 1230, "この狭さも\n慣れれば\n快適ですわ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 101, 1231, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 102, 1232, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 1, 1233, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 2, 1234, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 3, 1235, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 4, 1236, "今度一緒に…\nパーティにでも\n行きませんこと？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 101, 1237, "その、よろしければ\n私とお散歩など\nいかがです？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 102, 1238, "あなたさまの優しさは\nお金には\n代えられませんわね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 1, 1, 1239, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 2, 2, 1240, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 3, 3, 1241, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 1, 1242, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 2, 1243, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 3, 1244, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 4, 1245, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 5, 1246, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 6, 1247, "目標は遠くに\n置いた方が励みに\nなりますわ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 7, 1248, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103202, 0, 0, 8, 1249, "商人って、物語では\n悪役にされやすいの\nですね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 1, 1251, "家の中のものを使って\n物ボケ、考えるべ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 2, 1252, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 3, 1253, "うちに塀ができたら、\nへー、って言いたい！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 4, 1254, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 5, 1255, "エリザベスも\n連れてきてぇ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 101, 1256, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 102, 1257, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 1, 1258, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 2, 1259, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 3, 1260, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 4, 1261, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 101, 1262, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 102, 1263, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 1, 1, 1264, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 2, 2, 1265, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 3, 3, 1266, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 1, 1267, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 2, 1268, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 3, 1269, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 4, 1270, "必要以上に\n癒されちまうべや～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 5, 1271, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 6, 1272, "お笑い芸人は、\n体が資本だべさ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 7, 1273, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103301, 0, 0, 8, 1274, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 1, 1276, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 2, 1277, "この部屋でも、商品\n売っていいんだべか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 3, 1278, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 4, 1279, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 5, 1280, "エリザベスも\n連れてきてぇ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 101, 1281, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 102, 1282, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 1, 1283, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 2, 1284, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 3, 1285, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 4, 1286, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 101, 1287, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 102, 1288, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 1, 1, 1289, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 2, 2, 1290, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 3, 3, 1291, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 1, 1292, "うーん…衣装、\nずれてねぇべか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 2, 1293, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 3, 1294, "はう～\nなまらめんこいべや～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 4, 1295, "必要以上に\n癒されちまうべや～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 5, 1296, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 6, 1297, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 7, 1298, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103302, 0, 0, 8, 1299, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 1, 1301, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 2, 1302, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 3, 1303, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 4, 1304, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 5, 1305, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 101, 1306, "デートとか…\n誘っても嫌がられ\nないかな…？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 102, 1307, "もしかして運命の\n相手だったり…\nするのかな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 1, 1308, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 2, 1309, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 3, 1310, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 4, 1311, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 101, 1312, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 102, 1313, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 1, 1, 1314, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 2, 2, 1315, "おっひるー♪\nなに食べよっかな～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 3, 3, 1316, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 1, 1317, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 2, 1318, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 3, 1319, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 4, 1320, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 5, 1321, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 6, 1322, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 7, 1323, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103401, 0, 0, 8, 1324, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 1, 1326, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 2, 1327, "今日は\nのんびり\n過ごそうかな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 3, 1328, "あ…\nお皿、洗って\nなかった…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 4, 1329, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 5, 1330, "素敵な彼氏…\n欲しいなぁ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 101, 1331, "彼と私\n釣り合うかな…\n大丈夫よね？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 102, 1332, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 1, 1333, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 2, 1334, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 3, 1335, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 4, 1336, "お姉さんが～\n可愛がって\nあげようか～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 101, 1337, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 102, 1338, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 1, 1, 1339, "さーて、今日も\nいっちょやるぞー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 2, 2, 1340, "後でのんびり\nストレッチでも\nしようっと♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 3, 3, 1341, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 1, 1342, "仕事の前に、髪とか\n整えないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 2, 1343, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 3, 1344, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 4, 1345, "うわあ\nこれ、私の好きな\n香りだー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 5, 1346, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 6, 1347, "地道な努力が\nリア充への\n第一歩！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 7, 1348, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103402, 0, 0, 8, 1349, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 1, 1351, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 2, 1352, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 3, 1353, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 4, 1354, "ちょっと、休憩…\n本でも読もうかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 5, 1355, "もっとたくさんの本に\n囲まれて生活したいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 101, 1356, "むかしむかしある所に\nシオリと\nあの人が…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 102, 1357, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 1, 1358, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 2, 1359, "あ…お姉ちゃんと\n約束があったんだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 3, 1360, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 4, 1361, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 101, 1362, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 102, 1363, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 1, 1, 1364, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 2, 2, 1365, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 3, 3, 1366, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 1, 1367, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 2, 1368, "少し休もう\n…やっぱり\nたくさん休もう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 3, 1369, "かわいいものが\n枕元にあると\n安心できるんですよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 4, 1370, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 5, 1371, "こうして温まって\nいると、病気も\n治る気がします", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 6, 1372, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 7, 1373, "少食ですけど\nこれならたくさん\n食べられそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103801, 0, 0, 8, 1374, "続きが気になって\n読むのを止める\nタイミングが…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 1, 1376, "しっかり休まないと\n早く回復するように", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 2, 1377, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 3, 1378, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 4, 1379, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 5, 1380, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 101, 1381, "むかしむかしある所に\nシオリと\nあの人が…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 102, 1382, "あの人の\nためにも早く\n病気を治さないと…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 1, 1383, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 2, 1384, "新記録です！\n一週間連続、\n平熱を保ちました！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 3, 1385, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 4, 1386, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 101, 1387, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 102, 1388, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 1, 1, 1389, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 2, 2, 1390, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 3, 3, 1391, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 1, 1392, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 2, 1393, "少し休もう\n…やっぱり\nたくさん休もう", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 3, 1394, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 4, 1395, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 5, 1396, "こうして温まって\nいると、病気も\n治る気がします", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 6, 1397, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 7, 1398, "いっぱい食べて\n健康な体にならないと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (103802, 0, 0, 8, 1399, "続きが気になって\n読むのを止める\nタイミングが…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 1, 1401, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 2, 1402, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 3, 1403, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 4, 1404, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 5, 1405, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 101, 1406, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 102, 1407, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 1, 1408, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 2, 1409, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 3, 1410, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 4, 1411, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 101, 1412, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 102, 1413, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 1, 1, 1414, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 2, 2, 1415, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 3, 3, 1416, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 1, 1417, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 2, 1418, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 3, 1419, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 4, 1420, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 5, 1421, "灯りを見てると、\n心がなごみます～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 6, 1422, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 7, 1423, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104001, 0, 0, 8, 1424, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 1, 1426, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 2, 1427, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 3, 1428, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 4, 1429, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 5, 1430, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 101, 1431, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 102, 1432, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 1, 1433, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 2, 1434, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 3, 1435, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 4, 1436, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 101, 1437, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 102, 1438, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 1, 1, 1439, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 2, 2, 1440, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 3, 3, 1441, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 1, 1442, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 2, 1443, "ほわあ～…\nふかふかって\nいいものですね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 3, 1444, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 4, 1445, "灯りを見てると\n心がなごみます～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 5, 1446, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 6, 1447, "身体を鍛えて\nみなさんを\n守ります！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 7, 1448, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104002, 0, 0, 8, 1449, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 1, 1451, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 2, 1452, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 3, 1453, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 4, 1454, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 5, 1455, "あとで曲を復習して\nおかないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 101, 1456, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 102, 1457, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 1, 1458, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 2, 1459, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 3, 1460, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 4, 1461, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 101, 1462, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 102, 1463, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 1, 1, 1464, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 2, 2, 1465, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 3, 3, 1466, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 1, 1467, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 2, 1468, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 3, 1469, "この子たちを\n相手に練習して\nみようかな…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 4, 1470, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 5, 1471, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 6, 1472, "歌うためには\n体力づくりは\n欠かせません", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 7, 1473, "歌った後は\nお腹が空いて\nしまうんです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104201, 0, 0, 8, 1474, "音楽について\n理論も勉強\nしないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 1, 1476, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 2, 1477, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 3, 1478, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 4, 1479, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 5, 1480, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 101, 1481, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 102, 1482, "私の中にある\n私だけの歌…\n作ってみようかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 1, 1483, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 2, 1484, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 3, 1485, "魔物退治ですか！？\n…違います？\n良かった…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 4, 1486, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 101, 1487, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 102, 1488, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 1, 1, 1489, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 2, 2, 1490, "少し休んだら\n午後の練習に\n行ってきますね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 3, 3, 1491, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 1, 1492, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 2, 1493, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 3, 1494, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 4, 1495, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 5, 1496, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 6, 1497, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 7, 1498, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104202, 0, 0, 8, 1499, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 1, 1501, "店の新メニューでも\n考えるかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 2, 1502, "あたしの店から、\n出前を届けようか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 3, 1503, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 4, 1504, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 5, 1505, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 101, 1506, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 102, 1507, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 1, 1508, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 2, 1509, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 3, 1510, "腹減ったか？　なんか\n作ってやろうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 4, 1511, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 101, 1512, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 102, 1513, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 1, 1, 1514, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 2, 2, 1515, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 3, 3, 1516, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 1, 1517, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 2, 1518, "ふわぁ、\n一休みすっかなぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 3, 1519, "うっ！　か、かわいい\nじゃねぇか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 4, 1520, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 5, 1521, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 6, 1522, "オラオラオラーーー！\n燃えるぜーーー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 7, 1523, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104301, 0, 0, 8, 1524, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 1, 1526, "パトロール終了～\n本日も異常なしっと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 2, 1527, "あたしの店から\n出前を届けようか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 3, 1528, "あいつ、早く\n帰ってこないかな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 4, 1529, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 5, 1530, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 101, 1531, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 102, 1532, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 1, 1533, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 2, 1534, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 3, 1535, "腹減ったか？　なんか\n作ってやろうか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 4, 1536, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 101, 1537, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 102, 1538, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 1, 1, 1539, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 2, 2, 1540, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 3, 3, 1541, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 1, 1542, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 2, 1543, "ふぅ～♪\n疲れがとれるな～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 3, 1544, "うっ！　か、かわいい\nじゃねぇか", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 4, 1545, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 5, 1546, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 6, 1547, "オラオラオラーーー！\n燃えるぜーーー！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 7, 1548, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104302, 0, 0, 8, 1549, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 1, 1551, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 2, 1552, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 3, 1553, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 4, 1554, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 5, 1555, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 101, 1556, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 102, 1557, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 1, 1558, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 2, 1559, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 3, 1560, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 4, 1561, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 101, 1562, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 102, 1563, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 1, 1, 1564, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 2, 2, 1565, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 3, 3, 1566, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 1, 1567, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 2, 1568, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 3, 1569, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 4, 1570, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 5, 1571, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 6, 1572, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 7, 1573, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104501, 0, 0, 8, 1574, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 1, 1576, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 2, 1577, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 3, 1578, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 4, 1579, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 5, 1580, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 101, 1581, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 102, 1582, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 1, 1583, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 2, 1584, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 3, 1585, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 4, 1586, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 101, 1587, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 102, 1588, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 1, 1, 1589, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 2, 2, 1590, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 3, 3, 1591, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 1, 1592, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 2, 1593, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 3, 1594, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 4, 1595, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 5, 1596, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 6, 1597, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 7, 1598, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104502, 0, 0, 8, 1599, "どんな本も\nよく見ると\nいやらしいです…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 1, 1601, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 2, 1602, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 3, 1603, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 4, 1604, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 5, 1605, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 101, 1606, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 102, 1607, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 1, 1608, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 2, 1609, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 3, 1610, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 4, 1611, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 101, 1612, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 102, 1613, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 1, 1, 1614, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 2, 2, 1615, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 3, 3, 1616, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 1, 1617, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 2, 1618, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 3, 1619, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 4, 1620, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 5, 1621, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 6, 1622, "本当は外で\n運動したいん\nだけどにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 7, 1623, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104601, 0, 0, 8, 1624, "ふむふむ…猫の\nしつけ方は、と…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 1, 1626, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 2, 1627, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 3, 1628, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 4, 1629, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 5, 1630, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 101, 1631, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 102, 1632, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 1, 1633, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 2, 1634, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 3, 1635, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 4, 1636, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 101, 1637, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 102, 1638, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 1, 1, 1639, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 2, 2, 1640, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 3, 3, 1641, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 1, 1642, "にゃにゃ…\n髪が乱れてるにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 2, 1643, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 3, 1644, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 4, 1645, "猫でもこわくない\n優しい灯りにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 5, 1646, "ぽっかぽか\nしあわせな気分にゃ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 6, 1647, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 7, 1648, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104602, 0, 0, 8, 1649, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 1, 1651, "内職手伝える人\nいないかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 2, 1652, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 3, 1653, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 4, 1654, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 5, 1655, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 101, 1656, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 102, 1657, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 1, 1658, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 2, 1659, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 3, 1660, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 4, 1661, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 101, 1662, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 102, 1663, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 1, 1, 1664, "おはよう！\n今日も朝から\nバイトよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 2, 2, 1665, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 3, 3, 1666, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 1, 1667, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 2, 1668, "はぁ～バイトで\n疲れたから癒される～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 3, 1669, "これを内職で\n作ったら\n儲かるかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 4, 1670, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 5, 1671, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 6, 1672, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 7, 1673, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104801, 0, 0, 8, 1674, "日々絶えず学んで、\n生活に活かすのよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 1, 1676, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 2, 1677, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 3, 1678, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 4, 1679, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 5, 1680, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 101, 1681, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 102, 1682, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 1, 1683, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 2, 1684, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 3, 1685, "今から\n休憩するところなの\n一緒にどう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 4, 1686, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 101, 1687, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 102, 1688, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 1, 1, 1689, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 2, 2, 1690, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 3, 3, 1691, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 1, 1692, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 2, 1693, "ここで少し\n休んだら\nまたバイトよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 3, 1694, "これを内職で\n作ったら\n儲かるかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 4, 1695, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 5, 1696, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 6, 1697, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 7, 1698, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104802, 0, 0, 8, 1699, "日々絶えず学んで\n生活に活かすのよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 1, 1701, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 2, 1702, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 3, 1703, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 4, 1704, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 5, 1705, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 101, 1706, "この部屋を、\n私と弟くんの\n愛の巣に…", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 102, 1707, "はぁ～…弟くん\n禁断症状だよ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 1, 1708, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 2, 1709, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 3, 1710, "姉と弟の外出は\nデートって\nいうのかな？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 4, 1711, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 101, 1712, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 102, 1713, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 1, 1, 1714, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 2, 2, 1715, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 3, 3, 1716, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 1, 1717, "きれいなお姉ちゃん\n好きだよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 2, 1718, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 3, 1719, "かわいい♪\nお姉ちゃんチョップ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 4, 1720, "この香り、\n弟くん好きかな？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 5, 1721, "これ、弟くんみたいに\nあったかいね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 6, 1722, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 7, 1723, "あ、これ\n弟くんが好きな\n味付けだね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104901, 0, 0, 8, 1724, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 1, 1726, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 2, 1727, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 3, 1728, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 4, 1729, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 5, 1730, "はっ！\n弟くんが困ってる\n気がする！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 101, 1731, "はぁ～…弟くん、\n禁断症状だよ～", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 102, 1732, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 1, 1733, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 2, 1734, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 3, 1735, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 4, 1736, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 101, 1737, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 102, 1738, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 1, 1, 1739, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 2, 2, 1740, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 3, 3, 1741, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 1, 1742, "弟くんを\n誘惑しちゃうぞ～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 2, 1743, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 3, 1744, "かわいい♪\nお姉ちゃんチョップ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 4, 1745, "お肌に潤いが\nでてきたみたい～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 5, 1746, "これ、弟くんみたいに\nあったかいね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 6, 1747, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 7, 1748, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (104902, 0, 0, 8, 1749, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 1, 1751, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 2, 1752, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 3, 1753, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 4, 1754, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 5, 1755, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 101, 1756, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 102, 1757, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 1, 1758, "わーい、アメだー♪\n食べる食べるー♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 2, 1759, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 3, 1760, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 4, 1761, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 101, 1762, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 102, 1763, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 1, 1, 1764, "朝のスキンケアは\n大事なの！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 2, 2, 1765, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 3, 3, 1766, "エイジングケアを\nしないとねー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 1, 1767, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 2, 1768, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 3, 1769, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 4, 1770, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 5, 1771, "あったかーい♪\nここで宿題\nしようかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 6, 1772, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 7, 1773, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105001, 0, 0, 8, 1774, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 1, 1776, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 2, 1777, "恋人にするなら\nイケメンか\nただイケよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 3, 1778, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 4, 1779, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 5, 1780, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 101, 1781, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 102, 1782, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 1, 1783, "なーに？\nアタシに甘えたいの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 2, 1784, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 3, 1785, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 4, 1786, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 101, 1787, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 102, 1788, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 1, 1, 1789, "レディの朝は\n忙しいのよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 2, 2, 1790, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 3, 3, 1791, "エイジングケアを\nしないとねー", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 1, 1792, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 2, 1793, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 3, 1794, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 4, 1795, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 5, 1796, "あったかーい♪\nここで宿題\nしようかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 6, 1797, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 7, 1798, "レディは、食べ物にも\n気を使うのよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105002, 0, 0, 8, 1799, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 1, 1801, "魔法と科学\nどっちも極めて\nみせるわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 2, 1802, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 3, 1803, "たまった治療代の\n請求書を\nまとめないと…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 4, 1804, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 5, 1805, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 101, 1806, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 102, 1807, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 1, 1808, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 2, 1809, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 3, 1810, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 4, 1811, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 101, 1812, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 102, 1813, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 1, 1, 1814, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 2, 2, 1815, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 3, 3, 1816, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 1, 1817, "医者たるもの\nいつだって\n清潔に…♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 2, 1818, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 3, 1819, "ぬいぐるみなら\n動物も…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 4, 1820, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 5, 1821, "ここにいると\nついウトウト\nしちゃうわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 6, 1822, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 7, 1823, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105101, 0, 0, 8, 1824, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 1, 1826, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 2, 1827, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 3, 1828, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 4, 1829, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 5, 1830, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 101, 1831, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 102, 1832, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 1, 1833, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 2, 1834, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 3, 1835, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 4, 1836, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 101, 1837, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 102, 1838, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 1, 1, 1839, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 2, 2, 1840, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 3, 3, 1841, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 1, 1842, "身なりについて\n研究するのも\n楽しそうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 2, 1843, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 3, 1844, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 4, 1845, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 5, 1846, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 6, 1847, "運動しないと\n体型の維持が…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 7, 1848, "適度な食事も\n大切よね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105102, 0, 0, 8, 1849, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 1, 1851, "ぺっぺっ！\n…い、いけないっ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 2, 1852, "いっけな～い！\nひづめで床に傷が！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 3, 1853, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 4, 1854, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 5, 1855, "みんな、体重\nどれくらいなのかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 101, 1856, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 102, 1857, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 1, 1858, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 2, 1859, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 3, 1860, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 4, 1861, "力仕事ならまかせて！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 101, 1862, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 102, 1863, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 1, 1, 1864, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 2, 2, 1865, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 3, 3, 1866, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 1, 1867, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 2, 1868, "ミシッっていった！\n私、重すぎるかも！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 3, 1869, "あ！　あなたも\n美人さんね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 4, 1870, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 5, 1871, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 6, 1872, "な、なんだか野生に\n帰っちゃいそう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 7, 1873, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105201, 0, 0, 8, 1874, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 1, 1876, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 2, 1877, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 3, 1878, "動物苑…って\n嫌なギルド名ね…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 4, 1879, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 5, 1880, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 101, 1881, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 102, 1882, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 1, 1883, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 2, 1884, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 3, 1885, "ぺっぺっ！\n…い、いけないっ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 4, 1886, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 101, 1887, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 102, 1888, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 1, 1, 1889, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 2, 2, 1890, "ダイエット中だから\nお昼は抜いてるの～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 3, 3, 1891, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 1, 1892, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 2, 1893, "ミシッっていった！\n私、重すぎるかも！？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 3, 1894, "あ！　あなたも\n美人さんね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 4, 1895, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 5, 1896, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 6, 1897, "な、なんだか野生に\n帰っちゃいそう！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 7, 1898, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105202, 0, 0, 8, 1899, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 1, 1901, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 2, 1902, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 3, 1903, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 4, 1904, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 5, 1905, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 101, 1906, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 102, 1907, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 1, 1908, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 2, 1909, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 3, 1910, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 4, 1911, "私ともっと高く、\n共に飛ぼう！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 101, 1912, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 102, 1913, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 1, 1, 1914, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 2, 2, 1915, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 3, 3, 1916, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 1, 1917, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 2, 1918, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 3, 1919, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 4, 1920, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 5, 1921, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 6, 1922, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 7, 1923, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105301, 0, 0, 8, 1924, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 1, 1926, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 2, 1927, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 3, 1928, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 4, 1929, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 5, 1930, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 101, 1931, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 102, 1932, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 1, 1933, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 2, 1934, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 3, 1935, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 4, 1936, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 101, 1937, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 102, 1938, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 1, 1, 1939, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 2, 2, 1940, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 3, 3, 1941, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 1, 1942, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 2, 1943, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 3, 1944, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 4, 1945, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 5, 1946, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 6, 1947, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 7, 1948, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105302, 0, 0, 8, 1949, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 1, 1951, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 2, 1952, "団員募集の\nポスター\n作ろうっと♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 3, 1953, "父さん…\n今頃どこで\n何してるんだろ？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 4, 1954, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 5, 1955, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 101, 1956, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 102, 1957, "父さん…\n私にもね、大切な\n仲間ができたよ", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 1, 1958, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 2, 1959, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 3, 1960, "なーにー？\nどうしたの？\n今呼んだよね？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 4, 1961, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 101, 1962, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 102, 1963, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 1, 1, 1964, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 2, 2, 1965, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 3, 3, 1966, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 1, 1967, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 2, 1968, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 3, 1969, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 4, 1970, "いい香り～♪\n気持ちが\n落ち着く～…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 5, 1971, "これって\n騎空艇に\n置けるかな…？", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 6, 1972, "どんな仕事でも\nこなせるような\n体力がないとね…！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 7, 1973, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105701, 0, 0, 8, 1974, "空を冒険するには\n色んなことを\n知っておかないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 1, 1976, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 2, 1977, "暇だよ…\nどこかに強い人は\nいないか？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 3, 1978, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 4, 1979, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 5, 1980, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 101, 1981, "私の力の源は\n仲間との\n絆だよ！", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 102, 1982, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 1, 1983, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 2, 1984, "私はいつも通りに\n喋ってるつもり\nだよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 3, 1985, "どうした？\nもしかして手合せ\nしたいか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 4, 1986, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 101, 1987, "背中はあなたに\n預けたよ！\nよろしくね！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 102, 1988, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 1, 1, 1989, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 2, 2, 1990, "腹減ったね…\nなにか食べに\n行くよ！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 3, 3, 1991, "修行のしすぎは\n良くないよ？\n気をつけるね", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 1, 1992, "美しい技は\n美しい体が\nあってこそ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 2, 1993, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 3, 1994, "…練習に\n使うのはさすがに\n可哀そうね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 4, 1995, "これは…\n瞑想の時にも\n使ってみたいね", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 5, 1996, "出来ればもっと\n熱いほうが\n嬉しいよ", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 6, 1997, "ここでも\n修行出来るなんて\n最高だよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 7, 1998, "気力の源は体力！\n体力の源は\n健康とご飯！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105702, 0, 0, 8, 1999, "んー…\n難しいことは\nわかんないよ！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 1, 2001, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 2, 2002, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 3, 2003, "ん？\n食べ物のにおい？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 4, 2004, "みんなと仲良く食卓を\n囲みたいですね～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 5, 2005, "この部屋が、わたしの\n帰る場所です♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 101, 2006, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 1, 1, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 102, 2007, "あの人、\n大食いの子って\n嫌いかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 1, 2008, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 2, 2009, "つ、つまみ食いなんて\nしてませんよ？\nこれは味見です！", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 3, 2010, "おやつは別腹、\nごはんも別腹、\n魔物も別腹～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 4, 2011, "好きなものを、\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 101, 2012, "あなたは、\nごはん王子ですね～", 1, 2, 4);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 102, 2013, "大好きです～\n愛してます～\nえへへ～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 1, 1, 2014, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 2, 2, 2015, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 3, 3, 2016, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 1, 2017, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 2, 2018, "ふい～\n消化中です～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 3, 2019, "おいしそ…\nか、かわいいです～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 4, 2020, "いい香りですね～\n空気までおいしそう～\nじゅるり♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 5, 2021, "ほっかほか～♪\n魔物の丸焼きが\n作れちゃいますね！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 6, 2022, "走って食べて！\n走って食べて！\n\この無限ループです！", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 7, 2023, "いくつでも\n食べられますよ！\n幸せ～☆", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105801, 0, 0, 8, 2024, "料理の絵を\n見ているだけで、\nよ、よだれが～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 1, 2026, "この部屋が、わたしの\n帰る場所です♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 2, 2027, "ギルドハウス\nどんどん素敵に\nなっていくな～♪", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 3, 2028, "「やばい」とか\n「おいっす」とか…\n民の言葉って難しい", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 4, 2029, "お菓子の家に\n住みたいな～", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 5, 2030, "あ、頼んでた出前が\n届いたかな？", 1, 1, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 101, 2031, "大切な人がいて\n大切な場所がある\nすごく幸せです♪", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 102, 2032, "あの人\n大食いの子って\n嫌いかな？", 1, 1, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 1, 2033, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 2, 2034, "モグモグ…\nあげませんよ～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 3, 2035, "おやつは別腹\nごはんも別腹\n魔物も別腹～", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 4, 2036, "好きなものを\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 101, 2037, "大好きです～\n愛してます～\nえへへ～♪", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 102, 2038, "わたし食べても\n太らないんです♪\nほらほら～触って\nみてくださいよ～", 1, 2, 8);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 1, 1, 2039, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 2, 2, 2040, "お昼ご飯\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 3, 3, 2041, "お夕飯も、晩ごはんも\n食べたし～。さぁ！\nお夜食の時間ですよ☆", 1, 2, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 1, 2042, "お姫さまみたいに\nきれいに\nならないと…", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 2, 2043, "のんびりしていても\nお腹がすいちゃうん\nですよね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 3, 2044, "おいしそ…\nか、かわいいです～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 4, 2045, "アップル系、ピーチ系\nオレンジ系…\n香りも色々ですね～", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 5, 2046, "ここでお魚を\n焼けたら\n最高ですね～♪", 1, 3, 1);
INSERT INTO `v1_45f1d371d93735d5b50e5f84beb3b50de77e7a409f68f785585441be58537c58` VALUES (105802, 0, 0, 6, 2047, "身体も鍛えなきゃ～！\nお腹がすいちゃいます\nけど…", 1, 3, 1);
